content:
import Animation from '../entity/animation';
import Chat from './chat';
import Overlay from './overlay';
import log from '../lib/log';
import Module from '../utils/modules';
import Packets from '../network/packets';
import Detect from '../utils/detect';

/**
 * Handles game mouse and keyboard input
 * @class
 */
export default class Input {
  /**
   * Default constructor
   * @param {Game} game instance of the game
   */
  constructor(game) {
    /**
    * An instance of the game
    * @type {Game}
    */
    this.game = game;

    /**
    * An instance of the client
    * @type {Client}
    */
    this.client = game.client;

    /**
    * An instance of the game renderer
    * @type {Renderer}
    */
    this.renderer = game.renderer;

    /**
    * Whether or not the selected cell is visible
    * @type {Boolean}
    */
    this.selectedCellVisible = false;

    /**
    * The previous mouse click envent
    * @type {MouseEvent}
    */
    this.previousClick = {};

    /**
    * Whether or not the cursor is visible
    * @type {Boolean}
    */
    this.cursorVisible = true;

    /**
    * Whether or not the target is visible
    * @type {Boolean}
    */
    this.targetVisible = true;

    /**
    * The select x position
    * @type {Number}
    */
    this.selectedX = -1;

    /**
    * The select y position
    * @type {Number}
    */
    this.selectedY = -1;

    /**
    * The cursor
    * @type {Cursor}
    */
    this.cursor = null;

    /**
    * A new instance of the cursor (for changing cursors)
    * @type {Cursor}
    */
    this.newCursor = null;

    /**
    * Data on the targeted entity
    * @type {Entity}
    */
    this.targetData = null;

    /**
    * The color of the target
    * @type {String}
    */
    this.targetColour = null;

    /**
    * The new target color
    * @type {String}
    */
    this.newTargetColour = null;

    /**
    * A moving target's color
    * @type {String}
    */
    this.mobileTargetColour = 'rgba(51, 255, 0)';

    /**
    * The previous key pressed
    * @type {KeyboardEvent}
    */
    this.previousKey = {};

    /**
    * All loaded cursors
    * @type {Object<Cursor>}
    */
    this.cursors = {};

    /**
    * The entity being hovered over
    * @type {Entity}
    */
    this.hovering = null;

    /**
     * A reference to the Chat class
     * @type {Chat}
     */
    this.chatHandler = null;

    /**
     * A reference to the overlay handler
     * @type {Overlay}
     */
    this.overlay = null;

    /**
    * Information about the mouse position
    * @type {{x: Number, y:Number}}
    */
    this.mouse = {
      x: 0,
      y: 0,
    };

    this.loadInput();
  }

  /**
   * Load spinner sprite, chat handler and overlay
   */
  loadInput() {
    /**
     * This is the animation for the target
     * cell spinner sprite (only on desktop)
     */
    this.targetAnimation = new Animation('move', 4, 0, 16, 16);
    this.targetAnimation.setSpeed(50);
    this.chatHandler = new Chat(this.game);
    this.overlay = new Overlay(this);
  }

  /**
   * Load cursors
   */
  loadCursors() {
    log.debug('Input - loadCursors()');

    this.cursors.hand = this.game.getSprite('hand');
    this.cursors.sword = this.game.getSprite('sword');
    this.cursors.loot = this.game.getSprite('loot');
    this.cursors.target = this.game.getSprite('target');
    this.cursors.arrow = this.game.getSprite('arrow');
    this.cursors.talk = this.game.getSprite('talk');
    this.cursors.spell = this.game.getSprite('spell');
    this.cursors.bow = this.game.getSprite('bow');

    this.newCursor = this.cursors.hand;
    this.newTargetColour = 'rgba(255, 255, 255, 0.5)';
  }

  /**
   * Handle input
   * @param  {Modules} inputType the Modules.InputType to handle
   * @param  {Object} data      data related to the input (keycode, coordinates, etc)
   */
  handle(inputType, data) {
    console.log('canvas handling game input', inputType, data);
    switch (inputType) {
      default:
        break;

      // WASD and arrow key press movement
      case Module.InputType.Key:
        console.log('canvas input type key');
        if (this.chatHandler.isActive()) {
          this.chatHandler.key(data);
          return;
        }

        switch (data) {
          default:
            break;
          case Module.Keys.Up:
          case Module.Keys.W:
            this.getPlayer().moveUp = true;
            break;
          case Module.Keys.A:
          case Module.Keys.Left:
            this.getPlayer().moveLeft = true;
            break;
          case Module.Keys.S:
          case Module.Keys.Down:
            this.getPlayer().moveDown = true;
            break;
          case Module.Keys.D:
          case Module.Keys.Right:
            this.getPlayer().moveRight = true;
            break;
          case Module.Keys.Enter:
            this.chatHandler.toggle();
            break;
        }
        break;

      // mouse click movement and interactions
      case Module.InputType.LeftClick:
        console.log('canvas input type click');
        this.getPlayer().disableAction = false;
        this.setCoords(data);
        console.log('canvas clicking on cords', this.getCoords());
        this.click(this.getCoords());
        break;
    }
  }

  /**
   * Handle keyUp events
   * @param  {Modules} key the Modules.Keys that was triggered
   */
  keyUp(key) {
    const player = this.getPlayer();

    // WASD and arrow key press movement
    switch (key) {
      default:
        break;
      case Module.Keys.W:
      case Module.Keys.Up:
        player.moveUp = false;
        break;
      case Module.Keys.A:
      case Module.Keys.Left:
        player.moveLeft = false;
        break;
      case Module.Keys.S:
      case Module.Keys.Down:
        player.moveDown = false;
        break;
      case Module.Keys.D:
      case Module.Keys.Right:
        player.moveRight = false;
        break;
    }

    player.disableAction = false;
  }

  /**
   * Handle key presses to move the player in a direction
   * @param  {Object} position The x,y coordinates of the click position
   */
  keyMove(position) {
    const player = this.getPlayer();

    if (!player.hasPath()) {
      this.click(position);
    }
  }

  /**
   * Handle clicking events
   * @param  {Object} position the x,y coordinates of the click position
   */
  click(position) {
    const player = this.getPlayer();

    if (player.stunned) {
      console.log('canvas player is stunned');
      return;
    }

    this.setPassiveTarget();

    /**
     * It can be really annoying having the chat open
     * on mobile, and it is far harder to control.
     */
    if (
      this.renderer.mobile
      && this.chatHandler.input.is(':visible')
      && this.chatHandler.input.val() === ''
    ) {
      console.log('canvas hiding chat handler input');
      this.chatHandler.hideInput();
    }

    if (this.game.zoning && this.game.zoning.direction) {
      console.log('canvas zoning');
      return;
    }

    const entity = this.game.getEntityAt(
      position.x,
      position.y,
      position.x === player.gridX && position.y === player.gridY,
    );

    console.log('canvas entity is', entity);

    if (entity && !player.disableAction) {
      console.log('canvas setting attack target');
      this.setAttackTarget();

      if (this.isTargetable(entity)) {
        console.log('canvas is targetable');
        player.setTarget(entity);
      }

      if (
        player.getDistance(entity) < 7
        && player.isRanged()
        && this.isAttackable(entity)
      ) {
        console.log('canvas stopping for ranged enemy');
        this.game.socket.send(Packets.Target, [
          Packets.TargetOpcode.Attack,
          entity.id,
        ]);
        player.lookAt(entity);
        return;
      }

      if (entity.gridX === player.gridX && entity.gridY === player.gridY) {
        console.log('canvas setting attack', entity.id);
        this.game.socket.send(Packets.Target, [
          Packets.TargetOpcode.Attack,
          entity.id,
        ]);
      }

      // @TODO this was commented out, not sure why??
      // if (entity.type === 'player') {
      //   console.log('canvas show player actions');
      //   this.getActions().showPlayerActions(entity, this.mouse.x, this.mouse.y);
      //   return;
      // }

      if (this.isTargetable(entity)) {
        console.log('canvas following the entity');
        player.follow(entity);
        return;
      }

      player.disableAction = true;
    } else {
      console.log('canvas removing target');
      player.removeTarget();
    }

    this.getActions().hidePlayerActions();

    player.go(position.x, position.y);

    if (this.game.interface) {
      console.log('canvas hiding all game interface');
      this.game.interface.hideAll();
    }

    if (!this.game.audio.song && Detect.isSafari()) {
      console.log('canvas updating safari audio');
      this.game.audio.update();
    }
  }

  /**
   * Change the cursor if visible and newCursor doesn't equal cursor
   */
  updateCursor() {
    if (!this.cursorVisible) {
      return;
    }

    // change to this new cursor
    if (this.newCursor !== this.cursor) {
      this.cursor = this.newCursor;
    }

    if (this.newTargetColour !== this.targetColour) {
      this.targetColour = this.newTargetColour;
    }
  }

  /**
   * Move the cursor or display a different cursor depending on what the
   * player is hovering over
   */
  moveCursor() {
    if (!this.renderer || this.renderer.mobile || !this.renderer.camera) {
      return;
    }

    const position = this.getCoords();
    const player = this.getPlayer();
    const entity = this.game.getEntityAt(
      position.x,
      position.y,
      player.gridX === position.x && player.gridY === position.y,
    );

    this.overlay.update(entity);

    if (!entity || entity.id === player.id) {
      this.setCursor(this.cursors.hand);
      this.hovering = null;
    } else {
      switch (entity.type) {
        default:
          break;
        case 'item':
        case 'chest':
          this.setCursor(this.cursors.loot);
          this.hovering = Module.Hovering.Item;
          break;
        case 'mob':
          this.setCursor(this.getAttackCursor());
          this.hovering = Module.Hovering.Mob;
          break;
        case 'player':
          this.setCursor(
            this.game.pvp && entity.pvp
              ? this.getAttackCursor()
              : this.cursors.hand,
          );
          this.hovering = Module.Hovering.Player;
          break;

        case 'npc':
          this.setCursor(this.cursors.talk);
          this.hovering = Module.Hovering.NPC;
          break;
      }
    }
  }

  /**
   * Set the selectedX and selectedY positions
   * @param {Number} x X coordinate
   * @param {Number} y Y coordinate
   */
  setPosition(x, y) {
    this.selectedX = x;
    this.selectedY = y;
  }

  /**
   * Sets the coordinates of the mouse relative to the offset of the canvas
   * @param {MouseEvent} event the mouse move or mouse click event
   */
  setCoords(event) {
    // log.debug('Input - setCoords', event, this.client.canvas);
    const offset = this.client.canvas.offset();
    const x = event.clientX - offset.left;
    const y = event.clientY - offset.top;

    // check background
    const {
      width,
      height,
    } = this.renderer.backgroundCanvas;

    this.mouse.x = Math.round(
      x - this.newCursor.width,
    );

    this.mouse.y = Math.round(
      y,
    );

    // log.debug('Input - variables', offset, height, width, this.mouse.x, this.mouse.y, this.client.getZoom());

    if (this.mouse.x >= width) {
      this.mouse.x = width - 1;
    } else if (this.mouse.x <= 0) {
      this.mouse.x = 0;
    }

    if (this.mouse.y >= height) {
      this.mouse.y = height - 1;
    } else if (this.mouse.y <= 0) {
      this.mouse.y = 0;
    }
  }

  /**
   * Set a new cursor
   * @param {Cusror} cursor the cursor to use
   */
  setCursor(cursor) {
    if (cursor) {
      this.newCursor = cursor;
    } else {
      log.error(`Cursor: ${cursor} could not be found.`);
    }
  }

  /**
   * Set an attack target
   */
  setAttackTarget() {
    this.targetAnimation.setRow(1);
    this.mobileTargetColour = 'rgb(255, 51, 0)';
  }

  /**
   * Set a passive target
   */
  setPassiveTarget() {
    this.targetAnimation.setRow(0);
    this.mobileTargetColour = 'rgb(51, 255, 0)';
  }

  /**
   * Get an attack cursor (bow or sword) depending on if the player is ranged or not
   * @return {Cursor} the cursor to use for attacking
   */
  getAttackCursor() {
    return this.cursors[this.getPlayer().isRanged() ? 'bow' : 'sword'];
  }

  /**
   * Get the coordinates from the mouse relative to the canvas grid
   * @return {{x: Number, y:Number}} returns the grid x,y coordinates
   */
  getCoords() {
    if (!this.renderer || !this.renderer.camera) {
      return {};
    }

    const tileScale = this.renderer.tileSize * this.renderer.getDrawingScale();
    const offsetX = this.mouse.x % tileScale;
    const offsetY = this.mouse.y % tileScale;
    const x = (this.mouse.x - offsetX) / tileScale + this.game.getCamera().gridX;
    const y = (this.mouse.y - offsetY) / tileScale + this.game.getCamera().gridY;

    return {
      x,
      y,
    };
  }

  /**
   * Get data about the current target
   * @return {{
   *  sprite: Sprite,
   *  x: Number,
   *  y: Number,
   *  width: Number,
   *  height: Number,
   *  dy: Number,
   *  dw: Number,
   *  dh: Number,
   *  }} target data
   */
  getTargetData() {
    const frame = this.targetAnimation.currentFrame;
    const scale = this.renderer.getDrawingScale();
    const sprite = this.game.getSprite('target');

    if (!sprite.loaded) {
      sprite.loadSprite();
    }

    this.targetData = {
      sprite,
      x: frame.x * scale,
      y: frame.y * scale,
      width: sprite.width * scale,
      height: sprite.height * scale,
      dx: this.selectedX * 16 * scale,
      dy: this.selectedY * 16 * scale,
      dw: sprite.width * scale,
      dh: sprite.height * scale,
    };

    return this.targetData;
  }

  /**
   * Returns whether or not this entity can be targeted
   * @param  {Entity}  entity the entity to check if it can be targeted or not
   * @return {Boolean} returns true player can target this entity
   */
  isTargetable(entity) {
    return (
      this.isAttackable(entity)
      || entity.type === 'npc'
      || entity.type === 'chest'
    );
  }

  /**
   * Returns whether or not the player can attack this entity
   * @param  {Entity}  entity the entity they want to attack
   * @return {Boolean} returns true if they can attack this entity
   */
  isAttackable(entity) {
    return (
      entity.type === 'mob'
      || (entity.type === 'player' && entity.pvp && this.game.pvp)
    );
  }

  /**
   * Returns an instance of the player
   * @return {Entity} the player's entity
   */
  getPlayer() {
    return this.game.player;
  }

  /**
   * Returns the interface actions
   * @return {Actions} game interface actions
   */
  getActions() {
    return this.game.interface.actions;
  }
}

import $ from 'jquery';
import Inventory from '../interface/inventory';
import Profile from '../interface/profile/profile';
import Actions from '../interface/actions';
import Bank from '../interface/bank';
import Enchant from '../interface/enchant';
import Warp from '../interface/warp';
import log from '../lib/log';

/**
 * Handles game UI interfaces
 * @class
 */
export default class Interface {
  /**
   * Default constructor
   * @param {Game} game instance of the game
   */
  constructor(game) {
    /**
     * An instance of the game
     * @type {Game}
     */
    this.game = game;

    /**
     * Jquery DOM reference to the notifcations box
     * @type {DOMElement}
     */
    this.notify = $('#notify');

    /**
     * Jquery DOM reference to the confirm box
     * @type {DOMElement}
     */
    this.confirm = $('#confirm');

    /**
     * Jquery DOM reference to the message box
     * @type {DOMElement}
     */
    this.message = $('#message');

    /**
     * Jquery DOM reference to the notifcations fading out
     * @type {DOMElement}
     */
    this.fade = $('#notifFade');

    /**
     * Jquery DOM reference to the notifcations done button
     * @type {DOMElement}
     */
    this.done = $('#notifyDone');

    /**
     * A reference to the inventory class
     * @type {Inventory}
     */
    this.inventory = null;

    /**
     * A reference to the player's profile class
     * @type {Profile}
     */
    this.profile = null;

    /**
     * A reference to player click interactions
     * @type {Actions}
     */
    this.actions = null;

    /**
     * A reference to player's bank
     * @type {Bank}
     */
    this.bank = null;

    /**
     * A reference to the player's magic
     * @type {Enchant}
     */
    this.enchant = null;

    /**
     * A reference to warp map
     * @type {Warp}
     */
    this.warp = null;

    // load the good stuff
    this.loadNotifications();
    this.loadActions();
    this.loadWarp();

    this.done.click(() => {
      this.hideNotify();
    });
  }

  /**
   * Handles resizing the interface elements by calling
   * each UI element's unique resize functionality
   */
  resize() {
    if (this.inventory) {
      this.inventory.resize();
    }

    if (this.profile) {
      this.profile.resize();
    }

    if (this.bank) {
      this.bank.resize();
    }

    if (this.enchant) {
      this.enchant.resize();
    }
  }

  /**
   * This can be called multiple times and can be used
   * to completely refresh the inventory.
   * @param {Number} size how large the inventory is
   * @param {Object} data the player's inventory items from the server
   */
  loadInventory(size, data) {
    this.inventory = new Inventory(this.game, size);
    this.inventory.loadInventory(data);
  }

  /**
   * Similar structure as the inventory, just that it
   * has two containers. The bank and the inventory.
   * @param {Number} size how large the inventory is
   * @param {Object} data the player's bank information
   */
  loadBank(size, data) {
    this.bank = new Bank(this.game, this.inventory.container, size);
    this.bank.loadBank(data);

    // @TODO why is load enchant in load bank??
    this.loadEnchant();
  }

  /**
   * Load the player's profile
   */
  loadProfile() {
    if (!this.profile) {
      this.profile = new Profile(this.game);
    }
  }

  /**
   * Load the player's click actions
   */
  loadActions() {
    if (!this.actions) {
      this.actions = new Actions(this);
    }
  }

  /**
   * Load the player's magic enchantments
   */
  loadEnchant() {
    if (!this.enchant) {
      this.enchant = new Enchant(this.game, this);
    }
  }

  /**
   * Load warp map
   */
  loadWarp() {
    if (!this.warp) {
      this.warp = new Warp(this.game, this);
    }
  }

  /**
   * Load the player's current notifications
   */
  loadNotifications() {
    const ok = $('#ok');
    const cancel = $('#cancel');
    const done = $('#done');

    /**
     * Simple warning dialogue
     */
    ok.click(() => {
      log.debug('Client - loadNotifications() - dialog ok clicked');
      this.hideNotify();
    });

    /**
     * Callbacks responsible for
     * Confirmation dialogues
     */
    cancel.click(() => {
      log.debug('Client - loadNotifications() - dialog cancel clicked');
      this.hideConfirm();
    });

    /**
     * Callback responsible for dismissing the dialog box
     */
    done.click(() => {
      log.debug('Client - loadNotifications() - dialog done clicked');
      this.hideConfirm();
    });
  }

  /**
   * Hides all interface menus
   */
  hideAll() {
    if (this.inventory && this.inventory.isVisible()) {
      this.inventory.hide();
    }

    if (this.actions && this.actions.isVisible()) {
      this.actions.hide();
    }

    if (
      this.profile
      && (this.profile.isVisible() || this.profile.settings.isVisible())
    ) this.profile.hide();

    if (
      this.game.input
      && this.game.input.chatHandler
      && this.game.input.chatHandler.input.is(':visible')
    ) {
      this.game.input.chatHandler.hideInput();
    }

    if (this.bank && this.bank.isVisible()) {
      this.bank.hide();
    }

    if (this.enchant && this.enchant.isVisible()) {
      this.enchant.hide();
    }

    if (this.warp && this.warp.isVisible()) {
      this.warp.hide();
    }
  }

  /**
   * Display a notification if not already visible
   * @param  {String} message the message to display
   */
  displayNotify(message) {
    if (this.isNotifyVisible()) {
      return;
    }

    this.notify.css('display', 'block');
    this.fade.css('display', 'block');
    this.message.css('display', 'block');

    this.message.text(message);
  }

  /**
   * Display the confirmation box
   * @param  {[type]} message The confirmation message
   */
  displayConfirm(message) {
    if (this.isConfirmVisible()) {
      return;
    }

    this.confirm.css('display', 'block');
    this.confirm.text(message);
  }

  /**
   * Hide the notifications window
   */
  hideNotify() {
    this.fade.css('display', 'none');
    this.notify.css('display', 'none');
    this.message.css('display', 'none');
  }

  /**
   * Hide the confirmation window
   */
  hideConfirm() {
    this.confirm.css('display', 'none');
  }

  /**
   * Return a reference to the profile quests
   * @return {Quest} The quests for this player
   */
  getQuestPage() {
    return this.profile.quests;
  }

  /**
   * Checks to see if the notifications window is visible or not
   * @return {Boolean} the status of the notifications window
   */
  isNotifyVisible() {
    return this.notify.css('display') === 'block';
  }

  /**
   * Check if the confirmation box is visible
   * @return {Boolean} the status of the confirmation window
   */
  isConfirmVisible() {
    return this.confirm.css('display') === 'block';
  }
}

import $ from 'jquery';

/**
 * Shows an popup overlay when the mouse
 * is over a character or enemy that displays
 * information about them
 * @class
 */
export default class Overlay {
  /**
   * Default constructor
   * @param {Input} input reference to the Input class
   */
  constructor(input) {
    /**
     * Input class
     * @type {Input}
     */
    this.input = input;

    /**
     * A reference to the entity they are hovering over
     * @type {Entity}
     */
    this.hovering = null;

    /**
     * Jquery reference to the attack info box
     * @type {DOMElement}
     */
    this.attackInfo = $('#attackInfo');

    /**
     * Jquery reference to the image section of the attack info box
     * @type {DOMElement}
     */
    this.image = this.attackInfo.find('.image div');

    /**
     * Jquery reference to the name of the attack info box
     * @type {DOMElement}
     */
    this.name = this.attackInfo.find('.name');

    /**
     * Jquery reference to the details of the attack info box
     * @type {DOMElement}
     */
    this.details = this.attackInfo.find('.details');

    /**
     * Jquery reference to the name of the health bar of the
     * attack info box
     * @type {DOMElement}
     */
    this.health = this.attackInfo.find('.health');

    /**
     * A callback for when the overlay is done updating
     * @type {Function}
     */
    this.updateCallback = null;
  }

  /**
   * Update the overlay
   * @param  {Entity} entity The entity they are attacking
   * @return {Boolean} returns false if invalid entity or currently visible
   */
  update(entity) {
    if (!this.validEntity(entity)) {
      this.hovering = null;

      if (this.isVisible()) {
        this.hide();
      }

      return;
    }

    if (!this.isVisible()) {
      this.display();
    }

    this.hovering = entity;

    this.name.html(entity.type === 'player'
      ? entity.username
      : entity.label);

    if (this.hasHealth()) {
      this.health.css({
        display: 'block',
        width: `${Math.ceil((entity.hitPoints / entity.maxHitPoints) * 100) - 10}%`,
      });

      this.details.html(`${entity.hitPoints} / ${entity.maxHitPoints}`);
    } else {
      this.health.css('display', 'none');
      this.details.html('');
    }

    this.onUpdate((entityId, hitPoints) => {
      if (
        this.hovering
        && this.hovering.id === entityId
        && this.hovering.type !== 'npc'
        && this.hovering.type !== 'item'
      ) {
        if (hitPoints < 1) {
          this.hide();
        } else {
          this.health.css(
            'width',
            `${Math.ceil((hitPoints / this.hovering.maxHitPoints) * 100)
              - 10
            }%`,
          );
          this.details.html(`${hitPoints} / ${this.hovering.maxHitPoints}`);
        }
      }
    });
  }

  /**
   * Checks whether or not this is an entity they can
   * interact with
   * @param  {Entity} entity the entity they want to interact with
   * @return {Boolean} Returns true if this is not a player or projectile
   */
  validEntity(entity) {
    return (
      entity
      && entity.id !== this.input.getPlayer().id
      && entity.type !== 'projectile'
    );
  }

  /**
   * Cleans out the details and the entity they're hovering on
   */
  clean() {
    this.details.html('');
    this.hovering = null;
  }

  /**
   * Check if the entity has a health bar
   * @return {Boolean} Rturns true if this entity is a (mob or player)
   */
  hasHealth() {
    return this.hovering.type === 'mob' || this.hovering.type === 'player';
  }

  /**
   * Fades in the attack info
   */
  display() {
    this.attackInfo.fadeIn('fast');
  }

  /**
   * Fades out and hides the attack info
   */
  hide() {
    this.attackInfo.fadeOut('fast');
  }

  /**
   * Check to see if the atttack info is currently visible
   * @return {Boolean}
   */
  isVisible() {
    return this.attackInfo.css('display') === 'block';
  }

  /**
   * Returns an instance of the game object
   * @return {Game}
   */
  getGame() {
    return this.input.game;
  }

  /**
   * Sets an update callback
   * @param  {Function} callback called when the overlay is updated
   */
  onUpdate(callback) {
    this.updateCallback = callback;
  }
}

import _ from 'underscore';
import Player from '../entity/character/player/player';
import Sprites from './sprites';
import Item from '../entity/objects/item';
import log from '../lib/log';

/**
 * Used to load the character and items for the player
 * @class
 */
export default class PickCharacter {
  /**
   * Default constructor
   * @param {Game} game Reference to the game class
   */
  constructor(game) {
    /**
     * Instance of the game
     * @type {Game}
     */
    this.game = game;

    /**
     * Instance of the renderer
     * @type {Renderer}
     */
    this.renderer = game.renderer;

    /**
     * Grids
     * @type {[type]}
     */
    this.grids = null;

    /**
     * A reference to other game sprites in the game
     * @type {Sprites}
     */
    this.sprites = null;

    /**
     * A reference to the entities in the game
     * @type {Array}
     */
    this.entities = {};

    /**
     * @TODO not sure this is even used
     * @type {Object}
     */
    this.decrepit = {};
  }

  /**
   * Load the character into the game
   */
  loadCharacter() {
    log.debug('PickCharacter - loadCharacter()', this.sprites);
    this.game.client.sendStatus('Inviting craziness...');

    if (!this.sprites) {
      this.sprites = new Sprites(this.game.renderer);
    }

    this.game.client.sendStatus('Lots of spooky monsters...');
  }

  /**
   * Updating sprites in the game
   */
  update() {
    if (this.sprites) {
      this.sprites.updateSprites();
    }
  }

  /**
   * Create the player in the game
   */
  create() {
    const entity = new Player();

    entity.setGridPosition(0, 0);

    // set the default character sprite
    entity.setSprite(this.getSprite('clotharmor'));
    entity.idle();
    entity.loadHandler(this.game);
    this.addEntity(entity);
    entity.setGridPosition(0, 0);
    entity.idle();
    this.addEntity(entity);

    if (entity.handler) {
      entity.handler.setGame(this.game);
      entity.handler.loadEntity();
    }
  }

  /**
   * Return a specific entity by index
   * @param  {Number} id returns an entity with that ID
   * @return {Entity}
   */
  get(id) {
    if (id in this.entities) {
      return this.entities[id];
    }

    return null;
  }

  /**
   * Checks to see if an entity is there
   * @param  {Number} id The ID of the entity
   * @return {Entity
   */
  exists(id) {
    return id in this.entities;
  }

  /**
   * Clears the other players from the grid to calculate pathing
   * so they don't collide, then resets the players back into position
   * @param  {Player} exception Player to exlude when clearing
   */
  clearPlayers(exception) {
    _.each(this.entities, (entity) => {
      if (entity.id !== exception.id && entity.type === 'player') {
        this.grids.removeFromRenderingGrid(
          entity,
          entity.gridX,
          entity.gridY,
        );
        this.grids.removeFromPathingGrid(entity.gridX, entity.gridY);

        delete this.entities[entity.id];
      }
    });

    this.grids.resetPathingGrid();
  }

  /**
   * Adds an entity
   * @param {Entity} entity instance of the entity to add
   */
  addEntity(entity) {
    if (this.entities[entity.id]) {
      return;
    }

    this.entities[entity.id] = entity;
    this.registerPosition(entity);

    if (
      !(entity instanceof Item && entity.dropped)
      && !this.renderer.isPortableDevice()
    ) entity.fadeIn(this.game.time);
  }

  /**
   * Remove an item from the game
   * @param  {Item} item An instance of an item
   */
  removeItem(item) {
    if (!item) {
      return;
    }

    this.grids.removeFromItemGrid(item, item.gridX, item.gridY);
    this.grids.removeFromRenderingGrid(item, item.gridX, item.gridY);

    delete this.entities[item.id];
  }

  /**
   * Register the position of an entity and add it to the player's grid
   * @param  {Entity} entity An instance of a player, mob, npc or chest
   */
  registerPosition(entity) {
    log.debug('PickCharacter - registerPosition()', entity);

    if (!entity) {
      return;
    }

    if (
      entity.type === 'player'
      || entity.type === 'mob'
      || entity.type === 'npc'
      || entity.type === 'chest'
    ) {
      this.grids.addToEntityGrid(entity, entity.gridX, entity.gridY);

      if (entity.type !== 'player' || entity.nonPathable) {
        this.grids.addToPathingGrid(entity.gridX, entity.gridY);
      }
    }

    if (entity.type === 'item') {
      this.grids.addToItemGrid(entity, entity.gridX, entity.gridY);
    }

    this.grids.addToRenderingGrid(entity, entity.gridX, entity.gridY);
  }

  /**
   * Register position with duality (two things in the same spot) so
   * the entity is moved to a different spot
   * @param  {Entity} entity An instance of an entity
   */
  registerDuality(entity) {
    if (!entity) {
      return;
    }

    this.grids.entityGrid[entity.gridY][entity.gridX][entity.id] = entity;

    this.grids.addToRenderingGrid(entity, entity.gridX, entity.gridY);

    if (entity.nextGridX > -1 && entity.nextGridY > -1) {
      this.grids.entityGrid[entity.nextGridY][entity.nextGridX][
        entity.id
      ] = entity;

      if (!(entity instanceof Player)) {
        this.grids.pathingGrid[entity.nextGridY][entity.nextGridX] = 1;
      }
    }
  }

  /**
   * Remove an entity from a position on the grids
   * @param  {Entity} entity remove the entity from the grid
   */
  unregisterPosition(entity) {
    if (!entity) {
      return;
    }

    this.grids.removeEntity(entity);
  }

  /**
   * Get a sprite by name
   * @param  {String} name the name of the sprite
   * @return {Sprite} returns the sprite if found
   */
  getSprite(name) {
    return this.sprites.sprites[name];
  }

  /**
   * Returns all available entities
   * @return {Entity[]} array of entities
   */
  getAll() {
    return this.entities;
  }

  /**
   * Apply a callback function to every entity
   * @param  {Function} callback the function to apply to every entity
   */
  forEachEntity(callback) {
    _.each(this.entities, (entity) => {
      callback(entity);
    });
  }

  /**
   * Apply a callback function to entities within a position radius
   * @param  {Number}   x        X coordinate in the grid
   * @param  {Number}   y        Y coordinate in the grids
   * @param  {Number}   radius   How many grid spaces in the circle radius
   * @param  {Function} callback The callback function to apply
   */
  forEachEntityAround(x, y, radius, callback) {
    for (let i = x - radius, maxI = x + radius; i <= maxI; i += 1) {
      for (let j = y - radius, maxJ = y + radius; j <= maxJ; j += 1) {
        if (!this.map.isOutOfBounds(i, j)) {
          _.each(this.grids.renderingGrid[j][i], (entity) => {
            callback(entity);
          });
        }
      }
    }
  }
}

import $ from 'jquery';
import _ from 'underscore';
import Pointer from '../renderer/pointers/pointer';
import Module from '../utils/modules';
import log from '../lib/log';

/**
 * Handles the cursor on the HTML5 canvas, different pointers
 * can be swapped out depending on the entity the player is
 * interacting with, this holds all of the individual pointers
 * whereas {@link Pointer} holds a specific/individual cursor
 * @class
 */
export default class Cursor {
  /**
   * Default constructor
   * @param {Game} game instance of the game
   */
  constructor(game) {
    /**
     * Instance of the game
     * @type {Game}
     */
    this.game = game;

    /**
     * Pointer from the renderer
     * @type {Pointer}
     */
    this.pointers = {};

    /**
     * Scale of the device
     * @type {Number}
     */
    this.scale = this.getScale();

    /**
     * The camera used for the pointer
     * @type {Camera}
     */
    this.camera = null;

    /**
     * Jquery referencce to the bubble message box
     * @type {DOMElement}
     */
    this.container = $('#bubbles');
  }

  /**
   * Create a new cursor
   * @param  {Number} id   ID of the cursor
   * @param  {String} type type of the pointer
   */
  create(id, type) {
    if (id in this.pointers) {
      return;
    }

    const element = $(`<div id="${id}" class="pointer"></div>`);

    this.setSize(element);
    this.container.append(element);
    this.pointers[id] = new Pointer(id, element, type);
  }

  /**
   * Handle resizing the cursors
   */
  resize() {
    _.each(this.pointers, (pointer) => {
      switch (pointer.type) {
        case Module.Pointers.Relative:
          const scale = this.getScale(); // eslint-disable-line
          const x = pointer.x; // eslint-disable-line
          const y = pointer.y; // eslint-disable-line
          let offsetX = 0; // eslint-disable-line
          let offsetY = 0; // eslint-disable-line

          if (scale === 1) {
            offsetX = pointer.element.width() / 2 + 5;
            offsetY = pointer.element.height() / 2 - 4;
          }

          pointer.element.css('left', `${(x * scale) - offsetX}px`);
          pointer.element.css('top', `${(y * scale) - offsetY}px`);
          break;
        default:
          break;
      }
    });
  }

  /**
   * Set the size of the cursors
   */
  setSize(element) {
    const width = 8;
    const height = width + (width * 0.2);
    const image = 'url("/img/common/hud-active.png")';

    this.updateScale();

    element.css({
      width: `${width * this.scale}px`,
      height: `${height * this.scale}px`,
      margin: 'inherit',
      'margin-top': `-${(height / 2) * this.scale}px`,
      'margin-left': '1px',
      top: `${height * this.scale}px`,
      background: image,
      'background-size': '100% 100%',
    });
  }

  /**
   * Destroy all the cursors
   */
  clean() {
    _.each(this.pointers, (pointer) => {
      pointer.destroy();
    });

    this.pointers = {};
  }

  /**
   * Destroy a specific cursor
   * @param  {Pointer} pointer An instance of the cursor
   */
  destroy(pointer) {
    delete this.pointers[pointer.id];
    pointer.destroy();
  }

  /**
   * Set the position of the cursor
   * @param {Pointer} pointer Instance of the pointer to move
   * @param {Number} posX    X position
   * @param {Number} posY    Y position
   */
  set(pointer, posX, posY) {
    this.updateScale();
    this.updateCamera();

    const tileSize = 16 * this.scale;
    const x = ((posX - this.camera.x) * this.scale);
    const width = parseInt(pointer.element.css('width') + 24, 10);
    const offset = (width / 2) - (tileSize / 2);
    const y = ((posY - this.camera.y) * this.scale) - tileSize;

    pointer.element.css('left', `${x - offset}px`);
    pointer.element.css('top', `${y}px`);
  }

  /**
   * Set the cursor to a specific entity
   * @param {Entity} entity an instance of the entity
   */
  setToEntity(entity) {
    log.debug('Cursor - setToEntity()', entity);

    const pointer = this.get(entity.id);

    if (!pointer) {
      return;
    }

    this.set(pointer, entity.x, entity.y);
  }

  /**
   * Set the pointer to this x and y position
   * @param {Number} id the pointer's id
   * @param {Number}  x the x coordinate
   * @param {Number}  y the y coordinate
   */
  setToPosition(id, x, y) {
    const pointer = this.get(id);

    if (!pointer) {
      return;
    }

    pointer.setPosition(x, y);
    this.set(pointer, x, y);
  }

  /**
   * Set this pointer to a relative position
   * based on the scale of the screen
   * @param {Number} id the pointer's id
   * @param {Number}  x the x coordinate
   * @param {Number}  y the y coordinate
   */
  setRelative(id, x, y) {
    const pointer = this.get(id);

    if (!pointer) {
      return;
    }

    const scale = this.getScale();
    let offsetX = 0;
    let offsetY = 0;

    /**
     * Must be set in accordance to the lowest scale.
     */
    if (scale === 1) {
      offsetX = pointer.element.width() / 2 + 5;
      offsetY = pointer.element.height() / 2 - 4;
    }

    pointer.setPosition(x, y);
    pointer.element.css('left', `${(x * scale) - offsetX}px`);
    pointer.element.css('top', `${(y * scale) - offsetY}px`);
  }

  /**
   * Update the pointer in the render loop
   */
  update() {
    _.each(this.pointers, (pointer) => {
      switch (pointer.type) {
        case Module.Pointers.Entity:
          const entity = this.game.entities.get(pointer.id); // eslint-disable-line

          if (entity) {
            this.setToEntity(entity);
          } else {
            this.destroy(pointer);
          }
          break;
        case Module.Pointers.Position:
          if (pointer.x !== -1 && pointer.y !== -1) {
            this.set(pointer, pointer.x, pointer.y);
          }
          break;
        default:
          break;
      }
    });
  }

  /**
   * Return a specific pointer
   * @param  {Number} id the pointer's id
   * @return {Pointer} the pointer
   */
  get(id) {
    if (id in this.pointers) {
      return this.pointers[id];
    }

    return null;
  }

  /**
   * Update the scale of the pointer
   */
  updateScale() {
    this.scale = this.getDrawingScale();
  }

  /**
   * Update the camera reference with the
   * game renderer camera reference
   */
  updateCamera() {
    this.camera = this.game.renderer.camera;
  }

  /**
   * Return the current scale of the pointer
   * @return {Number} the scale
   */
  getScale() {
    return this.game.getScaleFactor();
  }

  /**
   * Return the renderer's drawing scale
   * @return {Number} the actual renderer calculated drawing scale
   */
  getDrawingScale() {
    return this.game.renderer.getDrawingScale();
  }
}

import $ from 'jquery';
import _ from 'underscore';
import Sprite from '../entity/sprite';
import Animation from '../entity/animation';
import log from '../lib/log';

/**
 * Class responsible for loading all the necessary sprites from the JSON.
 * @class
 */
export default class Sprites {
  /**
   * Default constructor
   * @param {Renderer} renderer instance of the renderer
   */
  constructor(renderer) {
    log.debug('Sprites - constructor()', renderer);

    /**
     * An instance of the game renderer
     * @type {Renderer}
     */
    this.renderer = renderer;

    /**
     * An array of sprites
     * @type {Sprite[]}
     */
    this.sprites = {};

    /**
     * An animation for sparks
     * @type {Animation}
     */
    this.sparksAnimation = null;

    /**
     * A callback function when all sprites are loaded
     * @type {Function}
     */
    this.loadedSpritesCallback = null;

    $.getJSON('/data/sprites.json', (json) => {
      this.loadSpriteData(json);
    });

    this.loadAnimations();
  }

  /**
   * Load data for a sprite
   * @param  {Array} spriteData an array of data for the sprites to load
   */
  loadSpriteData(spriteData) {
    log.debug('Sprites - loadSpriteData()', spriteData);

    _.each(spriteData, (sprite) => {
      this.sprites[sprite.id] = new Sprite(
        sprite,
        this.renderer.drawingScale,
      );
    });

    if (this.loadedSpritesCallback) {
      log.debug('Sprites - loadSpriteData() - Finished loading sprite data...', this.sprites);
      this.loadedSpritesCallback();
    }
  }

  /**
   * Load animations
   */
  loadAnimations() {
    log.debug('Sprites - loadAnimations()');
    this.sparksAnimation = new Animation('idle_down', 6, 0, 16, 16);
    this.sparksAnimation.setSpeed(120);
  }

  /**
   * Update sprites in the render loop
   */
  updateSprites() {
    log.debug('Sprites - updateSprites()', this.renderer.getDrawingScale());

    _.each(this.sprites, (sprite) => {
      sprite.update(this.renderer.getDrawingScale());
    });
  }

  /**
   * Once a sprite is loaded set the sprites callback
   * @param  {Function} callback the callback for the sprite
   */
  onLoadedSprites(callback) {
    log.debug('Sprites - onLoadedSprites()', callback);
    this.loadedSpritesCallback = callback;
  }
}

import Module from '../utils/modules';

/**
 * Keeps track of the {@link Entity} direction
 * @class
 */
export default class Zone {
  /**
   * Default constructor
   * @param {Game} game instance of the game
   */
  constructor(game) {
    /**
     * Instance of the game
     * @type {Game}
     */
    this.game = game;

    /**
     * Instance of the game renderer
     * @type {Renderer}
     */
    this.renderer = game.renderer;

    /**
     * Instance of the game camera
     * @type {Camera}
     */
    this.camera = game.camera;

    /**
     * Instance of the game input
     * @type {Input}
     */
    this.input = game.input;

    /**
     * The direction
     * @type {Orientation}
     */
    this.direction = null;
  }

  /**
   * Reset the direction
   */
  reset() {
    this.direction = null;
  }

  /**
   * Set the direction to up
   */
  setUp() {
    this.direction = Module.Orientation.Up;
  }

  /**
   * Set the direction to down
   */
  setDown() {
    this.direction = Module.Orientation.Down;
  }

  /**
   * Set the direction to right
   */
  setRight() {
    this.direction = Module.Orientation.Right;
  }

  /**
   * Set the direction to right
   */
  setLeft() {
    this.direction = Module.Orientation.Left;
  }

  /**
   * Return the current direction
   * @return {Orientation} the direction for the zone
   */
  getDirection() {
    return this.direction;
  }
}

/**
 * Control animations for a sprite
 * @class
 */
export default class Animation {
  /**
   * Default constructor
   * @param {String} name   sprite sheet name
   * @param {Number} length frame length of the animation
   * @param {Number} row    row in the sprite sheet
   * @param {Number} width  width of 1 frame in the animation
   * @param {Number} height height of 1 frame in the animation
   */
  constructor(name, length, row, width, height) {
    /**
     * Sprite sheet
     * @type {String}
     */
    this.name = name;

    /**
     * Number of rames in the animation
     * @type {Number}
     */
    this.length = length;

    /**
     * Row of the animation in the sprite sheet
     * @type {Number}
     */
    this.row = row;

    /**
     * Width of 1 frame in the animation in pixels
     * @type {Number}
     */
    this.width = width;

    /**
     * Height of 1 frame in the animation in pixels
     * @type {Number}
     */
    this.height = height;

    /**
     * The current frame in the animation
     * @type {Number}
     */
    this.currentFrame = 0;

    /**
     * An internal counter for the callback
     * @type {Number}
     */
    this.count = 0;

    /**
     * End of counter calback function
     * @type {Function}
     */
    this.endCountCallback = null;

    /**
     * The last time the animation was updated
     * @type {Number}
     */
    this.lastTime = 0;

    /**
     * The speed of the animation in miliseconds
     * @type {Number}
     */
    this.speed = 1000;

    // set the baseline values
    this.reset();
  }

  /**
   * Grab the next frame in the animation
   * @return {Void}
   */
  tick() {
    let i = this.currentFrame.index;

    i = i < this.length - 1 ? i + 1 : 0;

    if (this.count > 0 && i === 0) {
      this.count -= 1;

      if (this.count === 0) {
        this.currentFrame.index = 0;
        this.endCountCallback();
        return;
      }
    }

    this.currentFrame.x = this.width * i;
    this.currentFrame.y = this.height * this.row;

    this.currentFrame.index = i;
  }

  /**
   * Update the animation over time by changing to the next frame
   * @param  {Number} time a duration of time
   * @return {Boolean} returns false if not ready to animate again
   */
  update(time) {
    if (this.lastTime === 0 && this.name.substr(0, 3) === 'atk') {
      this.lastTime = time;
    }

    if (this.readyToAnimate(time)) {
      this.lastTime = time;
      this.tick();

      return true;
    }
    return false;
  }

  /**
   * Set the counter for the callback, used for attack sequences
   * @param {Number} count      update the animation callback counter
   * @param {Function} onEndCount set the callback function when the count ends
   */
  setCount(count, onEndCount) {
    this.count = count;
    this.endCountCallback = onEndCount;
  }

  /**
   * Set the speed of the animation
   * @param {Number} speed in miliseconds
   */
  setSpeed(speed) {
    this.speed = speed;
  }

  /**
   * Set the row the animation is in
   * @param {Number} row row in the sprite sheet
   */
  setRow(row) {
    this.row = row;
  }

  /**
   * Figure out whether or not this animation is ready to animate again
   * @param  {Number} time time in miliseconds
   * @return {Boolean} returns true if difference between lasttime > speed
   */
  readyToAnimate(time) {
    return time - this.lastTime > this.speed;
  }

  /**
   * Reset the animation back to the beginning
   */
  reset() {
    this.lastTime = 0;
    this.currentFrame = {
      index: 0,
      x: 0,
      y: this.row * this.height,
    };
  }
}

import _ from 'underscore';
import Entity from '../entity';
import Transition from '../../utils/transition';
import Animation from '../animation';
import Module from '../../utils/modules';

/**
 * A specific type of {@link Entity}. The base class for a
 * {@link Mob} or {@link Npc} or {@link Player}
 * @class
 */
export default class Character extends Entity {
  /**
   * Default constructor
   * @param {Number} id the ID of the {@link Entity}
   * @param {String} kind the kind of {@link Entity} this is (sprite name)
   * @param {String} label the name to display for the overlay
   */
  constructor(id, kind, label) {
    super(id, kind, label);

    /**
     * Next grid X position
     * @type {Number}
     */
    this.nextGridX = -1;

    /**
     * Next grid Y position
     * @type {Number}
     */
    this.nextGridY = -1;

    /**
     * Previous gridX position
     * @type {Number}
     */
    this.prevGridX = -1;

    /**
     * Previous gridY position
     * @type {Number}
     */
    this.prevGridY = -1;

    /**
     * The direction they are pointing
     * @type {Orientation}
     */
    this.orientation = Module.Orientation.Down;

    /**
     * How many hit points they have left
     * @type {Number}
     */
    this.hitPoints = -1;

    /**
     * How many hit points they have total
     * @type {Number}
     */
    this.maxHitPoints = -1;

    /**
     * How much magic they have left
     * @type {Number}
     */
    this.mana = -1;

    /**
     * How much magic they have total
     * @type {Number}
     */
    this.maxMana = -1;

    /**
     * Is there health bar visible or not
     * @type {Boolean}
     */
    this.healthBarVisible = false;

    /**
     * Does their health reset after time is up
     * @type {Boolean}
     */
    this.healthBarTimeout = false;

    /**
     * Are they dead
     * @type {Boolean}
     */
    this.dead = false;

    /**
     * Are they following something
     * @type {Boolean}
     */
    this.following = false;

    /**
     * Are they attacking something
     * @type {Boolean}
     */
    this.attacking = false;

    /**
     * Should their action be interrupted
     * @type {Boolean}
     */
    this.interrupted = false;

    /**
     * Did they get a critical hit
     * @type {Boolean}
     */
    this.critical = false;

    /**
     * Are they frozen
     * @type {Boolean}
     */
    this.frozen = false;

    /**
     * Are they stunned
     * @type {Boolean}
     */
    this.stunned = false;

    /**
     * Did they explode
     * @type {Boolean}
     */
    this.explosion = false;

    /**
     * What is their current path
     * @type {Astar}
     */
    this.path = null;

    /**
     * What is their current target
     * @type {Entity}
     */
    this.target = null;

    /**
     * An array of things trying to attack them
     * @type {Object}
     */
    this.attackers = {};

    /**
     * Movement transitions
     * @type {Transition}
     */
    this.movement = new Transition();

    /**
     * Idle animation speed
     * @type {Number}
     */
    this.idleSpeed = 450;

    /**
     * Attack animation speed
     * @type {Number}
     */
    this.attackAnimationSpeed = 50;

    /**
     * Walk animation speed
     * @type {Number}
     */
    this.walkAnimationSpeed = 100;

    /**
     * Movement animation speed
     * @type {Number}
     */
    this.movementSpeed = 250;

    /**
     * Do they have a weapon
     * @type {Boolean}
     */
    this.weapon = false;

    /**
     * Do they have a shadow
     * @type {Boolean}
     */
    this.shadow = false;

    /**
     * What is their attack range
     * @type {Number}
     */
    this.attackRange = 1;

    /**
     * Flip the sprite along the X axis
     * @type {Boolean}
     */
    this.spriteFlipX = false;

    /**
     * Flip the sprite along the Y axis
     * @type {Boolean}
     */
    this.spriteFlipY = false;

    /**
     * Location of a new destination during pathing
     * @type {Object}
     */
    this.newDestination = null;

    /**
     * Default step number
     * @type {Number}
     */
    this.step = 0;

    /**
     * Object of adjacent tiles while pathing
     * @type {Object}
     */
    this.adjacentTiles = {};

    /**
     * The X and Y locations of the destination
     * @type {{ gridX: Number, gridY: Number}}
     */
    this.destination = {};

    /**
     * If this character is in terror
     * @type {Boolean}
     */
    this.terror = false;

    /**
     * If this character's pathing is being forced
     * @type {Boolean}
     */
    this.forced = false;

    /**
     * Request path callback
     * @type {Function}
     */
    this.requestPathCallback = null;

    /**
     * Start pathing callback
     * @type {Function}
     */
    this.startPathingCallback = null;

    /**
     * Stop pathing callback
     * @type {Function}
     */
    this.stopPathingCallback = null;

    /**
     * Before step callback
     * @type {Function}
     */
    this.beforeStepCallback = null;

    /**
     * Step callback
     * @type {Function}
     */
    this.stepCallback = null;

    /**
     * Second step callback
     * @type {Function}
     */
    this.secondStepCallback = null;

    /**
     * Move callback
     * @type {Function}
     */
    this.moveCallback = null;

    /**
     * Hit points callback
     * @type {Function}
     */
    this.hitPointsCallback = null;

    /**
     * Global critical animation
     * @type {Animation}
     */
    this.criticalAnimation = null;

    /**
     * Global terror animation
     * @type {Animation}
     */
    this.terrorAnimation = null;

    /**
     * Global stun animation
     * @type {Animation}
     */
    this.stunAnimation = null;

    /**
     * Global explosion animation
     * @type {Animation}
     */
    this.explosionAnimation = null;

    // prep this character by loading global animations
    this.loadGlobals();
  }

  /**
   * Load all global character animations
   */
  loadGlobals() {
    this.criticalAnimation = new Animation('atk_down', 10, 0, 48, 48);
    this.criticalAnimation.setSpeed(30);

    this.criticalAnimation.setCount(1, () => {
      this.critical = false;

      this.criticalAnimation.reset();
      this.criticalAnimation.count = 1;
    });

    this.terrorAnimation = new Animation('explosion', 8, 0, 64, 64);
    this.terrorAnimation.setSpeed(50);

    this.terrorAnimation.setCount(1, () => {
      this.terror = false;

      this.terrorAnimation.reset();
      this.terrorAnimation.count = 1;
    });

    this.stunAnimation = new Animation('atk_down', 6, 0, 48, 48);
    this.stunAnimation.setSpeed(30);

    this.explosionAnimation = new Animation('explosion', 8, 0, 64, 64);
    this.explosionAnimation.setSpeed(50);

    this.explosionAnimation.setCount(1, () => {
      this.explosion = false;

      this.explosionAnimation.reset();
      this.explosionAnimation.count = 1;
    });
  }

  /**
   * Animate the character depending on their current orientation
   * @param {String} animation orientation (atk, walk, or idle)
   * @param {Number} speed how fast to animate in ms
   * @param {Number} count how many times to animate
   * @param {Function} onEndCount callback function once the animation count has ended
   * @return {Null|Void} returns null if the character is dead
   */
  animate(animation, speed, count, onEndCount) {
    const orientationOptions = ['atk', 'walk', 'idle'];
    let updatedPosition = null;

    if (this.currentAnimation && this.currentAnimation.name === 'death') {
      return;
    }

    this.spriteFlipX = false;
    this.spriteFlipY = false;

    if (orientationOptions.indexOf(animation) > -1) {
      const characterOrientation = this.orientation === Module.Orientation.Left
        ? 'right'
        : Module.Orientation.toString(this.orientation);

      updatedPosition = `${animation}_${characterOrientation}`;
      this.spriteFlipX = this.orientation === Module.Orientation.Left;
    }

    this.setAnimation((updatedPosition || animation), speed, count, onEndCount);
  }

  /**
   * Look at a specific character
   * @param  {Character} character the character to look at
   */
  lookAt(character) {
    if (character.gridX > this.gridX) {
      this.setOrientation(Module.Orientation.Right);
    } else if (character.gridX < this.gridX) {
      this.setOrientation(Module.Orientation.Left);
    } else if (character.gridY > this.gridY) {
      this.setOrientation(Module.Orientation.Down);
    } else if (character.gridY < this.gridY) {
      this.setOrientation(Module.Orientation.Up);
    }

    this.idle();
  }

  /**
   * Follow a specific character
   * @param  {Character} character the character to follow
   */
  follow(character) {
    console.log('canvas follow', character);
    this.following = true;

    this.setTarget(character);
    this.move(character.gridX, character.gridY);
  }

  /**
   * Attack a specific character
   * @param  {Character} attacker  who is the one attacking (unused currently??)
   * @param  {Character} character the character that should be attacked and followed
   */
  attack(attacker, character) {
    this.attacking = true;
    // @TODO add an attacker? not sure why we're not doing this here but we do in addAttacker??
    // this.attackers[attacker.instance] = character;
    this.follow(character);
  }

  /**
   * Stop following or attacking another character
   */
  backOff() {
    this.attacking = false;
    this.following = false;

    this.removeTarget();
  }

  /**
   * Add an attacker to a character
   * @param {Character} character who is attacking them
   * @return {Boolean} false if they are already attacking this character
   */
  addAttacker(character) {
    if (this.hasAttacker(character)) {
      return false;
    }

    this.attackers[character.instance] = character;
    return true;
  }

  /**
   * Removes an attacker from a character
   * @param  {Character} character who should stop attacking them
   * @return {Boolean} true if deleted, false if not found
   */
  removeAttacker(character) {
    if (this.hasAttacker(character)) {
      return delete this.attackers[character.id];
    }

    return false;
  }

  /**
   * Check if they are already being attacked by this character
   * @param  {Character} character who to check if they are currently an attacker
   * @return {Boolean} false if not currently attacking this character
   */
  hasAttacker(character) {
    if (this.attackers.size === 0) {
      return false;
    }

    return character.instance in this.attackers;
  }

  /**
   * Set a specific animation action
   * @param  {Number} orientation an orientation {@link Modules}
   * @param  {Number} action an action {@link Modules}
   */
  performAction(orientation, action) {
    this.setOrientation(orientation);

    switch (action) {
      case Module.Actions.Idle:
        this.animate('idle', this.idleSpeed);
        break;

      case Module.Actions.Attack:
        this.animate('atk', this.attackAnimationSpeed, 1);
        break;

      case Module.Actions.Walk:
        this.animate('walk', this.walkAnimationSpeed);
        break;
      default:
        break;
    }
  }

  /**
   * Set the idle animation and optional orientation direction
   * @param  {Number} direction an orientation {@link Modules}
   */
  idle(direction) {
    const orientation = direction || this.orientation;

    this.performAction(orientation, Module.Actions.Idle);
  }

  /**
   * Go to a specific x,y value
   * @param  {Number}  x grid x coordinate
   * @param  {Number}  y grid y coordinate
   * @param  {Boolean} forced whether or not to force ths action
   * @return {Boolean} false if movement fails
   */
  go(x, y, forced) {
    if (this.frozen) {
      return false;
    }

    if (this.following) {
      this.following = false;
      this.target = null;
    }

    return this.move(x, y, forced);
  }

  /**
   * Set a new destination during movement somewhere
   * @param  {Number} x grid X coordinate
   * @param  {Number} y grid Y coordinate
   */
  proceed(x, y) {
    this.newDestination = {
      x,
      y,
    };
  }

  /**
   * We can have the movement remain client side because
   * the server side will be responsible for determining
   * whether or not the player should have reached the
   * location and ban all hackers. That and the fact
   * the movement speed is constantly updated to avoid
   * hacks previously present in BrowserQuest.
   * @return {Boolean} false if there is no more steps in the path
   */
  nextStep() {
    let stop = false;
    let path;

    if (this.step % 2 === 0 && this.secondStepCallback) {
      this.secondStepCallback();
    }

    this.prevGridX = this.gridX;
    this.prevGridY = this.gridY;

    if (!this.hasPath()) {
      return false;
    }

    if (this.beforeStepCallback) {
      this.beforeStepCallback();
    }

    this.updateGridPosition();

    if (!this.interrupted) {
      if (this.hasNextStep()) {
        this.nextGridX = this.path[this.step + 1][0]; // eslint-disable-line
        this.nextGridY = this.path[this.step + 1][1]; // eslint-disable-line
      }

      if (this.stepCallback) {
        this.stepCallback();
      }

      // path has changed
      if (this.changedPath()) {
        const { x, y } = this.newDestination;

        path = this.requestPathfinding(x, y);

        if (!path) {
          return false;
        }

        this.newDestination = null;

        if (path.length < 2) {
          stop = true;
        } else {
          this.followPath(path);
        }
      } else if (this.hasNextStep()) { // there is a next step available
        this.step += 1;
        this.updateMovement();
      } else { // stop movement
        stop = true;
      }
    } else { // not interrupted, stop movement
      stop = true;
      this.interrupted = false;
    }

    if (stop) { // stop pathing
      this.path = null;
      this.idle();

      if (this.stopPathingCallback) {
        this.stopPathingCallback(this.gridX, this.gridY, this.forced);
      }

      if (this.forced) {
        this.forced = false;
      }
    }

    return true;
  }

  /**
   * Update movement on the path
   */
  updateMovement() {
    const { step, path } = this;

    if (path[step][0] < path[step - 1][0]) {
      this.performAction(Module.Orientation.Left, Module.Actions.Walk);
    }

    if (path[step][0] > path[step - 1][0]) {
      this.performAction(Module.Orientation.Right, Module.Actions.Walk);
    }

    if (path[step][1] < path[step - 1][1]) {
      this.performAction(Module.Orientation.Up, Module.Actions.Walk);
    }

    if (path[step][1] > path[step - 1][1]) {
      this.performAction(Module.Orientation.Down, Module.Actions.Walk);
    }
  }

  /**
   * Taken from TTA - this is to ensure the player
   * does not click on himself or somehow into another
   * dimension
   * @param {Array} path the path the character is taking as they move
   * @return {Boolean} false if the path is too short or no next step
   */
  followPath(path) {
    if (!path || path.length < 2) {
      return false;
    }

    this.path = path;
    this.step = 0;

    if (this.following) {
      path.pop();
    }

    if (this.startPathingCallback) {
      this.startPathingCallback(path);
    }

    return this.nextStep();
  }

  /**
   * Move the character to a new location
   * @param  {Number} x grid X coordinate
   * @param  {Number} y grid Y coordinate
   * @param  {Boolean} forced whether or not to force the movement
   */
  move(x, y, forced) {
    this.destination = {
      gridX: x,
      gridY: y,
    };

    this.adjacentTiles = {};

    if (this.hasPath() && !forced) {
      this.proceed(x, y);
    } else {
      this.followPath(this.requestPathfinding(x, y));
    }
  }

  /**
   * Stop the character movement
   * @param  {Boolean} force force them to stop
   */
  stop(force) {
    if (!force) {
      this.interrupted = true;
    } else if (this.hasPath()) {
      this.path = null;
      this.newDestination = null;
      this.movement = new Transition();
      this.performAction(this.orientation, Module.Actions.Idle);
      this.nextGridX = this.gridX;
      this.nextGridY = this.gridY;
    }
  }

  /**
   * Get the correct effect animation
   * @return {String} animation name
   */
  getEffectAnimation() {
    if (this.critical) {
      return this.criticalAnimation;
    }

    if (this.stunned) {
      return this.stunAnimation;
    }

    if (this.terror) {
      return this.terrorAnimation;
    }

    if (this.explosion) {
      return this.explosionAnimation;
    }

    return null;
  }

  /**
   * Return the active effect on the character
   * @return {String} the effect in place on the character
   */
  getActiveEffect() {
    if (this.critical) {
      return 'criticaleffect';
    }

    if (this.stunned) {
      return 'stuneffect';
    }

    if (this.terror) {
      return 'explosion-terror';
    }

    if (this.explosion) {
      return 'explosion-fireball';
    }

    return null;
  }

  /**
   * The triggered health bar (currently fighting)
   */
  triggerHealthBar() {
    this.healthBarVisible = true;

    if (this.healthBarTimeout) {
      clearTimeout(this.healthBarTimeout);
    }

    this.healthBarTimeout = setTimeout(() => {
      this.healthBarVisible = false;
    }, 7000);
  }

  /**
   * Clear the health bar from the screen
   */
  clearHealthBar() {
    this.healthBarVisible = false;
    clearTimeout(this.healthBarTimeout);
    this.healthBarTimeout = null;
  }

  /**
   * Request path finding from current position to this position
   * @param  {Number} x grid X coordinate
   * @param  {Number} y grid Y coordinate
   * @return {Array} valid path or null
   */
  requestPathfinding(x, y) {
    if (this.requestPathCallback) {
      return this.requestPathCallback(x, y);
    }

    return null;
  }

  /**
   * Update the character's position on the grid
   */
  updateGridPosition() {
    this.setGridPosition(this.path[this.step][0], this.path[this.step][1]);
  }

  /**
   * Check if the character is currently moving
   * @return {Boolean} true if moving
   */
  isMoving() {
    return (
      this.currentAnimation.name === 'walk'
      && (this.x % 2 !== 0 || this.y % 2 !== 0)
    );
  }

  /**
   * Apply a callback to each attacker
   * @param  {Function} callback function to apply to each attacker
   */
  forEachAttacker(callback) {
    _.each(this.attackers, (attacker) => {
      callback(attacker);
    });
  }

  /**
   * Check if they are being attacked
   * @return {Boolean} returns true if they have any attackers
   */
  isAttacked() {
    return Object.keys(this.attackers).length > 0;
  }

  /**
   * Returns true if they have a weapon
   * @return {Boolean}
   */
  hasWeapon() {
    return this.weapon;
  }

  /**
   * Returns true if they have a shadow
   * @return {Boolean}
   */
  hasShadow() {
    return this.shadow;
  }

  /**
   * Returns true if they are targeting someone
   * @return {Boolean}
   */
  hasTarget() {
    return !(this.target === null);
  }

  /**
   * Returns true if they have a path
   * @return {Boolean}
   */
  hasPath() {
    return this.path !== null;
  }

  /**
   * Returns true if they have a next path step
   * @return {Boolean}
   */
  hasNextStep() {
    return this.path.length - 1 > this.step;
  }

  /**
   * Returns true if they've changed paths
   * @return {[type]}
   */
  changedPath() {
    return !!this.newDestination;
  }

  /**
   * Returns true if the target was removed
   * @return {Boolean}
   */
  removeTarget() {
    if (!this.target) {
      return false;
    }

    this.target = null;
    return true;
  }

  /**
   * Removes all attakers
   */
  forget() {
    this.attackers = {};
  }

  /**
   * The character has moved, trigger the move callback if there is one
   */
  moved() {
    this.loadDirty();

    if (this.moveCallback) {
      this.moveCallback();
    }
  }

  /**
   * Sets a target for this character
   * @param {Character} target the character to target
   */
  setTarget(target) {
    console.log('canvas setting target', target);
    if (target === null) {
      this.removeTarget();
      return;
    }

    // they are already targeting this character
    if (this.target && this.target.id === target.id) {
      console.log('canvas already has this target');
      return;
    }

    // remove any old targets
    if (this.hasTarget()) {
      console.log('canvas removing old target');
      this.removeTarget();
    }

    this.target = target;
  }

  /**
   * Set the character's hit points then triggers hitPointsCallback
   * @param {Number} hitPoints how many hit points they caused
   */
  setHitPoints(hitPoints) {
    this.hitPoints = hitPoints;

    if (this.hitPointsCallback) {
      this.hitPointsCallback(this.hitPoints);
    }
  }

  /**
   * Set the character's maximum hit points
   * @param {Number} maxHitPoints how much HP total
   */
  setMaxHitPoints(maxHitPoints) {
    this.maxHitPoints = maxHitPoints;
  }

  /**
   * Set the character's orientation
   * @param {String} orientation which direction {@link Modules}
   */
  setOrientation(orientation) {
    this.orientation = orientation;
  }

  /**
   * Callback for path requested
   * @param  {Function} callback
   */
  onRequestPath(callback) {
    this.requestPathCallback = callback;
  }

  /**
   * Start pathing callback
   * @param  {Function} callback
   */
  onStartPathing(callback) {
    this.startPathingCallback = callback;
  }

  /**
   * Stop pathing callback
   * @param  {Function} callback
   */
  onStopPathing(callback) {
    this.stopPathingCallback = callback;
  }

  /**
   * Before a pathing step callback
   * @param  {Function} callback
   */
  onBeforeStep(callback) {
    this.beforeStepCallback = callback;
  }

  /**
   * Pathing step callback
   * @param  {Function} callback
   */
  onStep(callback) {
    this.stepCallback = callback;
  }

  /**
   * Second step callback
   * @param  {Function} callback
   */
  onSecondStep(callback) {
    this.secondStepCallback = callback;
  }

  /**
   * On move callback
   * @param  {Function} callback
   */
  onMove(callback) {
    this.moveCallback = callback;
  }

  /**
   * On hit points callback
   * @param  {Function} callback
   */
  onHitPoints(callback) {
    this.hitPointsCallback = callback;
  }
}

