   * Load equipment for this player given the data response from the server
   * @param {Object} data player equipment and info from the server
   * @param {Number} data.instance ID of the player
   * @param {Number} data.x grid X position
   * @param {Number} data.y grid Y position
   * @param {Number} data.hitPoints current hit points
   * @param {Number} data.mana current mana points
   * @param {String} data.username the name of the player
   * @param {Number} data.experience the player experience points
   * @param {Number} data.level the player's level
   * @param {String} data.lastLogin the last login date
   * @param {Number} data.pvpKills the total pvp kills
   * @param {Number} data.pvpDeaths the total pvp deaths
   */
  loadPlayer(data) {
    this.setId(data.instance);
    this.setGridPosition(data.x, data.y);
    this.setPointsData(data.hitPoints, data.mana);

    this.username = data.username;
    this.experience = data.experience;
    this.level = data.level;
    this.lastLogin = data.lastLogin;
    this.pvpKills = data.pvpKills;
    this.pvpDeaths = data.pvpDeaths;
  }

  /**
   * This is for other player characters
   * @param {Game} game an instance of the game
   */
  loadHandler(game) {
    this.handler.setGame(game);
    this.handler.loadEntity();
  }

  /**
   * Returns true if there was keyboard movememnt
   * @return {Boolean}
   */
  hasKeyboardMovement() {
    return this.moveLeft || this.moveRight || this.moveUp || this.moveDown;
  }

  /**
   * Sets the ID of the player
   * @param {Number} id player ID
   */
  setId(id) {
    this.id = id;
  }

  /**
   * Loads the player's default (empty) equipment
   */
  loadEquipment() {
    this.armour = null;
    this.weapon = null;
    this.pendant = null;
    this.ring = null;
    this.boots = null;
  }

  /**
   * Returns true if the has a ranged weapon
   * @return {Boolean}
   */
  isRanged() {
    // @TODO try changing to // this.weapon && this.weapon.exists() && this.weapon.ranged;
    return this.weapon && this.weapon.ranged;
  }

  /**
   * Returns true if the player has a weapon
   * @return {Boolean}
   */
  hasWeapon() {
    return this.weapon ? this.weapon.exists() : false;
  }

  /**
   * Sets the name and username of the player
   * @param {String} name username of the player
   */
  setName(name) {
    // @TODO why do we need both of these??
    this.username = name;
    this.name = name;
  }

  /**
   * Gets the name of the sprite depending on their equipped armor
   * defaults to `clotharmor` if they don't have anything equipped
   * @return {String} sprite name for the character's armor
   */
  getSpriteName() {
    return this.armour
      ? this.armour.name
      : 'clotharmor';
  }

  /**
   * Set the player's current mana
   * @param {Number} mana amount
   */
  setMana(mana) {
    this.mana = mana;
  }

  /**
   * Set the player's maximum mana amount
   * @param {[type]} maxMana amount
   */
  setMaxMana(maxMana) {
    this.maxMana = maxMana;
  }

  /**
   * Get the player's grid X position
   * @return {Number} grid X position
   */
  getX() {
    return this.gridX;
  }

  /**
   * Get the player's grid Y position
   * @return {Number} grid Y position
   */
  getY() {
    return this.gridY;
  }

  /**
   * Set the player's health and mana from the server data
   * @param {Array} hitPointsData in the format of [ id, hitPoints, maxHitPoints ]
   * @param {Array} manaData      in the format of [ id, mana, maxMana ]
   */
  setPointsData(hitPointsData, manaData) {
    const hitPoints = hitPointsData.shift();
    const maxHitPoints = hitPointsData.shift();
    const mana = manaData.shift();
    const maxMana = manaData.shift();

    this.setHitPoints(hitPoints);
    this.setMana(mana);

    this.setMaxHitPoints(maxHitPoints);
    this.setMaxMana(maxMana);
  }

  /**
   * Set the player's equipment from the server
   * @param {Modules} type values from Modules.Equipment
   * @param {Array} info in the format of [ id, name, string, count, ability, abilityLevel ]
   */
  setEquipment(type, info) {
    const name = info.shift();
    const string = info.shift();
    const count = info.shift();
    const ability = info.shift();
    const abilityLevel = info.shift();

    switch (type) {
      case Module.Equipment.Armour:
        if (!this.armour) {
          this.armour = new Armour(
            name,
            string,
            count,
            ability,
            abilityLevel,
          );
        } else {
          this.armour.update(name, string, count, ability, abilityLevel);
        }

        if (this.updateArmourCallback) {
          this.updateArmourCallback(string);
        }

        break;

      case Module.Equipment.Weapon:
        if (!this.weapon) {
          this.weapon = new Weapon(
            name,
            string,
            count,
            ability,
            abilityLevel,
          );
        } else {
          this.weapon.update(name, string, count, ability, abilityLevel);
        }

        this.weapon.ranged = string && string.includes('bow');

        break;

      case Module.Equipment.Pendant:
        if (!this.pendant) {
          this.pendant = new Pendant(
            name,
            string,
            count,
            ability,
            abilityLevel,
          );
        } else {
          this.pendant.update(name, string, count, ability, abilityLevel);
        }

        break;

      case Module.Equipment.Ring:
        if (!this.ring) {
          this.ring = new Ring(name, string, count, ability, abilityLevel);
        } else {
          this.ring.update(name, string, count, ability, abilityLevel);
        }

        break;

      case Module.Equipment.Boots:
        if (!this.boots) {
          this.boots = new Boots(name, string, count, ability, abilityLevel);
        } else {
          this.boots.update(name, string, count, ability, abilityLevel);
        }

        break;
      default:
        break;
    }
  }

  /**
   * Unequip an item
   * @param  {String} type  type of equipment to remove
   */
  unequip(type) {
    switch (type) {
      case 'armour':
        this.armour.update('Cloth Armour', 'clotharmor', 1, -1, -1);
        break;

      case 'weapon':
        this.weapon.update(null, null, -1, -1, -1);
        break;

      case 'pendant':
        this.pendant.update(null, null, -1, -1, -1);
        break;

      case 'ring':
        this.ring.update(null, null, -1, -1, -1);
        break;

      case 'boots':
        this.boots.update(null, null, -1, -1, -1);
        break;
      default:
        break;
    }
  }

  /**
   * Temporary blink the entity
   */
  tempBlink() {
    this.blink(90);

    if (!this.tempBlinkTimeout) {
      this.tempBlinkTimeout = setTimeout(() => {
        this.stopBlinking();
      }, 500);
    }
  }

  /**
   * Set the callback for updating armour
   * @param  {Function} callback function to reset armor
   */
  onUpdateArmour(callback) {
    this.updateArmourCallback = callback;
  }
}

import Packets from '../../../network/packets';

/**
 * This is a player handler, responsible for all the {@link Player} callbacks
 * without having to clutter up the entire file.
 * @class
 */
export default class PlayerHandler {
  /**
   * Default constructor
   * @param {Game} game instance of the game
   * @param {Player} player instance of the player
   */
  constructor(game, player) {
    /**
     * Instance of the game
     * @type {Game}
     */
    this.game = game;

    /**
     * Reference to the game Camera
     * @type {Camera}
     */
    this.camera = game.getCamera();

    /**
     * Reference to input in the game
     * @type {Input}
     */
    this.input = game.input;

    /**
     * Reference to the player
     * @type {Player}
     */
    this.player = player;

    /**
     * Entities in the game
     * @type {Entities}
     */
    this.entities = game.entities;

    /**
     * Reference to the server Socket
     * @type {Socket}
     */
    this.socket = game.socket;

    /**
     * Reference to the render layers
     * @type {Renderer}
     */
    this.renderer = game.renderer;

    // load the player handler
    this.loadPlayerHandler();
  }

  /**
   * Load the the callbacks for the player
   */
  loadPlayerHandler() {
    this.player.onRequestPath((x, y) => {
      if (this.player.dead) {
        return null;
      }

      const ignores = [this.player];

      if (this.player.hasTarget()) {
        ignores.push(this.player.target);
      }

      this.socket.send(Packets.Movement, [
        Packets.MovementOpcode.Request,
        x,
        y,
        this.player.gridX,
        this.player.gridY,
      ]);

      return this.game.findPath(this.player, x, y, ignores);
    });

    this.player.onStartPathing((path) => {
      const i = path.length - 1;

      this.input.selectedX = path[i][0]; // eslint-disable-line
      this.input.selectedY = path[i][1]; // eslint-disable-line
      this.input.selectedCellVisible = true;

      if (!this.game.getEntityAt(this.input.selectedX, this.input.selectedY)) {
        console.log('canvas setting target no entity');
        this.socket.send(Packets.Target, [Packets.TargetOpcode.None]);
      }

      this.socket.send(Packets.Movement, [
        Packets.MovementOpcode.Started,
        this.input.selectedX,
        this.input.selectedY,
        this.player.gridX,
        this.player.gridY,
      ]);
    });

    this.player.onStopPathing((x, y) => {
      this.entities.unregisterPosition(this.player);
      this.entities.registerPosition(this.player);

      this.input.selectedCellVisible = false;

      this.camera.clip();

      const entity = this.game.getEntityAt(x, y, true);

      const hasTarget = this.player.hasTarget();

      const id = (entity && entity.id) || null;

      this.socket.send(Packets.Movement, [
        Packets.MovementOpcode.Stop,
        x,
        y,
        id,
        hasTarget,
      ]);

      if (hasTarget) {
        console.log('canvas stop pathing has target', hasTarget, this.player.target.id);
        this.socket.send(Packets.Target, [
          this.isAttackable()
            ? Packets.TargetOpcode.Attack
            : Packets.TargetOpcode.Talk,
          this.player.target.id,
        ]);

        this.player.lookAt(this.player.target);
      }

      this.input.setPassiveTarget();
    });

    this.player.onBeforeStep(() => {
      this.entities.unregisterPosition(this.player);

      if (!this.isAttackable()) {
        return;
      }

      if (this.player.isRanged()) {
        if (this.player.getDistance(this.player.target) < 7) {
          this.player.stop();
        }
      } else {
        this.input.selectedX = this.player.target.gridX;
        this.input.selectedY = this.player.target.gridY;
      }
    });

    this.player.onStep(() => {
      if (this.player.hasNextStep()) {
        this.entities.registerDuality(this.player);
      }

      if (!this.camera.centered) {
        this.checkBounds();
      }

      this.player.forEachAttacker((attacker) => {
        if (!attacker.stunned) attacker.follow(this.player);
      });

      this.socket.send(Packets.Movement, [
        Packets.MovementOpcode.Step,
        this.player.gridX,
        this.player.gridY,
      ]);
    });

    this.player.onSecondStep(() => {
      this.renderer.updateAnimatedTiles();
    });

    /**
     * This is a callback representing the absolute exact position of the player.
     */
    this.player.onMove(() => {
      if (this.camera.centered) {
        this.camera.centreOn(this.player);
      }

      if (this.player.hasTarget()) {
        this.player.follow(this.player.target);
      }
    });

    this.player.onUpdateArmour((armourName) => {
      this.player.setSprite(this.game.getSprite(armourName));
    });
  }

  /**
   * Whether or not this player can be attacked
   * @return {Boolean} returns true if it can be attacked
   */
  isAttackable() {
    const { target } = this.player;

    if (!target) {
      return false;
    }

    return target.type === 'mob' || (target.type === 'player' && target.pvp);
  }

  /**
   * Check the bounds of the player when they're not centered
   */
  checkBounds() {
    // get the X and Y position on the grid relative to
    // the player and the camera coordinates to check for
    // zoning directions
    const x = this.player.gridX - this.camera.gridX;
    const y = this.player.gridY - this.camera.gridY;

    if (x === 0) {
      this.game.zoning.setLeft();
    } else if (y === 0) {
      this.game.zoning.setUp();
    } else if (x === this.camera.gridWidth - 1) {
      this.game.zoning.setRight();
    } else if (y === this.camera.gridHeight - 1) {
      this.game.zoning.setDown();
    }

    // if the game is zoning to a direction update
    // the camera to the zone direction
    if (this.game.zoning.direction !== null) {
      this.camera.zone(this.game.zoning.getDirection());
      this.game.zoning.reset();
    }
  }
}

import EntityHandler from './entityhandler';
import log from '@/client/lib/log';

/**
 * Initialize a new entity:
 * - {@link Character}
 *   - {@link Mob}
 *   - {@link Npc}
 *   - {@link Player}
 * - {@link Chest}
 * - {@link Item}
 * - {@link Projectile}
 * @class
 * @example const myEntity = new Entity(1, 'player');
 */
export default class Entity {
  /**
   * Constructor to initialize a new entity
   * @param {Number} id the entity id
   * @param {String} kind the type of entity this is
   * @param {String} label a name for the entity this is
   */
  constructor(id, kind, label) {
    log.debug('Entity - constructor()', id, kind, label);

    /**
     * Entity id
     * @type {Number}
     */
    this.id = id;

    /**
     * Name of the entity
     * @type {String}
     */
    this.name = kind;

    /**
     * Label for the entity
     * @type {String}
     */
    this.label = label;

    /**
     * X coordinate
     * @type {Number}
     */
    this.x = 0;

    /**
     * Y coordinate
     * @type {Number}
     */
    this.y = 0;

    /**
     * Grid X position
     * @type {Number}
     */
    this.gridX = 0;

    /**
     * Grid Y position
     * @type {Number}
     */
    this.gridY = 0;

    /**
     * Sprite
     * @type {Sprite}
     */
    this.sprite = null;

    /**
     * Whether or not to flip the sprite on the X axis
     * @type {Boolean}
     */
    this.spriteFlipX = false;

    /**
     * Whether or not to flip the sprite on the Y axis
     * @type {Boolean}
     */
    this.spriteFlipY = false;

    /**
     * Animations for the sprite
     * @type {Animation}
     */
    this.animations = null;

    /**
     * Current animation the sprite should be playing
     * @type {String}
     */
    this.currentAnimation = null;

    /**
     * Offset for the shadow on the Y axis
     * @type {Number}
     */
    this.shadowOffsetY = 0;

    /**
     * Whether or not the sprite has been loaded
     * @type {Boolean}
     */
    this.spriteLoaded = false;

    /**
     * Wehther or not this entity is visible
     * @type {Boolean}
     */
    this.visible = true;

    /**
     * Whether or not this is fading
     * @type {Boolean}
     */
    this.fading = false;

    /**
     * Handler for the entity
     * @type {EntityHandler}
     */
    this.handler = new EntityHandler(this);

    /**
     * [angled description]
     * @type {Boolean}
     */
    this.angled = false;

    /**
     * Angle of the entity
     * @type {Number}
     */
    this.angle = 0;

    /**
     * Critical health
     * @type {Boolean}
     */
    this.critical = false;

    /**
     * Stunned status
     * @type {Boolean}
     */
    this.stunned = false;

    /**
     * Terror status
     * @type {Boolean}
     */
    this.terror = false;

    /**
     * Non pathable
     * @type {Boolean}
     */
    this.nonPathable = false;

    /**
     * Has a counter
     * @type {Boolean}
     */
    this.hasCounter = false;

    /**
     * Count down time
     * @type {Number}
     */
    this.countdownTime = 0;

    /**
     * Current counter
     * @type {Number}
     */
    this.counter = 0;

    /**
     * Has a shadow
     * @type {Boolean}
     */
    this.shadow = false;

    /**
     * Path
     * @type {Boolean}
     */
    this.path = false;

    /**
     * Rendering data
     * @type {Object}
     */
    this.renderingData = {
      scale: -1,
      angle: 0,
    };

    /**
     * Set if the entity is dirty
     * @type {Boolean}
     */
    this.dirty = false;

    /**
     * Fading time
     * @type {Number}
     */
    this.fadingTime = 0;

    /**
     * Timeout function for blinking
     * @type {Function}
     */
    this.blinking = null;

    /**
     * Normal sprite name
     * @type {String}
     */
    this.normalSprite = null;

    /**
     * Hurt sprite name
     * @type {String}
     */
    this.hurtSprite = null;

    /**
     * CAllback function when the entity is ready
     * @type {Function}
     */
    this.readyCallback = null;

    /**
     * Callback for when the entity is dirty
     * @type {Function}
     */
    this.dirtyCallback = null;

    this.loadDirty();
  }

  /**
   * Checks to see if the x,y coordinate is adjacent to the
   * entity's current position
   *
   * @param {Number} x coordinate
   * @param {Number} y coordinate
   * @param {Boolean} ignoreDiagonals if true, will ignore diagonal
   * directions, defaults to false (optional)
   * @return {Boolean}
   */
  isPositionAdjacent(x, y, ignoreDiagonals = false) {
    // north west - diagonal
    if (!ignoreDiagonals && x - 1 === this.gridX && y + 1 === this.gridY) {
      return true;
    }

    // north
    if (x === this.gridX && y + 1 === this.gridY) {
      return true;
    }

    // north east - diagonal
    if (!ignoreDiagonals && x + 1 === this.gridX && y + 1 === this.gridY) {
      return true;
    }

    // west
    if (x - 1 === this.gridX && y === this.gridY) {
      return true;
    }

    // east
    if (x + 1 === this.gridX && y === this.gridY) {
      return true;
    }

    // south west - diagonal
    if (!ignoreDiagonals && x - 1 === this.gridX && y - 1 === this.gridY) {
      return true;
    }

    // south
    if (x === this.gridX && y - 1 === this.gridY) {
      return true;
    }

    // south west - diagonal
    if (!ignoreDiagonals && x - 1 === this.gridX && y - 1 === this.gridY) {
      return true;
    }

    return false;
  }

  /**
   * This is important for when the client is
   * on a mobile screen. So the sprite has to be
   * handled differently.
   */
  loadDirty() {
    this.dirty = true;

    if (this.dirtyCallback) {
      this.dirtyCallback();
    }
  }

  /**
   * Tell this to fade in given the duration
   * @param {Number} time the amount of time to fade in
   */
  fadeIn(time) {
    this.fading = true;
    this.fadingTime = time;
  }

  /**
   * Tell this entity to start blinking
   * @param  {Number} speed the interval time between blinks
   */
  blink(speed) {
    this.blinking = setInterval(() => {
      this.toggleVisibility();
    }, speed);
  }

  /**
   * Tell the entity to stop blinking and force it to be visible
   */
  stopBlinking() {
    if (this.blinking) {
      clearInterval(this.blinking);
    }

    this.setVisible(true);
  }

  /**
   * Set the entity's name
   * @param {String} name the name of this entity
   */
  setName(name) {
    this.name = name;
  }

  /**
   * Set the sprite for this entity
   * @param {Sprite} sprite the sprite for this entity
   */
  setSprite(sprite) {
    if (!sprite || (this.sprite && this.sprite.name === sprite.name)) {
      return;
    }

    if (!sprite.loaded) {
      sprite.loadSprite();
    }

    sprite.name = sprite.id; // eslint-disable-line

    this.sprite = sprite;

    this.normalSprite = this.sprite;
    this.hurtSprite = sprite.getHurtSprite();
    this.animations = sprite.createAnimations();
    this.spriteLoaded = true;

    if (this.readyCallback) {
      this.readyCallback();
    }
  }

  /**
   * Set the x,y position for the entity
   * @param {Number} x the x position on the screen
   * @param {[type]} y the y position on the screen
   */
  setPosition(x, y) {
    this.x = x;
    this.y = y;
  }

  /**
   * Set the x,y position of the entity on the tile grid
   * @param {Number} x the gridX
   * @param {Number} y the gridY
   */
  setGridPosition(x, y) {
    this.gridX = x;
    this.gridY = y;

    this.setPosition(x * 16, y * 16);
  }

  /**
   * Set the animation on this entity
   * @param {String} name       the name of the animation
   * @param {Number} speed      the animation speed
   * @param {Number} count      how many times to play the animation
   * @param {Number} onEndCount callback to trigger when the animation
   * ends after count number of times
   */
  setAnimation(name, speed, count, onEndCount) {
    if (
      !this.spriteLoaded
      || (this.currentAnimation && this.currentAnimation.name === name)
    ) {
      return;
    }

    const anim = this.getAnimationByName(name);

    if (!anim) {
      return;
    }

    this.currentAnimation = anim;

    if (name.substr(0, 3) === 'atk') {
      this.currentAnimation.reset();
    }

    this.currentAnimation.setSpeed(speed);

    this.currentAnimation.setCount(
      count || 0,
      onEndCount
      || (() => {
        this.idle();
      }),
    );
  }

  /**
   * Set the count for the count down time
   * @param {Number} count sets the counter
   */
  setCountdown(count) {
    this.counter = count;
    this.countdownTime = new Date().getTime();
    this.hasCounter = true;
  }

  /**
   * Returns the instance of the weapon this entity has
   * @return {Item|Projectile} a weapon or projectile
   */
  hasWeapon() {
    return this.weapon;
  }

  /**
   * Get the distance from this entity to another entity
   * @param  {Entity} entity the entity you want to get the distance to
   * @return {Number} the furtherst x or y distance from the given entity
   */
  getDistance(entity) {
    const x = Math.abs(this.gridX - entity.gridX);
    const y = Math.abs(this.gridY - entity.gridY);
    return x > y ? x : y;
  }

  /**
   * Get the distance from this entity to the given x,y coordinates
   * @param  {Number} toX the x coordinate
   * @param  {Number} toY the y coordinate
   * @return {Number} the further x or y distance to this coordinate
   */
  getCoordDistance(toX, toY) {
    const x = Math.abs(this.gridX - toX);
    const y = Math.abs(this.gridY - toY);

    return x > y ? x : y;
  }

  /**
   * Figure out if this entity is within the attack radius of the given entity
   * @param  {Entity} entity Player|Mob|Character|NPC
   * @return {Boolean} returns true if they are within the entity's attack radius,
   * if the entity has no attack radius default to 2 grid cells
   */
  inAttackRadius(entity) {
    const attackRadius = entity.attackRadius || 2;
    return (
      entity
      && this.getDistance(entity) < attackRadius
      && !(this.gridX !== entity.gridX && this.gridY !== entity.gridY)
    );
  }

  /**
   * Figure out if this entity is within the extended attack radius of the given entity
   * @param  {Entity} entity Player|Mob|Character|NPC
   * @return {Boolean} returns true if they are within the entity's attack radius,
   * if the entity has no attack radius default to 3 grid cells
   */
  inExtraAttackRadius(entity) {
    const attackRadius = (entity.attackRadius + 1) || 3;
    return (
      entity
      && this.getDistance(entity) < attackRadius
      && !(this.gridX !== entity.gridX && this.gridY !== entity.gridY)
    );
  }

  /**
   * Look for an animtions list for this specific animation given it's name
   * @param  {String} name the name of the animation
   * @return {Animation|null} returns the animation object or null if not found
   */
  getAnimationByName(name) {
    if (name in this.animations) {
      return this.animations[name];
    }

    return null;
  }

  /**
   * Returns the name of the sprite
   * @return {String} the name of the sprite
   */
  getSprite() {
    return this.sprite.name;
  }

  /**
   * Set the visibility on this entity
   * @param {Boolean} visible true|false
   */
  setVisible(visible) {
    this.visible = visible;
  }

  /**
   * Toggle the current visibility of this entity
   */
  toggleVisibility() {
    this.setVisible(!this.visible);
  }

  /**
   * Check whether or not this entity is visible
   * @return {Boolean} returns true if visibile
   */
  isVisible() {
    return this.visible;
  }

  /**
   * Check whether or not this entity has a shadow
   * @return {Object} returns the entity's shadow
   */
  hasShadow() {
    return this.shadow;
  }

  /**
   * Check if this entity currently has a path
   * @return {Object} returns the entity's path
   */
  hasPath() {
    return this.path;
  }

  /**
   * When this entity's sprite is loaded trigger this callback
   * @param  {Function} callback the function to call when the sprite is loaded
   */
  onReady(callback) {
    this.readyCallback = callback;
  }

  /**
   * When this entity's is dirty (mobile) use this callback
   * @param {Function} callback the function to call when the sprite isDirty
   */
  onDirty(callback) {
    this.dirtyCallback = callback;
  }
}

