import Packets from '../network/packets';
import log from '../lib/log';

/**
* This is a entity handler, responsible for all the {@link Entity} callbacks
* without having to clutter up the entire file.
* @class
*/
export default class EntityHandler {
  /**
   * Default constructor
   * @param {Entity} entity that will be handled
   */
  constructor(entity) {
    log.debug('Entity Handler - constructor()', entity);

    /**
     * Instance of the entity
     * @type {Entity}
     */
    this.entity = entity;

    /**
     * Instance of the game
     * @type {Game}
     */
    this.game = null;

    /**
     * An array of nearby entities
     * @type {Entities[]}
     */
    this.entities = null;
  }

  /**
   * Load the entity
   * @return {Boolean} false if no entity or game
   */
  load() {
    if (!this.entity || !this.game) {
      return false;
    }

    if (this.isCharacter()) {
      this.entity.onRequestPath((x, y) => {
        const ignored = [this.entity];

        return this.game.findPath(this.entity, x, y, ignored);
      });

      this.entity.onBeforeStep(() => {
        this.entities.unregisterPosition(this.entity);
      });

      this.entity.onStep(() => {
        this.entities.registerDuality(this.entity);

        this.entity.forEachAttacker((attacker) => {
          if (
            attacker.hasTarget()
            && attacker.target.id === this.entity.id
            && !attacker.stunned
          ) attacker.follow(this.entity);
        });

        if (this.entity.type === 'mob') {
          this.game.socket.send(Packets.Movement, [
            Packets.MovementOpcode.Entity,
            this.entity.id,
            this.entity.gridX,
            this.entity.gridY,
          ]);
        }

        if (
          this.entity.attackRange > 1
          && this.entity.hasTarget()
          && this.entity.getDistance(this.entity.target)
          <= this.entity.attackRange
        ) this.entity.stop(false);
      });

      this.entity.onStopPathing(() => {
        this.entities.grids.addToRenderingGrid(
          this.entity,
          this.entity.gridX,
          this.entity.gridY,
        );

        this.entities.unregisterPosition(this.entity);
        this.entities.registerPosition(this.entity);
      });
    }

    return true;
  }

  /**
   * Check if this is a character
   * @return {Boolean}
   */
  isCharacter() {
    return (
      this.entity.type
      && (this.entity.type === 'player'
        || this.entity.type === 'mob'
        || this.entity.type === 'npc')
    );
  }

  /**
   * Set the game instance
   * @param {Game} game instance of the game
   */
  setGame(game) {
    if (!this.game) {
      this.game = game;
    }

    this.setEntities(this.game.entities);
  }

  /**
   * Set the entities list
   * @param {Entities[]} entities array of Entity objects
   */
  setEntities(entities) {
    if (!this.entities) {
      this.entities = entities;
    }
  }
}

import Entity from '../entity';

/**
* This os an object type of {@link Entity}
* @class
*/
export default class Chest extends Entity {
  /**
   * Default constructor
   * @param {Number} id    id for the entity
   * @param {String} kind  type of the entity
   */
  constructor(id, kind) {
    super(id, kind);

    /**
     * The type of the entity
     * @type {String}
     */
    this.type = 'chest';
  }

  /**
   * Idle animation
   */
  idle() {
    this.setAnimation('idle_down', 150);
  }

  /**
   * Stops the idle animation
   */
  stop() {
    this.setAnimation('idle_down', 150);
  }
}

import Entity from '../entity';

/**
* This os an object type of {@link Entity}
* @class
*/
export default class Item extends Entity {
  /**
   * Default constructor
   * @param {Number} id            id of the item
   * @param {String} kind          type of the item
   * @param {String} name          name of the item
   * @param {Number} count         how many
   * @param {Ability} ability      ability
   * @param {Number} abilityLevel  ability level
   */
  constructor(id, kind, name, count, ability, abilityLevel) {
    super(id, kind, name);

    /**
     * How many
     * @type {Number}
     */
    this.count = count;

    /**
     * Ability
     * @type {Ability}
     */
    this.ability = ability;

    /**
     * Ability level
     * @type {Number}
     */
    this.abilityLevel = abilityLevel;

    /**
     * If this item was dropped
     * @type {Boolean}
     */
    this.dropped = false;

    /**
     * If this item is stacked
     * @type {Boolean}
     */
    this.stackable = false;

    /**
     * Type of entity
     * @type {String}
     */
    this.type = 'item';

    /**
     * Whether or not it has a shadow
     * @type {Boolean}
     */
    this.shadow = true;
  }

  /**
   * Set the idle animation
   */
  idle() {
    this.setAnimation('idle', 150);
  }

  /**
   * Check if this has a shadow or not
   * @return {Boolean}
   */
  hasShadow() {
    return this.shadow;
  }
}

import Entity from '../entity';

/**
* This os an object type of {@link Entity}
* @class
*/
export default class Projectile extends Entity {
  /**
   * Default constructor
   * @param {Number} id     entity id number
   * @param {String} kind   type of projectile
   * @param {Entity} owner  who owns this projectile
   * @param {String} name   name of the projectile
   */
  constructor(id, kind, owner, name) {
    super(id, kind);

    /**
     * Owner of this projectile
     * @type {Entity}
     */
    this.owner = owner;

    /**
     * Name of the projectile
     * @type {String}
     */
    this.name = name || '';

    /**
     * X coordinate start position
     * @type {Number}
     */
    this.startX = -1;

    /**
     * Y coordinate start position
     * @type {Number}
     */
    this.startY = -1;

    /**
     * X coordinate destination
     * @type {Number}
     */
    this.destX = -1;

    /**
     * Y coordinate destination
     * @type {Number}
     */
    this.destY = -1;

    /**
     * Speed of the projectile
     * @type {Number}
     */
    this.speed = 200;

    /**
     * If this is a special projectile
     * @type {Number}
     */
    this.special = -1;

    /**
     * If this is a static projectile
     * @type {Boolean}
     */
    this.static = false;

    /**
     * If this is a dynamic projectile
     * @type {Boolean}
     */
    this.dynamic = false;

    /**
     * Angle of the projectile
     * @type {Number}
     */
    this.angle = 0;

    /**
     * Path of the projectile
     * @type {Boolean}
     */
    this.path = false;

    /**
     * Callback function for impact
     * @type {Function}
     */
    this.impactCallback = null;
  }

  /**
   * Return the ID
   * @return {Number}
   */
  getId() {
    return this.id;
  }

  /**
   * Check if this has made an impact
   */
  impact() {
    if (this.impactCallback) {
      this.impactCallback();
    }
  }

  /**
   * Set the starting point for the projectile
   * @param {Number} x  x coordinate
   * @param {Number} y  y coordinate
   */
  setStart(x, y) {
    this.setGridPosition(Math.floor(x / 16), Math.floor(y / 16));

    this.startX = x;
    this.startY = y;
  }

  /**
   * Set the projectile destination
   * @param {Number} x  x coordinate
   * @param {Number} y  y coordinate
   */
  setDestination(x, y) {
    this.static = true;

    this.destX = x;
    this.destY = y;

    this.updateAngle();
  }

  /**
   * Set the target for the projectile
   * @param {Entity} target the entity this is trying to hit
   * @return {Boolean}
   */
  setTarget(target) {
    if (!target) {
      return false;
    }

    this.dynamic = true;

    this.destX = target.x;
    this.destY = target.y;

    this.updateAngle();

    if (target.type !== 'mob') {
      return false;
    }

    target.onMove(() => {
      this.destX = target.x;
      this.destY = target.y;

      this.updateAngle();
    });

    return true;
  }

  /**
   * Gets the speed of the projectile
   * @return {Number} speed of the projectile
   */
  getSpeed() {
    return this.speed;
  }

  /**
   * Update the target location
   * @param  {Number} x x coordinate
   * @param  {Number} y y coordinate
   */
  updateTarget(x, y) {
    this.destX = x;
    this.destY = y;
  }

  /**
   * Return if this has a path
   * @return {Boolean}
   */
  hasPath() {
    return this.path;
  }

  /**
   * Update the angle of the projectile
   */
  updateAngle() {
    this.angle = Math.atan2(this.destY - this.y, this.destX - this.x)
      * (180 / Math.PI) - 90;
  }

  /**
   * Set the impact callback
   * @param  {Function} callback what happens if the projectile hits
   */
  onImpact(callback) {
    this.impactCallback = callback;
  }
}

'use client'

/* global Image, document */
import Animation from './animation';
import log from '../lib/log';

export default class Sprite {
  constructor(sprite, scale) {
    // log.debug('Sprite - constructor()', sprite, scale);

    this.sprite = sprite;
    this.scale = scale;
    this.id = sprite.id;
    this.loaded = false;
    this.offsetX = 0;
    this.offsetY = 0;
    this.offsetAngle = 0;
    this.whiteSprite = { loaded: false };

    this.loadSpriteImgData();
  }

  loadSprite() {
    // log.debug('Sprite - loadSprite()', this.filepath);

    this.image = new Image();
    this.image.crossOrigin = 'Anonymous';
    this.image.src = this.filepath;

    this.image.onload = () => {
      // log.debug('Sprite - loadSprite() - image loaded', this.filepath);
      this.loaded = true;

      if (this.onLoadCallback) {
        // log.debug('Sprite - loadSprite() - image loaded callback', this.onLoadCallback);
        this.onLoadCallback();
      }
    };
  }

  loadSpriteImgData() {
    log.debug('Sprite - loadSpriteImgData()', this.sprite);

    const { sprite } = this;

    this.filepath = `/img/${this.scale}/${this.id}.png`;
    this.animationData = sprite.animations;

    this.width = sprite.width;
    this.height = sprite.height;

    this.offsetX = sprite.offsetX !== undefined ? sprite.offsetX : -16;
    this.offsetY = sprite.offsetY !== undefined ? sprite.offsetY : -16;
    this.offfsetAngle = sprite.offsetAngle !== undefined ? sprite.offsetAngle : 0;
    this.loadSprite();
  }

  update(newScale) {
    log.debug('Sprite - update()');
    this.scale = newScale;

    this.loadSpriteImgData();
  }

  createAnimations() {
    log.debug('Sprite - createAnimations()');

    const animations = {};

    for (const name in this.animationData) { // eslint-disable-line
      if (this.animationData.hasOwnProperty(name)) { // eslint-disable-line
        const a = this.animationData[name];

        animations[name] = new Animation(
          name,
          a.length,
          a.row,
          this.width,
          this.height,
        );
      }
    }

    return animations;
  }

  /**
   * This is when an entity gets hit, they turn red then white.
   */

  createHurtSprite() {
    if (!this.loaded) {
      log.debug('Sprite - createHurtSprite()');
      this.loadSprite();
    }

    if (this.whiteSprite.loaded) {
      return;
    }

    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');

    let spriteData;
    canvas.width = this.width;
    canvas.height = this.height;

    context.drawImage(this.image, 0, 0, this.image.width, this.image.height);

    try {
      spriteData = context.getImageData(
        0,
        0,
        this.image.width,
        this.image.height,
      );

      const {
        data,
      } = spriteData;

      for (let i = 0; i < data.length; i += 4) {
        data[i] = 255;
        data[i + 1] = data[i + 2] = 75; // eslint-disable-line
      }

      spriteData.data = data;

      context.putImageData(spriteData, 0, 0);

      this.whiteSprite = {
        image: canvas,
        loaded: true,
        offsetX: this.offsetX,
        offsetY: this.offsetY,
        width: this.width,
        height: this.height,
      };
    } catch (e) {
      log.debug('Sprite - createHurtSprite() - error', e, this.image, spriteData);
    }
  }

  getHurtSprite() {
    log.debug('Sprite - getHurtSprite()');

    try {
      if (!this.loaded) {
        this.loadSprite();
      }

      this.createHurtSprite();

      return this.whiteSprite;
    } catch (e) {
      log.debug('Sprite - getHurtSprite() - error', e);
      return null;
    }
  }

  onLoad(callback) {
    log.debug('Sprite - onLoad()', callback);
    this.onLoadCallback = callback;
  }
}

/* global window, document */
import _ from 'underscore';
import $ from 'jquery';
import log from './lib/log';
import Renderer from './renderer/renderer';
import LocalStorage from './utils/storage';
import Map from './map/map';
import Socket from './network/socket';
import Player from './entity/character/player/player';
import Updater from './renderer/updater';
import Entities from './controllers/entities';
import Input from './controllers/input';
import PlayerHandler from './entity/character/player/playerhandler';
import Pathfinder from './utils/pathfinder';
import Zoning from './controllers/zoning';
import Info from './controllers/info';
import Bubble from './controllers/bubble';
import Interface from './controllers/interface';
import Audio from './controllers/audio';
import Pointer from './controllers/pointer';
import Modules from './utils/modules';
import Packets from './network/packets';
import Detect from './utils/detect';
import { requestAnimFrame } from './utils/util';

/**
 * Creates an instance of the game
 * @class
 */
export default class Game {
  /**
   * Keep track of the game logic and the HTML5 canvas
   * @param {Client} client an instance of the client application
   */
  constructor(client) {
    log.debug('Game - constructor()', client);

    /**
     * An instance of the client application
     * @type {Client}
     */
    this.client = client;

    /**
     * An ID used to identify the player after they've
     * been given a handshake by the Node server
     * @type {Number}
     */
    this.id = -1;

    /**
     * An instance of the web socket used to talk to the node server
     * @type {Socket}
     */
    this.socket = null;

    /**
     * An array of mesages from the web socket
     * @type {Messages}
     */
    this.messages = null;

    /**
     * An instance of the renderer
     * @type {Renderer}
     */
    this.renderer = null;

    /**
     * An instance of the updater
     * @type {Updater}
     */
    this.updater = null;

    /**
     * A reference to the player's LocalStorage
     * @type {Object}
     */
    this.storage = null;

    /**
     * An array of entities in the game
     * @type {Array<Entity>}
     */
    this.entities = null;

    /**
     * Controls all of the game's input
     * @type {Input}
     */
    this.input = null;

    /**
     * An instance of the game's map
     * @type {Map}
     */
    this.map = null;

    /**
     * An instance of the game's player handler
     * @type {PlayerHandler}
     */
    this.playerHandler = null;

    /**
     * Used to find paths of entities
     * @type {Pathfinder}
     */
    this.pathfinder = null;

    /**
     * Used to figure out direction
     * @type {Zoning}
     */
    this.zoning = null;

    /**
     * Used to display additional information
     * on the screen (ie level up and hits and
     * health/exp regeneration)
     * @type {Info}
     */
    this.info = null;

    /**
     * Controls the interface in the game
     * @type {Interface}
     */
    this.interface = null;

    /**
     * Controls the game music and sfx
     * @type {Audio}
     */
    this.audio = null;

    /**
     * An instance of the player
     * @type {Player}
     */
    this.player = null;

    /**
     * Whether or not the game is started
     * @type {Boolean}
     */
    this.started = false;

    /**
     * Whether or not the game is ready
     * @type {Boolean}
     */
    this.ready = false;

    /**
     * Whether or not the map is loaded
     * @type {Boolean}
     */
    this.loaded = false;

    /**
     * Used for rendering fame ticks
     * @type {Date}
     */
    this.time = new Date();

    /**
     * True if there is currently pvp combat
     * @type {Boolean}
     */
    this.pvp = false;

    /**
     * How many players are in the game, defaults to -1
     * @type {Number}
     */
    this.population = -1;

    /**
     * Last time (used for renderer??)
     * @type {Boolean}
     */
    this.lastTime = new Date().getTime();

    /**
     * Returns server/config.json => devClient value
     * @type {Boolean}
     */
    this.development = null;

    /**
     * The pointer shown on the HTML5 canvas
     * @type {Pointer}
     */
    this.pointer = null;

    /**
     * A reference to the chat bubbles controller
     * @type {Bubble}
     */
    this.bubble = null;

    /**
     * A reference to a guild
     * @type {Guild}
     */
    this.guild = null;

    /**
     * A reference to a shop
     * @type {Shops}
     */
    this.bubble = null;

    this.loadRenderer();
    this.loadControllers();
  }

  /**
   * Start the game
   * @return {Boolean} true if the game was successfully started,
   * false if the game is already running
   */
  start() {
    log.debug('Game - start()', this.started);

    if (this.started) {
      return false;
    }

    this.client.fadeMenu();

    this.started = true;
    this.tick();

    return true;
  }

  /**
   * Stop the game, sets started and ready to false
   */
  stop() {
    log.debug('Game - stop()');

    this.started = false;
    this.ready = false;
  }

  /**
   * Calls the renderer to draw to the HTML5 canvas
   * if the game is ready and requests animations if
   * the game is started
   */
  tick() {
    // log.debug('Game - tick()', this.ready, this.updater, this.started);

    if (this.ready) {
      // log.debug('Game - tick() - ready');
      this.time = new Date().getTime();
      this.renderer.render();
      this.updater.update();

      if (this.started) {
        // log.debug('Game - tick() - started', window);
        requestAnimFrame(this.tick.bind(this));
      }
    }
  }

  /**
   * Resets all of the controllers, storage and
   * entities in the game. Stops all audio too.
   * Sets the game to a totally blank state so that
   * it can be initialized like a new game all over again
   * @return {Boolean}
   */
  unload() {
    log.debug('Game - unload()');

    this.socket = null;
    this.messages = null;
    this.renderer = null;
    this.updater = null;
    this.storage = null;
    this.entities = null;
    this.input = null;
    this.map = null;
    this.playerHandler = null;
    this.pathfinder = null;
    this.zoning = null;
    this.info = null;
    this.interface = null;
    this.audio.stop();
    this.audio = null;

    return true;
  }

  /**
   * Load the HTML5 canvas renderer layers for:
   * * background
   * * foreground
   * * text
   * * entities
   * * cursor
   * @return {Boolean}
   */
  loadRenderer() {
    const background = document.getElementById('background');
    const foreground = document.getElementById('foreground');
    const textCanvas = document.getElementById('textCanvas');
    const entities = document.getElementById('entities');
    const cursor = document.getElementById('cursor');

    log.debug('Game - loadRenderer()', background, entities, foreground, textCanvas, cursor);

    this.client.sendStatus('Soul sucking monster...');

    const renderer = new Renderer(background, entities, foreground, textCanvas, cursor, this);

    this.setRenderer(renderer);

    return true;
  }

  /**
   * Load all of the game controllers
   * @return {Boolean}
   */
  loadControllers() {
    log.debug('Game - loadControllers()');

    const hasWorker = this.client.hasWorker();

    this.client.sendStatus(hasWorker ? 'I tried to tell you...' : null);

    if (hasWorker) {
      this.loadGameMap();
    }

    this.client.sendStatus('Too late now...');
    this.setStorage(new LocalStorage(this.client));
    this.client.sendStatus("You're already doomed...");

    this.setSocket(new Socket(this));
    this.setMessages(this.socket.messages);
    this.setInput(new Input(this));
    this.client.sendStatus("Stop! Before it's too late...");

    const entity = new Entities(this);
    this.setEntityController(entity);

    const info = new Info(this);
    this.setInfo(info);

    const bubble = new Bubble(this);
    this.setBubble(bubble);

    const pointer = new Pointer(this);
    this.setPointer(pointer);

    const audio = new Audio(this);
    this.setAudio(audio);

    const gameInterface = new Interface(this);
    this.setInterface(gameInterface);

    this.implementStorage();

    if (!hasWorker) { // is this for offline mode?
      this.client.sendStatus(null);
      this.loaded = true;
    }

    return true;
  }

  /**
   * Load the game map data
   * @return {Boolean}
   */
  loadGameMap() {
    log.debug('Game - loadGameMap()');

    this.map = new Map(this);
    this.map.onReady(() => {
      log.debug('Game - loadGameMap() - map ready');
      this.mapReadyCallback();
    });
    return true;
  }

  /**
   * Callback for when the map data is done loading
   * @return {Boolean}
   */
  mapReadyCallback() {
    log.debug('Game - mapReadyCallback()');

    this.client.sendStatus('Okay I give up...');
    this.setPathfinder(new Pathfinder(this.map.width, this.map.height));

    this.renderer.setMap(this.map);
    this.renderer.loadCamera();

    this.client.sendStatus("You're beyond help at this point...");
    this.setUpdater(new Updater(this));

    this.entities.loadEntities();
    log.debug('Game - mapReadyCallback() - setting entities', this.entities);
    this.renderer.setEntities(this.entities);

    // this.postLoad();

    // clears the status message out now that we've loaded everything
    this.client.sendStatus(null);
    this.ready = true;
    this.loaded = true;
    this.postLoad();
    return true;
  }

  loadedSpritesCallback() {
    log.debug('loaded sprites callback');
  }

  /**
   * Connect to the nodeJS server and setup all the
   * server to client callback messages
   * @return {Boolean}
   */
  connect() {
    log.debug('Game - connect()');

    this.client.cleanErrors();
    setTimeout(() => this.socket.connect(), 1000);

    /**
     * This is for random miscellaneous commands that require
     * a specific action done by the client as opposed to
     * packet-oriented ones.
     */
    this.messages.onCommand(() => {});
    this.messages.onHandshake(data => this.handshakeCallback(data));
    this.messages.onWelcome(data => this.welcomeCallback(data));
    this.messages.onEquipment((opcode, info) => this.equipmentCallback(opcode, info));
    this.messages.onSpawn(data => this.spawnCallback(data));
    this.messages.onEntityList(data => this.entityListCallback(data));
    this.messages.onSync(data => this.syncCallback(data));
    this.messages.onMovement(data => this.movementCallback(data));
    this.messages.onTeleport(data => this.teleportCallback(data));
    this.messages.onDespawn(id => this.despawnCallback(id));
    this.messages.onCombat(data => this.combatCallback(data));
    this.messages.onAnimation((id, info) => this.animationCallback(id, info));
    this.messages.onProjectile((opcode, info) => this.projectileCallback(opcode, info));
    this.messages.onPopulation(population => this.populationCallback(population));
    this.messages.onPoints(data => this.pointsCallback(data));
    this.messages.onNetwork(this.networkCallback);
    this.messages.onChat(info => this.chatCallback(info));
    this.messages.onInventory((opcode, info) => this.inventoryCallback(opcode, info));
    this.messages.onBank((opcode, info) => this.bankCallback(opcode, info));
    this.messages.onAbility(() => {});
    this.messages.onQuest((opcode, info) => this.questCallback(opcode, info));
    this.messages.onNotification((opcode, message) => this.notificationCallback(opcode, message));
    this.messages.onBlink(instance => this.blinkCallback(instance));
    this.messages.onHeal(info => this.healCallback(info));
    this.messages.onExperience(info => this.experienceCallback(info));
    this.messages.onDeath(id => this.deathCallback(id));
    this.messages.onAudio(song => this.audioCallback(song));
    this.messages.onNPC((opcode, info) => this.npcCallback(opcode, info));
    this.messages.onRespawn((id, x, y) => this.respawnCallback(id, x, y));
    this.messages.onEnchant((opcode, info) => this.enchantCallback(opcode, info));
    this.messages.onGuild((opcode, info) => this.guildCallback(opcode, info));
    this.messages.onPointer((opcode, info) => this.pointerCallback(opcode, info));
    this.messages.onPVP((id, pvp) => this.pvpCallback(id, pvp));
    this.messages.onShop((opcode, info) => this.shopCallback(opcode, info));

    return true;
  }

  /**
   * Callback for when the server has acknowledged the client connection
   * then it creates the player object, makes sure the map is loaded,
   * and if the user is registering or logging in it handles player
   * registration or login requests
   * @param  {Messages} data server network message
   * @return {Boolean}
   */
  handshakeCallback(data) {
    log.debug('Game - handshakeCallback()', data);

    this.id = data.shift();
    this.development = data.shift();

    if (!this.player) {
      this.createPlayer();
    }

    if (!this.map) {
      this.loadGameMap();
    }

    this.client.updateLoader('Logging in...');

    if (this.client.isRegistering()) {
      log.debug('Game - handshakeCallback() - creating an account');

      const registerInfo = this.client.registerFields;
      const username = registerInfo[0].val();
      const password = registerInfo[1].val();
      const email = registerInfo[3].val();

      this.socket.send(Packets.Intro, [
        Packets.IntroOpcode.Register,
        username,
        password,
        email,
      ]);
    } else if (this.client.isGuest()) {
      log.debug('Game - handshakeCallback() - guest logging in');
      this.socket.send(Packets.Intro, [
        Packets.IntroOpcode.Guest,
        'n',
        'n',
        'n',
      ]);
    } else {
      log.debug('Game - handshakeCallback() - player logging in');
      const loginInfo = this.client.loginFields;
      const name = loginInfo[0].val();
      const pass = loginInfo[1].val();

      this.socket.send(Packets.Intro, [
        Packets.IntroOpcode.Login,
        name,
        pass,
        'n',
      ]);

      if (this.hasRemember()) {
        this.storage.data.player.username = name;
        this.storage.data.player.password = pass;
      } else {
        this.storage.data.player.username = '';
        this.storage.data.player.password = '';
      }

      this.storage.save();
    }

    return true;
  }

  /**
   * The callback for when the welcome message for brand new
   * players is done. Loads the player data and sets their
   * starting position then calls this.postLoad()
   * @param  {Object} playerData the data needed to load the player
   * @return {Boolean}
   */
  welcomeCallback(playerData) {
    log.debug('Game - welcomeCallback()', playerData);

    this.player.loadPlayer(playerData);
    this.input.setPosition(this.player.getX(), this.player.getY());

    // this.start();
    // this.postLoad();

    return true;
  }

  /**
   * Callback for when player equipment is done loading
   * @param  {Number} action the equipment action
   * @param  {Object} info information about the equipment
   * @return {Boolean}
   */
  equipmentCallback(action, info) {
    log.debug('Game - equipmentCallback()', action, info);

    switch (action) {
      case Packets.EquipmentOpcode.Batch:
        for (let i = 0; i < info.length; i += 1) {
          this.player.setEquipment(i, info[i]);
        }
        this.interface.loadProfile();
        break;
      case Packets.EquipmentOpcode.Equip:
        const equipmentType = info.shift(); // eslint-disable-line
        const name = info.shift(); // eslint-disable-line
        const string = info.shift(); // eslint-disable-line
        const count = info.shift(); // eslint-disable-line
        const ability = info.shift(); // eslint-disable-line
        const abilityLevel = info.shift(); // eslint-disable-line

        this.player.setEquipment(equipmentType, [
          name,
          string,
          count,
          ability,
          abilityLevel,
        ]);

        this.interface.profile.update();
        break;
      case Packets.EquipmentOpcode.Unequip:
        const type = info.shift(); // eslint-disable-line
        this.player.unequip(type);
        if (type === 'armour') {
          this.player.setSprite(this.getSprite(this.player.getSpriteName()));
        }
        this.interface.profile.update();
        break;
      default:
        break;
    }
  }

  /**
   * Triggered when an entity has finished being spawned
   * @param  {Object} data entity id
   * @return {Boolean}
   */
  spawnCallback(data) {
    log.debug('Game - spawnCallback()', data);
    return this.entities.create(data.shift());
  }

  /**
   * Callback for when the list of entities is done loading
   * and adds the new entities to the screen
   * @param  {Array<Entity>} data array of entities
   * @return {Boolean}
   */
  entityListCallback(data) {
    log.debug('Game - entityListCallback()', data);
    const ids = _.pluck(this.entities.getAll(), 'id');
    const known = _.intersection(ids, data);
    const newIds = _.difference(data, known);

    this.entities.decrepit = _.reject(
      this.entities.getAll(),
      entity => _.include(known, entity.id) || entity.id === this.player.id,
    );

    // this.entities.clean();
    this.entities.loadEntities();
    this.socket.send(Packets.Who, newIds);
    return true;
  }

  /**
   * Callback for when server side sync is done.
   * Used to make sure the player's information
   * doesn't differntiate from what the server has
   * for them
   * @param  {Entity<Player>} playerEntity player entity object
   * @return {Boolean} false if this entity is not a player type
   */
  syncCallback(playerEntity) {
    log.debug('Game - syncCallback()', playerEntity);

    const entity = this.entities.get(playerEntity.id);

    if (!entity || entity.type !== 'player') {
      return false;
    }

    if (playerEntity.hitPoints) {
      entity.hitPoints = playerEntity.hitPoints;
      entity.maxHitPoints = playerEntity.maxHitPoints;
    }

    if (playerEntity.mana) {
      entity.mana = playerEntity.mana;
      entity.maxMana = playerEntity.maxMana;
    }

    if (playerEntity.experience) {
      entity.experience = playerEntity.experience;
      entity.level = playerEntity.level;
    }

    if (playerEntity.armour) {
      entity.setSprite(this.getSprite(playerEntity.armour));
    }

    if (playerEntity.weapon) {
      entity.setEquipment(Modules.Equipment.Weapon, playerEntity.weapon);
    }

    this.interface.profile.update();
    return true;
  }

  /**
   * Callback for entity movement
   * @param  {Object<Messages>} data message data
   * @return {Boolean}
   */
  movementCallback(data) {
    log.debug('Game - movementCallback()', data);

    const opcode = data.shift();
    const info = data.shift();

    switch (opcode) {
      default:
        break;
      case Packets.MovementOpcode.Move:
        const id = info.shift(); // eslint-disable-line
        const x = info.shift(); // eslint-disable-line
        const y = info.shift(); // eslint-disable-line
        const forced = info.shift(); // eslint-disable-line
        const teleport = info.shift(); // eslint-disable-line
        const entity = this.entities.get(id); // eslint-disable-line

        if (!entity) {
          return false;
        }

        if (forced) {
          entity.stop(true);
        }

        this.moveCharacter(entity, x, y);
        break;
      case Packets.MovementOpcode.Follow:
        const follower = this.entities.get(info.shift()); // eslint-disable-line
        const followee = this.entities.get(info.shift()); // eslint-disable-line

        if (!followee || !follower) {
          return false;
        }

        follower.follow(followee);
        break;
      case Packets.MovementOpcode.Freeze:
      case Packets.MovementOpcode.Stunned:
        const pEntity = this.entities.get(info.shift()); // eslint-disable-line
        const state = info.shift(); // eslint-disable-line

        if (!pEntity) {
          return false;
        }

        if (state) {
          pEntity.stop(false);
        }

        if (opcode === Packets.MovementOpcode.Stunned) {
          pEntity.stunned = state;
        } else if (opcode === Packets.MovementOpcode.Freeze) {
          pEntity.frozen = state;
        }
        break;
    }

    return true;
  }

  /**
   * Callback for player teleport
   * @param  {Object<Messages>} data information about the teleport
   * @return {Boolean}
   */
  teleportCallback(data) {
    log.debug('Game - teleportCallback()', data);

    const id = data.shift();
    const x = data.shift();
    const y = data.shift();
    const withAnimation = data.shift();
    const isPlayer = id === this.player.id;
    const entity = this.entities.get(id);

    if (!entity) {
      return false;
    }

    entity.stop(true);
    entity.frozen = true;

    /**
     * Teleporting an entity seems to cause a glitch with the
     * hitbox. Make sure you keep an eye out for this.
     * @return {Boolean}
     */
    const doTeleport = () => {
      log.debug('Game - doTeleport()');

      this.entities.unregisterPosition(entity);
      entity.setGridPosition(x, y);

      if (isPlayer) {
        this.entities.clearPlayers(this.player);
        this.player.clearHealthBar();
        this.renderer.camera.centreOn(entity);
        this.renderer.updateAnimatedTiles();
      } else if (entity.type === 'player') {
        delete this.entities.entities[entity.id];
        return false;
      }

      this.socket.send(Packets.Request, [this.player.id]);
      this.entities.registerPosition(entity);

      entity.frozen = false;
      return true;
    };

    if (withAnimation) {
      const originalSprite = entity.sprite;
      entity.teleporting = true;
      entity.setSprite(this.getSprite('death'));

      entity.animate('death', 240, 1, () => {
        doTeleport();
        entity.currentAnimation = null;
        entity.setSprite(originalSprite);
        entity.idle();
        entity.teleporting = false;
      });
    } else {
      doTeleport();
    }

    return true;
  }

  /**
   * Callback for removing a spawned entity
   * @param  {Number} id the id of the entity that needs to be removed
   * @return {Boolean}
   */
  despawnCallback(id) {
    log.debug('Game - despawnCallback()', id);

    const entity = this.entities.get(id);

    if (!entity) {
      return false;
    }

    entity.dead = true;
    entity.stop();

    switch (entity.type) {
      default:
        break;
      case 'item':
        this.entities.removeItem(entity);
        return false;
      case 'chest':
        entity.setSprite(this.getSprite('death'));

        entity.setAnimation('death', 120, 1, () => {
          this.entities.unregisterPosition(entity);
          delete this.entities.entities[entity.id];
        });
        return false;
    }

    if (this.player.hasTarget() && this.player.target.id === entity.id) {
      this.player.removeTarget();
    }

    this.entities.grids.removeFromPathingGrid(entity.gridX, entity.gridY);

    if (entity.id !== this.player.id && this.player.getDistance(entity) < 5) {
      this.audio.play(
        Modules.AudioTypes.SFX,
        `kill${Math.floor(Math.random() * 2 + 1)}`,
      );
    }

    entity.hitPoints = 0;
    entity.setSprite(this.getSprite('death'));

    entity.animate('death', 120, 1, () => {
      this.entities.unregisterPosition(entity);
      delete this.entities.entities[entity.id];
    });

    return true;
  }

  combatCallback(data) {
    log.debug('Game - combatCallback()', data);

    const opcode = data.shift();
    const attacker = this.entities.get(data.shift());
    const target = this.entities.get(data.shift());

    if (!target || !attacker) {
      return;
    }

    switch (opcode) {
      default:
        break;
      case Packets.CombatOpcode.Initiate:
        attacker.setTarget(target);
        target.addAttacker(attacker);

        if (target.id === this.player.id || attacker.id === this.player.id) {
          this.socket.send(Packets.Combat, [
            Packets.CombatOpcode.Initiate,
            attacker.id,
            target.id,
          ]);
        }
        break;
      case Packets.CombatOpcode.Hit:
        const hit = data.shift(); // eslint-disable-line
        const isPlayer = target.id === this.player.id; // eslint-disable-line

        if (!hit.isAoE) {
          attacker.lookAt(target);
          attacker.performAction(
            attacker.orientation,
            Modules.Actions.Attack,
          );
        } else if (hit.hasTerror) {
          target.terror = true;
        }

        switch (hit.type) {
          case Modules.Hits.Critical:
            target.critical = true;
            break;
          default:
            if (attacker.id === this.player.id && hit.damage > 0) {
              this.audio.play(
                Modules.AudioTypes.SFX,
                `hit${Math.floor(Math.random() * 2 + 1)}`,
              );
            }
            break;
        }

        this.info.create(
          hit.type,
          [hit.damage, isPlayer],
          target.x,
          target.y,
        );

        attacker.triggerHealthBar();
        target.triggerHealthBar();

        if (isPlayer && hit.damage > 0) {
          this.audio.play(Modules.AudioTypes.SFX, 'hurt');
        }
        break;
      case Packets.CombatOpcode.Finish:
        if (target) {
          target.removeTarget();
          target.forget();
        }

        if (attacker) {
          attacker.removeTarget();
        }
        break;
    }
  }

  animationCallback(id, info) {
    log.debug('Game - animationCallback()', id, info);

    const entity = this.entities.get(id);
    const animation = info.shift();
    const speed = info.shift();
    const count = info.shift();

    if (!entity) {
      return;
    }

    entity.animate(animation, speed, count);
  }

  projectileCallback(opcode, info) {
    log.debug('Game - projectileCallback()', opcode, info);

    switch (opcode) {
      default:
        break;
      case Packets.ProjectileOpcode.Create:
        this.entities.create(info);
        break;
    }
  }

  populationCallback(population) {
    log.debug('Game - populationCallback()', population);

    this.population = population;
  }

  pointsCallback(data) {
    log.debug('Game - pointsCallback()', data);

    const id = data.shift();
    const hitPoints = data.shift();
    const mana = data.shift();
    const entity = this.entities.get(id);

    if (!entity) {
      return;
    }

    if (hitPoints) {
      entity.setHitPoints(hitPoints);

      if (
        this.player.hasTarget()
        && this.player.target.id === entity.id
        && this.input.overlay.updateCallback
      ) this.input.overlay.updateCallback(entity.id, hitPoints);
    }

    if (mana) {
      entity.setMana(mana);
    }
  }

  networkCallback() {
    log.debug('Game - networkCallback()');

    this.socket.send(Packets.Network, [Packets.NetworkOpcode.Pong]);
  }

  chatCallback(info) {
    log.debug('Game - chatCallback()', info);

    if (!info.duration) {
      info.duration = 5000; // eslint-disable-line
    }

    if (info.withBubble) {
      const entity = this.entities.get(info.id);

      if (entity) {
        this.bubble.create(info.id, info.text, this.time, info.duration);
        this.bubble.setTo(entity);

        this.audio.play(Modules.AudioTypes.SFX, 'npctalk');
      }
    }

    if (info.isGlobal) {
      info.name = `[Global] ${info.name}`; // eslint-disable-line
    }

    this.input.chatHandler.add(info.name, info.text, info.colour);
  }

  inventoryCallback(opcode, info) {
    log.debug('Game - inventoryCallback()', opcode, info);

    switch (opcode) {
      default:
        break;
      case Packets.InventoryOpcode.Batch:
        const inventorySize = info.shift(); // eslint-disable-line
        const data = info.shift(); // eslint-disable-line

        this.interface.loadInventory(inventorySize, data);
        break;
      case Packets.InventoryOpcode.Add:
        if (!this.interface.inventory) {
          return;
        }

        this.interface.inventory.add(info);

        if (!this.interface.bank) {
          return;
        }

        this.interface.bank.addInventory(info);
        break;
      case Packets.InventoryOpcode.Remove:
        if (!this.interface.inventory) {
          return;
        }

        this.interface.inventory.remove(info);

        if (!this.interface.bank) {
          return;
        }

        this.interface.bank.removeInventory(info);
        break;
    }
  }

  bankCallback(opcode, info) {
    log.debug('Game - bankCallback()', opcode, info);

    switch (opcode) {
      default:
        break;
      case Packets.BankOpcode.Batch:
        const bankSize = info.shift(); // eslint-disable-line
        const data = info.shift(); // eslint-disable-line

        this.interface.loadBank(bankSize, data);
        break;

      case Packets.BankOpcode.Add:
        if (!this.interface.bank) {
          return;
        }

        this.interface.bank.add(info);
        break;

      case Packets.BankOpcode.Remove:
        this.interface.bank.remove(info);
        break;
    }
  }

  questCallback(opcode, info) {
    log.debug('Game - questCallback()', opcode, info, this.interface.getQuestPage());

    switch (opcode) {
      default:
        log.debug('Game - questCallback() - invalid opcode', opcode);
        break;
      case Packets.QuestOpcode.Batch:
        log.debug('Game - questCallback() - batch', info.quests, info.achievements);
        this.interface.getQuestPage().loadQuest(info.quests, info.achievements);
        break;

      case Packets.QuestOpcode.Progress:
        log.debug('Game - questCallback() - progress', info);
        this.interface.getQuestPage().progress(info);
        break;

      case Packets.QuestOpcode.Finish:
        log.debug('Game - questCallback() - finish', info);
        this.interface.getQuestPage().finish(info);
        break;
    }
  }

  notificationCallback(opcode, message) {
    log.debug('Game - notificationCallback()', opcode, message);

    switch (opcode) {
      default:
        break;
      case Packets.NotificationOpcode.Ok:
        this.interface.displayNotify(message);
        break;

      case Packets.NotificationOpcode.YesNo:
        this.interface.displayConfirm(message);
        break;

      case Packets.NotificationOpcode.Text:
        this.input.chatHandler.add('WORLD', message, 'red');
        break;
    }
  }

  blinkCallback(instance) {
    log.debug('Game - blickCallback()', instance);

    const item = this.entities.get(instance);

    if (!item) {
      return;
    }

    item.blink(150);
  }

  healCallback(info) {
    log.debug('Game - healCallback()', info);

    const entity = this.entities.get(info.id);

    if (!entity) {
      return;
    }

    switch (info.type) {
      default:
        break;
      case 'health':
        this.info.create(
          Modules.Hits.Heal,
          [info.amount],
          entity.x,
          entity.y,
        );

        break;

      case 'mana':
        this.info.create(
          Modules.Hits.Mana,
          [info.amount],
          entity.x,
          entity.y,
        );

        break;
    }

    if (entity.hitPoints + info.amount > entity.maxHitPoints) {
      entity.setHitPoints(entity.maxHitPoints);
    } else {
      entity.setHitPoints(entity.hitPoints + info.amount);
    }

    entity.triggerHealthBar();
  }

  experienceCallback(info) {
    log.debug('Game - experienceCallback()', info);

    const entity = this.entities.get(info.id);

    if (!entity || entity.type !== 'player') {
      return;
    }

    entity.experience = info.experience;

    if (entity.level !== info.level) {
      entity.level = info.level;
      this.info.create(Modules.Hits.LevelUp, null, entity.x, entity.y);
    } else if (entity.id === this.player.id) {
      this.info.create(Modules.Hits.Experience, [info.amount], entity.x, entity.y);
    }

    this.interface.profile.update();
  }

  deathCallback(id) {
    log.debug('Game - deathCallback()', id);

    const entity = this.entities.get(id);

    if (!entity || id !== this.player.id) {
      return;
    }

    this.audio.play(Modules.AudioTypes.SFX, 'death');
    this.player.dead = true;
    this.player.removeTarget();
    this.player.orientation = Modules.Orientation.Down;
    this.client.body.addClass('death');
  }

  audioCallback(song) {
    log.debug('Game - audioCallback()', song);

    this.audio.songName = song;

    if (Detect.isSafari() && !this.audio.song) {
      return;
    }

    this.audio.update();
  }

  npcCallback(opcode, info) {
    log.debug('Game - npcCallback()', opcode, info);

    switch (opcode) {
      default:
        break;
      case Packets.NPCOpcode.Talk:
        console.log('canvas NPC talk!!!', info);
        let entity = this.entities.get(info.id); // eslint-disable-line
        const messages = info.text; // eslint-disable-line
        const isNPC = !info.nonNPC; // eslint-disable-line

        if (!entity) {
          console.log('canvas no entity by this ID');
          return;
        }

        if (!messages) {
          console.log('canvas no messages');
          entity.talkIndex = 0;
          return;
        }

        const message = isNPC
          ? entity.talk(messages)
          : messages; // eslint-disable-line

        console.log('canvas messages are', message);

        if (isNPC) {
          console.log('canvas this is an NPC');
          const bubble = this.bubble.create(info.id, message, this.time, 5000);
          console.log('canvas bubble', bubble);

          this.bubble.setTo(entity);

          if (this.renderer.mobile && this.renderer.autoCentre) {
            this.renderer.camera.centreOn(this.player);
          }

          if (bubble) {
            console.log('canvas clickable bubble');
            bubble.setClickable();

            bubble.element.on('click', () => {
              const bubbleInstance = this.entities.get(bubble.id);

              if (bubbleInstance) {
                console.log('canvas bubble instance');
                this.input.click({
                  x: bubbleInstance.gridX,
                  y: bubbleInstance.gridY,
                });
              }
            });
          }
        } else {
          console.log('canvas this is not an npc');
          this.bubble.create(info.id, message, this.time, 5000);
          this.bubble.setTo(entity);
        }

        let sound = 'npc'; // eslint-disable-line

        if (!message && isNPC) {
          sound = 'npc-end';
          this.bubble.destroy(info.id);
        }

        this.audio.play(Modules.AudioTypes.SFX, sound);

        break;

      case Packets.NPCOpcode.Bank:
        this.interface.bank.display();
        break;

      case Packets.NPCOpcode.Enchant:
        this.interface.enchant.display();
        break;

      case Packets.NPCOpcode.Countdown:
        const cEntity = this.entities.get(info.id); // eslint-disable-line
        const countdown = info.countdown; // eslint-disable-line

        if (cEntity) {
          cEntity.setCountdown(countdown);
        }

        break;
    }
  }

  respawnCallback(id, x, y) {
    log.debug('Game - respawnCallback()', id, x, y);

    if (id !== this.player.id) {
      log.error('Player id mismatch.');
      return;
    }

    this.player.setGridPosition(x, y);
    this.entities.addEntity(this.player);
    this.renderer.camera.centreOn(this.player);
    this.player.currentAnimation = null;
    this.player.setSprite(this.getSprite(this.player.getSpriteName()));
    this.player.idle();
    this.player.dead = false;
  }

  enchantCallback(opcode, info) {
    log.debug('Game - enchantCallback()', opcode, info);

    const {
      type,
      index,
    } = info;

    switch (opcode) {
      default:
        break;
      case Packets.EnchantOpcode.Select:
        this.interface.enchant.add(type, index);
        break;
      case Packets.EnchantOpcode.Remove:
        this.interface.enchant.moveBack(type, index);
        break;
    }
  }

  guildCallback(opcode, info) {
    log.debug('Game - guildCallback()', opcode, info);

    switch (opcode) {
      default:
        break;
      case Packets.GuildOpcode.Create:
        break;
      case Packets.GuildOpcode.Join:
        break;
    }

    this.guild = info;
  }

  pointerCallback(opcode, info) {
    log.debug('Game - pointerCallback()', opcode, info);

    switch (opcode) {
      default:
        break;
      case Packets.PointerOpcode.NPC:
        const entity = this.entities.get(info.id); // eslint-disable-line

        if (!entity) {
          return;
        }

        this.pointer.create(entity.id, Modules.Pointers.Entity);
        this.pointer.setToEntity(entity);
        break;

      case Packets.PointerOpcode.Location:
        this.pointer.create(info.id, Modules.Pointers.Position);
        this.pointer.setToPosition(info.id, info.x * 16, info.y * 16);
        break;

      case Packets.PointerOpcode.Relative:
        this.pointer.create(info.id, Modules.Pointers.Relative);
        this.pointer.setRelative(info.id, info.x, info.y);
        break;

      case Packets.PointerOpcode.Remove:
        this.pointer.clean();
        break;
    }
  }

  pvpCallback(id, pvp) {
    log.debug('Game - pvpCallback()', id, pvp);

    if (this.player.id === id) {
      this.pvp = pvp;
    } else {
      const entity = this.entities.get(id);

      if (entity) {
        entity.pvp = pvp;
      }
    }
  }

  shopCallback(opcode, info) {
    log.debug('Game - spawnCallback()', opcode, info);

    switch (opcode) {
      case Packets.ShopOpcode.Open:
        break;
      case Packets.ShopOpcode.Buy:
        break;
      case Packets.ShopOpcode.Sell:
        break;
      case Packets.ShopOpcode.Refresh:
        break;
      default:
        break;
    }

    this.shop = info;
  }

  /**
   * Call this after the player has been welcomed
   * by the server and the client received the connection.
   */
  postLoad() {
    log.debug('Game - postLoad()');

    // @TODO fix this line
    this.renderer.loadStaticSprites();

    this.getCamera().setPlayer(this.player);

    this.renderer.renderedFrame[0] = -1;

    this.entities.addEntity(this.player);

    const defaultSprite = this.getSprite(this.player.getSpriteName());

    this.player.setSprite(defaultSprite);
    this.player.idle();

    this.socket.send(Packets.Ready, [true]);

    this.playerHandler = new PlayerHandler(this, this.player);

    this.renderer.updateAnimatedTiles();

    this.zoning = new Zoning(this);

    this.updater.setSprites(this.entities.sprites);

    this.renderer.verifyCentration();

    if (this.storage.data.new) {
      this.storage.data.new = false;
      this.storage.save();
    }

    if (this.storage.data.welcome !== false) {
      this.client.body.addClass('welcomeMessage');
    }
  }

  implementStorage() {
    log.debug('Game - implementStorage()');

    const loginName = $('#wrapperNameInput');
    const loginPassword = $('#wrapperPasswordInput');

    loginName.prop('readonly', false);
    loginPassword.prop('readonly', false);

    if (!this.hasRemember()) {
      return;
    }

    if (this.getStorageUsername() !== '') {
      loginName.val(this.getStorageUsername());
    }

    if (this.getStoragePassword() !== '') {
      loginPassword.val(this.getStoragePassword());
    }

    $('#rememberMe').addClass('active');
  }

  setPlayerMovement(direction) {
    log.debug('Game - setPlayerMovement()', direction);

    this.player.direction = direction;
  }

  movePlayer(x, y) {
    log.debug('Game - movePlayer()', x, y);

    this.moveCharacter(this.player, x, y);
  }

  moveCharacter(character, x, y) {
    log.debug('Game - moveCharacter()', character, x, y);

    if (!character) {
      return;
    }

    character.go(x, y);
  }

  findPath(character, x, y, ignores) {
    log.debug('Game - findPath()', x, y, ignores);

    const grid = this.entities.grids.pathingGrid;
    let path = [];

    if (this.map.isColliding(x, y) || !this.pathfinder || !character) {
      return path;
    }

    if (ignores) {
      _.each(ignores, (entity) => {
        this.pathfinder.ignoreEntity(entity);
      });
    }

    path = this.pathfinder.find(grid, character, x, y, false);

    if (ignores) {
      this.pathfinder.clearIgnores();
    }

    return path;
  }

  onInput(inputType, data) {
    log.debug('Game - onInput()', inputType, data);

    this.input.handle(inputType, data);
  }

  /**
   * This function is responsible for handling sudden
   * disconnects of a player whilst in the game, not
   * menu-based errors.
   */
  handleDisconnection(noError) {
    log.debug('Game - handleDisconnection()', noError);

    if (!this.started) {
      return;
    }

    this.stop();
    this.renderer.stop();

    this.unload();

    this.client.showMenu();

    if (noError) {
      this.client.sendError(null, 'You have been disconnected from the server');
      this.client.statusMessage = null;
    }

    this.loadRenderer();
    this.loadControllers();

    this.client.toggleLogin(false);
    this.client.updateLoader('');
  }

  respawn() {
    log.debug('Game - respawn()');

    this.audio.play(Modules.AudioTypes.SFX, 'revive');
    this.client.body.removeClass('death');

    this.socket.send(Packets.Respawn, [this.player.id]);
  }

  tradeWith(player) {
    log.debug('Game - tradeWith()', player);

    if (!player || player.id === this.player.id) {
      return;
    }

    this.socket.send(Packets.Trade, [Packets.TradeOpcode.Request, player.id]);
  }

  resize() {
    log.debug('Game - resize()');

    this.renderer.resize();

    if (this.pointer) {
      this.pointer.resize();
    }
  }

  createPlayer() {
    log.debug('Game - createPlayer()');

    this.player = new Player();
  }

  getScaleFactor() {
    return this.client.getScaleFactor();
  }

  getStorage() {
    log.debug('Game - getStorage()');

    return this.storage;
  }

  getCamera() {
    log.debug('Game - getCamera()');

    return this.renderer.camera;
  }

  getSprite(spriteName) {
    log.debug('Game - getSprite()', spriteName);

    return this.entities.getSprite(spriteName);
  }

  getEntityAt(x, y, ignoreSelf) {
    log.debug('Game - getEntityAt()', x, y, ignoreSelf);

    console.log('getEntityAt', this.entities.grids.renderingGrid);
    const entities = this.entities.grids.renderingGrid[y][x];

    if (_.size(entities) > 0) {
      return entities[_.keys(entities)[ignoreSelf ? 1 : 0]];
    }

    const items = this.entities.grids.itemGrid[y][x];

    if (_.size(items) > 0) {
      _.each(items, (item) => {
        if (item.stackable) {
          return item;
        }
        return null;
      });

      return items[_.keys(items)[0]];
    }

    return null;
  }

  getStorageUsername() {
    log.debug('Game - getStorageUsername()', this.storage);

    return this.storage.data.player.username;
  }

  getStoragePassword() {
    log.debug('Game - getStoragePassword()', this.storage);

    return this.storage.data.player.password;
  }

  hasRemember() {
    log.debug('Game - hasRemember()');

    return this.storage.data.player.rememberMe;
  }

  setRenderer(renderer) {
    log.debug('Game - setRenderer()', renderer);

    if (this.renderer === null) {
      this.renderer = renderer;
    }
  }

  setStorage(storage) {
    log.debug('Game - setStorage()', storage);

    if (!this.storage) {
      this.storage = storage;
    }
  }

  setSocket(socket) {
    log.debug('Game - setSocket()', socket);

    if (!this.socket) {
      this.socket = socket;
    }
  }

  setMessages(messages) {
    log.debug('Game - setMessages()', messages);

    if (!this.messages) {
      this.messages = messages;
    }
  }

  setUpdater(updater) {
    log.debug('Game - setUpdater()', updater);

    if (!this.updater) {
      this.updater = updater;
    }
  }

  setEntityController(entities) {
    log.debug('Game - setEntityController()', entities, this.entities);

    if (!this.entities) {
      this.entities = entities;
    }
  }

  setInput(input) {
    log.debug('Game - setInput()', input);

    if (!this.input) {
      this.input = input;
      this.renderer.setInput(this.input);
    }
  }

  setPathfinder(pathfinder) {
    log.debug('Game - setPathfinder()', pathfinder);

    if (!this.pathfinder) {
      this.pathfinder = pathfinder;
    }
  }

  setInfo(info) {
    log.debug('Game - setInfo()', info);

    if (!this.info) {
      this.info = info;
    }
  }

  setBubble(bubble) {
    log.debug('Game - setBubble()', bubble);

    if (!this.bubble) {
      this.bubble = bubble;
    }
  }

  setPointer(pointer) {
    log.debug('Game - setPointer()', pointer);

    if (!this.pointer) {
      this.pointer = pointer;
    }
  }

  setInterface(intrface) {
    log.debug('Game - setInterface()', intrface);

    if (!this.interface) {
      this.interface = intrface;
    }
  }

  setAudio(audio) {
    log.debug('Game - setAudio()', audio);

    if (!this.audio) {
      this.audio = audio;
    }
  }
}

/* global document, window */
import $ from 'jquery';
import _ from 'underscore';
import Module from './utils/modules';
import log from './lib/log';
import Detect from './utils/detect';
import * as config from './config.json';

/**
 * Controls the client side changes for the application and game
 * @class
 */
export default class Client {
  /**
   * Load all the different windows/UI portions on the index page
   * relevant for use in the game, set the zoom factor, update the
   * orientation and then load the login screen
   */
  constructor() {
    log.debug('Client - constructor()', this);

    /**
     * Configuration for the client application
     * loaded from ./data/config.json
     * @type {Object}
     */
    this.config = config;

    /**
     * An instance of the DOM $('body') element
     * @type {Object}
     */
    this.body = $('body');

    /**
     * An instance of the DOM $('#content') element
     * @type {Object}
     */
    this.wrapper = $('#content');

    /**
     * An instance of the DOM $('#container') element
     * @type {Object}
     */
    this.container = $('#container');

    /**
     * An instance of the DOM $(window) element
     * @type {Object}
     */
    this.window = typeof window !== "undefined" ? $(window) : null;

    /**
     * An instance of the DOM $('#canvasLayers') element
     * @type {Object}
     */
    this.canvas = $('#canvasLayers');

    /**
     * An instance of the DOM $('#border') element
     * @type {Object}
     */
    this.border = $('#border');

    /**
     * An instance of the DOM $('#modal') element
     * @type {Object}
     */
    this.intro = $('#modal');

    /**
     * An instance of the DOM $('#loginButton') element
     * @type {Object}
     */
    this.loginButton = $('#loginButton');

    /**
     * An instance of the DOM $('#play') element
     * @type {Object}
     */
    this.createButton = $('#play');

    /**
     * An instance of the DOM $('#newCharacter') element
     * @type {Object}
     */
    this.registerButton = $('#newCharacter');

    /**
     * An instance of the DOM $('#helpButton') element
     * @type {Object}
     */
    this.helpButton = $('#helpButton');

    /**
     * An instance of the DOM $('#cancelButton') element
     * @type {Object}
     */
    this.cancelButton = $('#cancelButton');

    /**
     * An instance of the DOM $('#yes') element
     * @type {Object}
     */
    this.yes = $('#yes');

    /**
     * An instance of the DOM $('#no') element
     * @type {Object}
     */
    this.no = $('#no');

    /**
     * An instance of the DOM $('.loader') element
     * @type {Object}
     */
    this.loading = $('.loader');

    /**
     * An instance of the DOM $('.loader.message') element
     * @type {Object}
     */
    this.loadingMsg = $('.loader.message');

    /**
     * An instance of the DOM $('.error.message') element
     * @type {Object}
     */
    this.errorMsg = $('.error.message');

    /**
     * An instance of the DOM $('#respawn') element
     * @type {Object}
     */
    this.respawn = $('#respawn');

    /**
     * An instance of the DOM $('#rememberMe') element
     * @type {Object}
     */
    this.rememberMe = $('#rememberMe');

    /**
     * An instance of the DOM $('#guest') element
     * @type {Object}
     */
    this.guest = $('#guest');

    /**
     * An instance of the DOM $('#toggle-about') element
     * @type {Object}
     */
    this.about = $('#toggle-about');

    /**
     * An instance of the DOM $('#toggle-credits') element
     * @type {Object}
     */
    this.credits = $('#toggle-credits');

    /**
     * An instance of the DOM $('#toggle-git') element
     * @type {Object}
     */
    this.git = $('#toggle-git');

    /**
     * An instance of the DOM $('#loginNameInput') and $('#loginPasswordInput') element
     * @type {Object[]}
     */
    this.loginFields = [$('#loginNameInput'), $('#loginPasswordInput')];

    /**
     * Register fields
     * @type {Array}
     */
    this.registerFields = [];

    /**
     * An instance of Game class
     * @type {Game}
     */
    this.game = null;

    /**
     * Used to determine if the player is logging in as a guest or not
     * @type {Boolean}
     */
    this.guestLogin = false;

    /**
     * Zoom percentage from 0 to 1
     * @type {Number}
     */
    this.zoomFactor = 1;

    /**
     * Used to figure out whether or not to show the login screen
     * @type {Boolean}
     */
    this.loggingIn = false;

    /**
     * The width of the player's browser window
     * @type {Number}
     */
    this.width = this.window.innerWidth;

    /**
     * The height of the player's browser window
     * @type {Number}
     */
    this.height = this.window.innerHeight;

    /**
     * The orientation of the player's device
     * @type {String}
     */
    this.orientation = 'landscape';

    /**
     * Callback function when this class is done loading
     * @type {Function}
     */
    this.readyCallback = null;

    /**
     * Used to display loading status messages
     * @type {String}
     */
    this.statusMessage = null;

    /**
     * The field whose range is being updated
     * @type {Object}
     */
    this.rangeField = null;
  }

  /**
   * Add click listeners for the different parts of the UI
   */
  loadClient() {
    log.debug('Client - loadClient()', this);

    // auto-binding
    this.keydownEventListener = this.keydownEventListener.bind(this);
    this.keyupEventListener = this.keyupEventListener.bind(this);
    this.mousemoveEventListener = this.mousemoveEventListener.bind(this);
    this.mousemoveEventListener = this.mousemoveEventListener.bind(this);
    this.canvasClickEventListener = this.canvasClickEventListener.bind(this);

    this.sendStatus('You should turn back now...');
    this.zoom();
    this.updateOrientation();

    this.loginButton.on("click", () => this.login());
    this.createButton.on("click", () => this.login());
    this.yes.on("click", () => this.welcomeContinue());
    this.no.on("click", () => this.welcomeContinue());
    this.rememberMe.on("click", () => this.rememberLogin());
    this.guest.on("click", () => this.loginAsGuest());
    this.registerButton.on("click", () => this.displayScreen('loadCharacter', 'createCharacter'));
    this.cancelButton.on("click", () => this.displayScreen('createCharacter', 'loadCharacter'));
    this.about.on("click", () => this.displayScroll('about'));
    this.credits.on("click", () => this.displayScroll('credits'));
    this.git.on("click", () => this.displayScroll('git'));
    this.respawn.on("click", () => this.respawnPlayer());

    this.window.scrollTop(); // why do we do this?
    this.window.resize(this.zoom());

    log.debug('Client - loading client config');
    this.config = config;

    // load the character
    this.loadCharacter();

    $(document).on('keydown', this.keydownEventListener);
    $(document).on('keyup', this.keyupEventListener);
    $(document).on('mousemove', this.mousemoveEventListener);
    this.canvas.on('click', this.canvasClickEventListener);

    // this.canvas.click(this.canvasClickEventListener);

    $('input[type="range"]').on('input', () => {
      this.updateRange(this);
    });

    if (this.readyCallback) {
      log.debug('Client - loading client config done - readyCallback()');
      this.readyCallback();
    } else {
      setTimeout(this.load, 3000);
    }
  }

  /**
   * Dismissing the welcome screen when the application has
   * finished loading
   */
  welcomeContinue() {
    log.debug('Client - welcomeContinue()');

    if (!this.game) {
      return false;
    }

    // makes sure the welcome screen doesn't appear again
    this.game.storage.data.welcome = false;
    this.game.storage.save();
    this.body.removeClass('welcomeMessage');
    return true;
  }

  /**
   * Try to connect and login to the game
   * @private
   * @return {Boolean}
   */
  login() {
    log.debug('Client - login()');

    if (
      this.loggingIn
      || !this.game
      || !this.game.loaded
      || this.statusMessage
      || !this.verifyForm()
    ) return false;

    // this.toggleLogin(true);
    this.game.connect();
    return true;
  }

  /**
   * Try to login as a guest
   * @private
   * @return {Boolean}
   */
  loginAsGuest() {
    log.debug('Client - loginAsGuest()');

    if (!this.game) {
      log.debug('Client - loginAsGuest() - No Game');
      return false;
    }

    this.guestLogin = true;
    this.login();
    return true;
  }

  /**
   * Switch to the load character screen
   * @private
   * @return {Boolean}
   */
  loadCharacter() {
    log.debug('Client - loadCharacter()');

    if (
      this.wrapper.hasClass('about')
      || this.wrapper.hasClass('credits')
      || this.wrapper.hasClass('git')
    ) {
      this.wrapper.removeClass('about credits git');
      this.displayScroll('loadCharacter');
      return true;
    }

    return false;
  }

  /**
   * Tells the game to toggle remembering this player,
   * if true will store player information in local storage
   *
   * @TODO update this to use the database instead
   * @private
   * @return {Boolean}
   */
  rememberLogin() {
    log.debug('Client - rememberLogin()');

    if (!this.game || !this.game.storage) {
      return false;
    }

    const active = this.rememberMe.hasClass('active');

    this.rememberMe.toggleClass('active');
    this.game.storage.toggleRemember(!active);
    return true;
  }

  /**
   * Tells the game to respawn the player
   * @private
   * @return {Boolean}
   */
  respawnPlayer() {
    log.debug('Client - respawnPlayer()');

    if (!this.game || !this.game.player || !this.game.player.dead) {
      return false;
    }

    this.game.respawn();
    return true;
  }

  /**
   * Handles key down presses
   * @param {Object} event keypress event
   * @return {Boolean}
   */
  keydownEventListener(event) {
    log.debug('Client - keydownEventListener()', event);

    const key = event.which;

    if (!this.game) {
      return false;
    }

    this.body.focus();

    if (key === Module.Keys.Enter && !this.game.started) {
      this.login();
      return false;
    }

    if (this.game.started) {
      this.game.onInput(Module.InputType.Key, key);
    }

    return true;
  }

  /**
   * Handles key up presses
   * @param {Object} event keypress event
   * @return {Boolean}
   */
  keyupEventListener(event) {
    log.debug('Client - keyupEventListener()', event);

    const key = event.which;

    if (!this.game || !this.game.started) {
      return false;
    }

    this.game.input.keyUp(key);
    return true;
  }

  /**
   * Handles mouse movements
   * @param {Object} event mouse event
   */
  mousemoveEventListener(event) {
    // log.debug('Client - mousemoveEventListener()', event, this);

    if (!this.game || !this.game.input || !this.game.started) {
      return false;
    }

    this.game.input.setCoords(event);
    this.game.input.moveCursor();

    return true;
  }

  /**
   * Handles clicks on the HTML5 canvas
   * @param {Object} event click event
   * @return {Boolean}
   */
  canvasClickEventListener(event) {
    log.debug('Client - canvasClickEventListener()');

    if (!this.game || !this.game.started) {
      return false;
    }

    window.scrollTo(0, 1);
    this.game.input.handle(Module.InputType.LeftClick, event);

    return true;
  }

  /**
   * Handles zooming the container to scale the
   * game to fit porportionally with the player's window size
   */
  zoom() {
    log.debug('Client - zoom()');

    // const containerWidth = this.container.width();
    // const containerHeight = this.container.height();
    // const windowWidth = this.window.width();
    // const windowHeight = this.window.height();
    // let zoomFactor = windowWidth / containerWidth;
    //
    // if (containerHeight + 50 >= windowHeight) {
    //   zoomFactor = windowHeight / (containerHeight + 50);
    // }
    //
    // if (this.getScaleFactor() === 3) {
    //   zoomFactor -= 0.1;
    // }

    // this.body.css({
    //   zoom: zoomFactor,
    //   '-moz-transform': `scale(${zoomFactor})`,
    // });

    this.border.css('top', 0);
    // this.zoomFactor = zoomFactor;
  }

  /**
   * Fades the menu
   */
  fadeMenu() {
    log.debug('Client - fadeMenu()');

    this.updateLoader(null);

    setTimeout(() => {
      this.body.addClass('game');
      this.body.addClass('started');
      this.body.removeClass('intro');
    }, 500);
  }

  /**
   * Shows the menu
   */
  showMenu() {
    log.debug('Client - showMenu()');

    this.body.removeClass('game');
    this.body.removeClass('started');
    this.body.addClass('intro');
  }

  /**
   * Changes from one screen to another
   * @param {String} origin the old screen to hide
   * @param {String} destination the new screen to show
   */
  displayScreen(origin, destination) {
    log.debug('Client - displayScreen()', origin, destination);

    if (!destination || this.loggingIn) {
      return false;
    }

    this.cleanErrors();
    // this.wrapper.removeClass(origin).addClass(destination);
    $(`#${origin}`).css('display', 'none');
    $(`#${destination}`).css('display', 'block');
    return true;
  }

  /**
   * Display the given screen
   * @param {String} content - the screen to display
   */
  displayScroll(content) {
    log.debug('Client - displayScroll()', content);

    const state = this.wrapper.attr('class');

    if (this.game.started) {
      this.wrapper.removeClass().addClass(content);
      this.body.removeClass('credits legal about').toggleClass(content);

      if (this.game.player) {
        this.body.toggleClass('death');
      }

      if (content !== 'about') {
        this.helpButton.removeClass('active');
      }
    } else if (state !== 'animate') {
      this.displayScreen(state, state === content ? 'loadCharacter' : content);
    }
  }

  /**
   * Verify the active form
   */
  verifyForm() {
    log.debug('Client - verifyForm()');

    const activeForm = this.getActiveForm();

    if (activeForm === 'null') {
      return true;
    }

    switch (activeForm) {
      case 'loadCharacter': return this.verifyLoginForm();
      case 'createCharacter': return this.verifyJoinForm();
      default: break;
    }

    return true;
  }

  /**
   * Verify the input fields on the join form
   * @return {Boolean} true if valid
   */
  verifyJoinForm() {
    log.debug('Client - verifyJoinForm()');

    const characterName = $('#registerNameInput');
    const registerPassword = $('#registerPasswordInput');
    const email = $('#registerEmailInput');
    const registerPasswordConfirmation = $('#registerPasswordConfirmationInput');

    if (this.registerFields.length === 0) {
      this.registerFields = [
        characterName,
        registerPassword,
        registerPasswordConfirmation,
        email,
      ];
    }

    if (!characterName.val()) {
      this.sendError(characterName, 'A username is necessary you silly.');
      return false;
    }

    if (!registerPassword.val()) {
      this.sendError(registerPassword, 'You must enter a password.');
      return false;
    }

    if (registerPasswordConfirmation.val() !== registerPassword.val()) {
      this.sendError(
        registerPasswordConfirmation,
        'The passwords do not match!',
      );
      return false;
    }

    if (!email.val() || !this.verifyEmail(email.val())) {
      this.sendError(email, 'An email is required!');
      return false;
    }

    return true;
  }

  /**
   * Check to see if the player has entered a valid username and password
   * before attempting to login
   * @return {Boolean} true if valid
   */
  verifyLoginForm() {
    log.debug('Client - verifyLoginForm()');

    const nameInput = $('#loginNameInput');
    const passwordInput = $('#loginPasswordInput');

    if (this.loginFields.length === 0) {
      this.loginFields = [nameInput, passwordInput];
    }

    if (!nameInput.val() && !this.isGuest()) {
      this.sendError(nameInput, 'Please enter a username.');
      return false;
    }

    if (!passwordInput.val() && !this.isGuest()) {
      this.sendError(passwordInput, 'Please enter a password.');
      return false;
    }

    return true;
  }

  /* eslint-disable */
  /**
   * Verify this is a valid email address
   * @param  {String} email player's email address
   * @return {Boolean} true if valid
   */
  verifyEmail(email) {
    log.debug('Client - verifyEmail()', email);

    return /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
      email,
    );
  }
  /* eslint-enable */

  /**
   * Display a status message to the client
   * @param {String} message the message
   */
  sendStatus(message) {
    log.debug('Client - sendStatus()', message);

    this.cleanErrors();

    this.statusMessage = message;

    if (!message) {
      this.loadingMsg.html('');
      this.loading.hide();
      return;
    }

    this.loading.show();
    this.loadingMsg.html(message);
  }

  /**
   * Set this input field as having an error
   * @param {Object} field the Jquery instance of the field that has the error
   * @param {String} error the error message to display telling the player
   * that this field has an issue
   */
  sendError(field, error) {
    log.debug('Client - sendError()', field, error);

    this.cleanErrors();
    this.errorMsg.html(error);
    this.errorMsg.show();

    if (!field) {
      return;
    }

    field.addClass('field-error').select();
    field.bind('keypress', (event) => {
      field.removeClass('field-error');
      $('.validation-error').remove();
      $(this).unbind(event);
    });
  }

  /**
   * Clean up any current form validation errors
   * on the active form's input fields
   */
  cleanErrors() {
    log.debug('Client - cleanErrors()');

    const activeForm = this.getActiveForm();
    const fields = activeForm === 'loadCharacter'
      ? this.loginFields
      : this.registerFields;

    for (let i = 0; i < fields.length; i += 1) {
      fields[i].removeClass('field-error');
    }

    $('.validation-error').remove();
    $('.status').remove();
  }

  /**
   * Return the active form from the top wrapper's class name
   * @return {String} active form's ID
   */
  getActiveForm() {
    log.debug('Client - getActiveForm()');

    return this.wrapper && this.wrapper[0] && this.wrapper[0].className;
  }

  /**
   * Returns true if the join form is displayed
   * @return {Boolean} true if on the join form
   */
  isRegistering() {
    log.debug('Client - isRegistering()');

    return this.getActiveForm() === 'createCharacter';
  }

  /**
   * Returns true if this player is playing as a guest
   * @return {Boolean} true if player is a guest
   */
  isGuest() {
    log.debug('Client - isGuest()');

    return this.guestLogin;
  }

  /**
   * Call the game's resizer function
   */
  resize() {
    log.debug('Client - resize()');

    if (this.game) {
      this.game.resize();
    }
  }

  /**
   * Set the client to use a new instance of the game
   */
  setGame(game) {
    log.debug('Client - setGame()', game);

    this.game = game;
    this.window.wtf = this;
  }

  /**
   * Checks to see if the window has a web worker
   * @return {Boolean} returns true if the window has an active web worker
   */
  hasWorker() {
    log.debug('Client - hasWorker()');

    return !!this.window.Worker;
  }

  /**
   * Figures out the current scale factor to use
   * for assets
   * @return {Number} 1 = mobile, 2 = desktop and 3 = tablet
   */
  getScaleFactor() {
    const mobile = 1;
    const tablet = 2;
    const desktop = 3;
    const tabletOrDesktop = this.width <= 1500 || this.height <= 870
      ? tablet
      : desktop;

    /**
     * These are raw scales, we can adjust
     * for up-scaled rendering in the actual
     * rendering file.
     */
    return this.width <= 1000
      ? mobile
      : tabletOrDesktop;
  }

  /**
   * Revert the loader message to it's default state
   * of 'Connecting to server...'
   */
  revertLoader() {
    log.debug('Client - revertLoader()');

    this.updateLoader('Connecting to server...');
  }

  /**
   * Update the loader to show a specific message
   * @param  {String} message the loader message to display
   */
  updateLoader(message) {
    log.debug('Client - updateLoader()', message);

    if (!message) {
      this.loading.hide();
      this.loadingMsg.html('');
      return;
    }

    this.loading.show();
    this.loadingMsg.html(message);
  }

  /**
   * Toggle displaying the login form
   * @param  {Boolean} toggle true to hide, false to show
   */
  toggleLogin(toggle) {
    log.debug('Client - toggleLogin()', toggle);

    this.revertLoader();
    this.toggleTyping(toggle);
    this.loggingIn = toggle;

    if (toggle) {
      this.loading.hide();
      this.loginButton.addClass('disabled');
      this.registerButton.addClass('disabled');
    } else {
      this.loading.hide();
      this.loginButton.removeClass('disabled');
      this.registerButton.removeClass('disabled');
    }
  }

  /**
   * Toggle the read only attribute on the login fields
   * and form registration fields
   * @param  {String} state the state to update the fields to
   */
  toggleTyping(state) {
    log.debug('Client - toggleTyping()', state);

    if (this.loginFields) {
      _.each(this.loginFields, (field) => {
        field.prop('readonly', state);
      });
    }

    if (this.registerFields) {
      _.each(this.registerFields, (field) => {
        field.prop('readOnly', state);
      });
    }
  }

  /**
   * Update the range of and object
   * using a linear gradient on the background color
   */
  updateRange(field) {
    log.debug('Client - updateRange()', field);

    const obj = $(field);
    const val = (obj.val() - obj.attr('min')) / (obj.attr('max') - obj.attr('min'));

    obj.css(
      'background-image',
      `-webkit-gradient(linear, left top, right top, color-stop(${val}, #4d4d4d), color-stop(${val}, #C5C5C5)`,
    );

    this.rangeField = field;
  }

  /**
   * Updates the orientation of the application
   * to match the orientation of the device the
   * player is using
   */
  updateOrientation() {
    log.debug('Client - updateOrientation()');

    // this.zoom();
    this.orientation = this.getOrientation();
  }

  /**
   * Returns the orientation of the device the user is on
   * @return {String} portrait|landscape
   */
  getOrientation() {
    log.debug('Client - getOrientation()');

    return this.height > this.width ? 'portrait' : 'landscape';
  }

  /**
   * Returns the zoom factor
   * @return {Number} a value from 0 to 1
   */
  getZoom() {
    // log.debug('Client - getZoom()');

    return this.zoomFactor;
  }

  /**
   * The callback function when this app class is ready
   * and done loading
   * @param {Function} callback the callback function when
   * this application class is done loading
   */
  onReady(callback) {
    log.debug('Client - onReady()', callback);

    this.readyCallback = callback;
  }

  /**
   * Check if the player is using a phone
   * @return {Boolean} true if a phone
   */
  isMobile() {
    log.debug('Client - isMobile()');

    return this.getScaleFactor() < 2;
  }

  /**
   * Check if the player is using a tablet
   * @return {Boolean} true if a tablet
   */
  isTablet() {
    log.debug('Client - isTablet()');

    return Detect.isIpad() || (Detect.isAndroid() && this.getScaleFactor() > 1);
  }
}

