import $ from 'jquery';

export default class Abilities {
  constructor(game) {
    this.game = game;
    this.shortcuts = $('#abilityShortcut');
  }

  getList() {
    return this.shortcuts.find('ul');
  }
}

import $ from 'jquery';

export default class Actions {
  constructor(intrfce) {
    this.interface = intrfce;

    this.body = $('#actionContainer');
    this.drop = $('#dropDialog');
    this.dropInput = $('#dropCount');

    this.pBody = $('#pActions');
    this.follow = $('#follow');
    this.trade = $('#tradeAction');

    this.activeClass = null;

    this.miscButton = null;

    this.loadActions();
  }

  loadActions() {
    const dropAccept = $('#dropAccept');
    const dropCancel = $('#dropcancel');

    dropAccept.click((event) => {
      if (this.activeClass === 'inventory') {
        this.interface.inventory.clickAction(event);
      }
    });

    dropCancel.click((event) => {
      if (this.activeClass === 'inventory') {
        this.interface.inventory.clickAction(event);
      }
    });
  }

  loadDefaults(activeClass) {
    this.activeClass = activeClass;

    switch (this.activeClass) {
      default:
        break;
      case 'inventory':
        const dropButton = $('<div id="drop" class="actionButton">Drop</div>'); // eslint-disable-line
        this.add(dropButton);
        break;

      case 'profile':
        break;
    }
  }

  add(button, misc) {
    this.body.find('ul').prepend($('<li></li>').append(button));

    button.click((event) => {
      if (this.activeClass === 'inventory') {
        this.interface.inventory.clickAction(event);
      }
    });

    if (misc) {
      this.miscButton = button;
    }
  }

  removeMisc() {
    this.miscButton.remove();
    this.miscButton = null;
  }

  reset() {
    const buttons = this.getButtons();

    for (let i = 0; i < buttons.length; i += 1) {
      $(buttons[i]).remove();
    }
  }

  show() {
    this.body.fadeIn('fast');
  }

  showPlayerActions(player, mouseX, mouseY) {
    if (!player) return;

    this.pBody.fadeIn('fast');
    this.pBody.css({
      left: `${mouseX - this.pBody.width() / 2}px`,
      top: `${mouseY + this.pBody.height() / 2}px`,
    });

    this.follow.click(() => {
      this.getPlayer().follow(player);

      this.hidePlayerActions();
    });

    this.trade.click(() => {
      this.getGame().tradeWith(player);

      this.hidePlayerActions();
    });
  }

  hide() {
    this.body.fadeOut('slow');
  }

  hidePlayerActions() {
    this.pBody.fadeOut('fast');
  }

  displayDrop(activeClass) {
    this.activeClass = activeClass;
    this.drop.fadeIn('fast');
    this.dropInput.focus();
    this.dropInput.select();
  }

  hideDrop() {
    this.drop.fadeOut('slow');
    this.dropInput.blur();
    this.dropInput.val('');
  }

  getButtons() {
    return this.body.find('ul').find('li');
  }

  getGame() {
    return this.interface.game;
  }

  getPlayer() {
    return this.interface.game.player;
  }

  isVisible() {
    return this.body.css('display') === 'block';
  }
}

import $ from 'jquery';
import Container from './container/container';
import Packets from '../network/packets';

export default class Bank {
  constructor(game, inventoryContainer, size) {
    this.game = game;
    this.inventoryContainer = inventoryContainer;
    this.player = game.player;
    this.body = $('#bank');
    this.bankSlots = $('#bankSlots');
    this.bankInventorySlots = $('#bankInventorySlots');
    this.container = new Container(size);
    this.close = $('#closeBank');
    this.close.css('left', '97%');
    this.close.click(() => {
      this.hide();
    });
  }

  loadBank(data) {
    const bankList = this.bankSlots.find('ul');
    const inventoryList = this.bankInventorySlots.find('ul');

    for (let i = 0; i < data.length; i += 1) {
      const item = data[i];
      const slot = $(`<div id="bankSlot${i}" class="bankSlot"></div>`);

      this.container.setSlot(i, item);

      slot.css({
        'margin-right': `${2 * this.getScale()}px`,
        'margin-bottom': `${4 * this.getScale()}px`,
      });

      const image = $(`<div id="bankImage${i}" class="bankImage"></div>`);

      if (item.name) {
        image.css(
          'background-image',
          this.container.getImageFormat(this.getDrawingScale(), item.name),
        );
      }

      slot.click((event) => {
        this.click('bank', event);
      });

      if (this.game.client.isMobile()) {
        image.css('background-size', '600%');
      }

      slot.append(image);
      slot.append(
        `<div id="bankItemCount${i}" class="itemCount">${item.count > 1 ? item.count : ''}</div>`,
      );

      slot.find(`#bankItemCount${i}`).css({
        'font-size': `${4 * this.getScale()}px`,
        'margin-top': '0',
        'margin-left': '0',
      });

      const bankListItem = $('<li></li>');

      bankListItem.append(slot);
      bankList.append(bankListItem);
    }

    for (let j = 0; j < this.inventoryContainer.size; j += 1) {
      const iItem = this.inventoryContainer.slots[j];
      const iSlot = $(
        `<div id="bankInventorySlot${j}" class="bankSlot"></div>`,
      );

      iSlot.css({
        'margin-right': `${3 * this.getScale()}px`,
        'margin-bottom': `${6 * this.getScale()}px`,
      });

      const slotImage = $(
        `<div id="inventoryImage${j}" class="bankImage"></div>`,
      );

      if (iItem.name) {
        slotImage.css(
          'background-image',
          this.container.getImageFormat(this.getDrawingScale(), iItem.name),
        );
      }

      iSlot.click(function (event) {
        this.click('inventory', event);
      });

      if (this.game.client.isMobile()) {
        slotImage.css('background-size', '600%');
      }

      iSlot.append(slotImage);
      iSlot.append(
        `<div id="inventoryItemCount${j}" class="itemCount">${iItem.count > 1 ? iItem.count : ''}</div>`,
      );

      iSlot.find(`#inventoryItemCount${j}`).css({
        'margin-top': '0',
        'margin-left': '0',
      });

      const inventoryListItem = $('<li></li>');
      inventoryListItem.append(iSlot);
      inventoryList.append(inventoryListItem);
    }
  }

  resize() {
    const bankList = this.getBankList();
    const inventoryList = this.getInventoryList();

    for (let i = 0; i < bankList.length; i += 1) {
      const bankSlot = $(bankList[i]).find(`#bankSlot${i}`);
      const image = bankSlot.find(`#bankImage${i}`);
      const slot = this.container.slots[i];

      bankSlot.css({
        'margin-right': `${2 * this.getScale()}px`,
        'margin-bottom': `${4 * this.getScale()}px`,
      });

      bankSlot.find(`#bankItemCount${i}`).css({
        'font-size': `${4 * this.getScale()}px`,
        'margin-top': '0',
        'margin-left': '0',
      });

      if (this.game.client.isMobile()) {
        image.css('background-size', '600%');
      } else {
        image.css(
          'background-image',
          this.container.getImageFormat(this.getDrawingScale(), slot.name),
        );
      }
    }

    for (let j = 0; j < inventoryList.length; j += 1) {
      const inventorySlot = $(inventoryList[j]).find(`#bankInventorySlot${j}`);
      const iImage = inventorySlot.find(`#inventoryImage${j}`);
      const iSlot = this.inventoryContainer.slots[j];

      inventorySlot.css({
        'margin-right': `${3 * this.getScale()}px`,
        'margin-bottom': `${6 * this.getScale()}px`,
      });

      if (this.game.client.isMobile()) {
        iImage.css('background-size', '600%');
      } else {
        iImage.css(
          'background-image',
          this.container.getImageFormat(this.getDrawingScale(), iSlot.name),
        );
      }
    }
  }

  click(type, event) {
    const isBank = type === 'bank';
    const index = event.currentTarget.id.substring(isBank ? 8 : 17);

    this.game.socket.send(Packets.Bank, [
      Packets.BankOpcode.Select,
      type,
      index,
    ]);
  }

  add(info) {
    const item = $(this.getBankList()[info.index]);
    const slot = this.container.slots[info.index];

    if (!item || !slot) {
      return;
    }

    if (slot.isEmpty()) {
      slot.loadSlot(info.name, info.count, info.ability, info.abilityLevel);
    }

    slot.setCount(info.count);

    const bankSlot = item.find(`#bankSlot${info.index}`);
    const cssSlot = bankSlot.find(`#bankImage${info.index}`);
    const count = bankSlot.find(`#bankItemCount${info.index}`);

    cssSlot.css(
      'background-image',
      this.container.getImageFormat(this.getDrawingScale(), info.name),
    );

    if (this.game.client.isMobile()) {
      cssSlot.css('background-size', '600%');
    }

    if (slot.count > 1) {
      count.text(slot.count);
    }
  }

  remove(info) {
    const item = $(this.getBankList()[info.index]);
    const slot = this.container.slots[info.index];

    if (!item || !slot) {
      return;
    }

    slot.count -= info.count;

    if (slot.count < 1) {
      const divItem = item.find(`#bankSlot${info.index}`);

      divItem.find(`#bankImage${info.index}`).css('background-image', '');
      divItem.find(`#bankItemCount${info.index}`).text('');

      slot.empty();
    }
  }

  addInventory(info) {
    const item = $(this.getInventoryList()[info.index]);

    if (!item) {
      return;
    }

    const slot = item.find(`#bankInventorySlot${info.index}`);
    const image = slot.find(`#inventoryImage${info.index}`);

    image.css(
      'background-image',
      this.container.getImageFormat(this.getDrawingScale(), info.name),
    );

    if (this.game.client.isMobile()) {
      image.css('background-size', '600%');
    }

    if (info.count > 1) {
      slot.find(`#inventoryItemCount${info.index}`).text(info.count);
    }
  }

  removeInventory(info) {
    const item = $(this.getInventoryList()[info.index]);

    if (!item) {
      return;
    }

    const slot = item.find(`#bankInventorySlot${info.index}`);

    slot.find(`#inventoryImage${info.index}`).css('background-image', '');
    slot.find(`#inventoryItemCount${info.index}`).text('');
  }

  display() {
    this.body.fadeIn('slow');
  }

  hide() {
    this.body.fadeOut('fast');
  }

  isVisible() {
    return this.body.css('display') === 'block';
  }

  getDrawingScale() {
    return this.game.renderer.getDrawingScale();
  }

  getScale() {
    return this.game.getScaleFactor();
  }

  getBankList() {
    return this.bankSlots.find('ul').find('li');
  }

  getInventoryList() {
    return this.bankInventorySlots.find('ul').find('li');
  }
}

import Slot from './slot';

export default class Container {
  constructor(size) {
    this.size = size;
    this.slots = [];

    for (let i = 0; i < this.size; i += 1) {
      this.slots.push(new Slot(i));
    }
  }

  /**
   * We receive information from the server here,
   * so we mustn't do any calculations. Instead,
   * we just modify the container directly.
   */
  setSlot(index, info) {
    this.slots[index].loadSlot(
      info.name,
      info.count,
      info.ability,
      info.abilityLevel,
      info.edible,
      info.equippable,
    );
  }

  getEmptySlot() {
    for (let i = 0; i < this.slots; i += 1) {
      if (!this.slots[i].name) {
        return i;
      }
    }

    return -1;
  }

  getImageFormat(scale, name) {
    return `url("/img/${scale}/item-${name}.png")`;
  }
}

export default class Slot {
  constructor(index) {
    this.index = index;
    this.name = null;
    this.count = -1;
    this.ability = -1;
    this.abilityLevel = -1;
    this.edible = false;
    this.equippable = false;
  }

  loadSlot(name, count, ability, abilityLevel, edible, equippable) {
    this.name = name;
    this.count = count;
    this.ability = ability;
    this.abilityLevel = abilityLevel;
    this.edible = edible;
    this.equippable = equippable;
  }

  empty() {
    this.name = null;
    this.count = -1;
    this.ability = -1;
    this.abilityLevel = -1;
    this.edible = false;
    this.equippable = false;
  }

  isEmpty() {
    return this.name === null || this.count === -1;
  }

  setCount(count) {
    this.count = count;
  }
}

import $ from 'jquery';
import Packets from '../network/packets';

export default class Enchant {
  constructor(game, intrface) {
    this.game = game;
    this.interface = intrface;
    this.body = $('#enchant');
    this.container = $('#enchantContainer');
    this.enchantSlots = $('#enchantInventorySlots');
    this.selectedItem = $('#enchantSelectedItem');
    this.selectedShards = $('#enchantShards');
    this.confirm = $('#confirmEnchant');
    this.shardsCount = $('#shardsCount');

    this.confirm.css({
      left: '70%',
      top: '80%',
    });

    $('#closeEnchant').click(() => {
      this.hide();
    });

    this.confirm.click(() => {
      this.enchant();
    });
  }

  add(type, index) {
    const self = this;


    const image = this.getSlot(index).find(`#inventoryImage${index}`);

    switch (type) {
      default:
        break;
      case 'item':
        this.selectedItem.css(
          'background-image',
          image.css('background-image'),
        );

        break;

      case 'shards':
        this.selectedShards.css(
          'background-image',
          image.css('background-image'),
        );

        const { // eslint-disable-line
          count,
        } = this.getItemSlot(index);

        if (count > 1) {
          this.shardsCount.text(count);
        }

        break;
    }

    image.css('background-image', '');

    self
      .getSlot(index)
      .find(`#inventoryItemCount${index}`)
      .text('');
  }

  moveBack(type, index) {
    const image = this.getSlot(index).find(`#inventoryImage${index}`);
    const itemCount = this.getSlot(index).find(`#inventoryItemCount${index}`);
    const {
      count,
    } = this.getItemSlot(index);

    switch (type) {
      default:
        break;
      case 'item':
        image.css(
          'background-image',
          this.selectedItem.css('background-image'),
        );

        if (count > 1) itemCount.text(count);

        this.selectedItem.css('background-image', '');

        break;

      case 'shards':
        image.css(
          'background-image',
          this.selectedShards.css('background-image'),
        );

        if (count > 1) itemCount.text(count);

        this.selectedShards.css('background-image', '');

        this.shardsCount.text('');

        break;
    }
  }

  loadEnchant() {
    const list = this.getSlots();
    const inventoryList = this.interface.bank.getInventoryList();

    list.empty();

    for (let i = 0; i < this.getInventorySize(); i += 1) {
      const item = $(inventoryList[i]).clone();
      const slot = item.find(`#bankInventorySlot${i}`);

      slot.click((event) => {
        this.select(event);
      });

      list.append(item);
    }

    this.selectedItem.click(() => {
      this.remove('item');
    });

    this.selectedShards.click(() => {
      this.remove('shards');
    });
  }

  enchant() {
    this.game.socket.send(Packets.Enchant, [Packets.EnchantOpcode.Enchant]);
  }

  select(event) {
    this.game.socket.send(Packets.Enchant, [
      Packets.EnchantOpcode.Select,
      event.currentTarget.id.substring(17),
    ]);
  }

  remove(type) {
    this.game.socket.send(Packets.Enchant, [
      Packets.EnchantOpcode.Remove,
      type,
    ]);
  }

  getInventorySize() {
    return this.interface.inventory.getSize();
  }

  getItemSlot(index) {
    return this.interface.inventory.container.slots[index];
  }

  display() {
    this.body.fadeIn('fast');
    this.loadEnchant();
  }

  hide() {
    this.selectedItem.css('background-image', '');
    this.selectedShards.css('background-image', '');
    this.body.fadeOut('fast');
  }

  hasImage(image) {
    return image.css('background-image') !== 'none';
  }

  getSlot(index) {
    return $(this.getSlots().find('li')[index]);
  }

  getSlots() {
    return this.enchantSlots.find('ul');
  }

  isVisible() {
    return this.body.css('display') === 'block';
  }
}

import $ from 'jquery';
