 *
 * @cfg {Error} e The error to create a stacktrace from (optional)
 * @cfg {Boolean} guess If we should try to resolve the names of anonymous functions
 * @return {Array} of Strings with functions, lines, files, and arguments where possible
 */
function printStackTrace(options) {
  const ex = options && options.e ? options.e : null;
  const guess = options ? !!options.guess : true;

  const p = new printStackTrace.implementation();
  const result = p.run(ex);
  return guess ? p.guessFunctions(result) : result;
}

printStackTrace.implementation = () => {};

printStackTrace.implementation.prototype = {
  run(ex) {
    // Use either the stored mode, or resolve it
    const mode = this._mode || this.mode();
    if (mode === 'other') {
      return this.other(arguments.callee);
    }
    ex = ex
      || (function () {
        try {
          const _err = __undef__ << 1;
        } catch (e) {
          return e;
        }
      }());
    return this[mode](ex);
  },

  /**
   * @return {String} mode of operation for the environment in question.
   */
  mode() {
    try {
      const _err = __undef__ << 1;
    } catch (e) {
      if (e.arguments) {
        return (this._mode = 'chrome');
      }
      if (typeof window !== "undefined" && window.opera && e.stacktrace) {
        return (this._mode = 'opera10');
      }
      if (e.stack) {
        return (this._mode = 'firefox');
      }
      if (typeof window !== "undefined" && window.opera && !('stacktrace' in e)) {
        // Opera 9-
        return (this._mode = 'opera');
      }
    }
    return (this._mode = 'other');
  },

  /**
   * Given a context, function name, and callback function, overwrite it so that it calls
   * printStackTrace() first with a callback and then runs the rest of the body.
   *
   * @param {Object} context of execution (e.g. window)
   * @param {String} functionName to instrument
   * @param {Function} function to call with a stack trace on invocation
   */
  instrumentFunction(context, functionName, callback) {
    if (typeof window !== "undefined") {
      context = context || window;
    }

    context[`_old${functionName}`] = context[functionName];
    context[functionName] = function () {
      callback.call(this, printStackTrace());
      return context[`_old${functionName}`].apply(this, arguments);
    };
    context[functionName]._instrumented = true;
  },

  /**
   * Given a context and function name of a function that has been
   * instrumented, revert the function to it's original (non-instrumented)
   * state.
   *
   * @param {Object} context of execution (e.g. window)
   * @param {String} functionName to de-instrument
   */
  deinstrumentFunction(context, functionName) {
    if (
      context[functionName].constructor === Function
      && context[functionName]._instrumented
      && context[`_old${functionName}`].constructor === Function
    ) {
      context[functionName] = context[`_old${functionName}`];
    }
  },

  /**
   * Given an Error object, return a formatted Array based on Chrome's stack string.
   *
   * @param e - Error object to inspect
   * @return Array<String> of function calls, files and line numbers
   */
  chrome(e) {
    return e.stack
      .replace(/^[^\n]*\n/, '')
      .replace(/^[^\n]*\n/, '')
      .replace(/^[^\(]+?[\n$]/gm, '')
      .replace(/^\s+at\s+/gm, '')
      .replace(/^Object.<anonymous>\s*\(/gm, '{anonymous}()@')
      .split('\n');
  },

  /**
   * Given an Error object, return a formatted Array based on Firefox's stack string.
   *
   * @param e - Error object to inspect
   * @return Array<String> of function calls, files and line numbers
   */
  firefox(e) {
    return e.stack
      .replace(/^[^\n]*\n/, '')
      .replace(/(?:\n@:0)?\s+$/m, '')
      .replace(/^\(/gm, '{anonymous}(')
      .split('\n');
  },

  /**
   * Given an Error object, return a formatted Array based on Opera 10's stacktrace string.
   *
   * @param e - Error object to inspect
   * @return Array<String> of function calls, files and line numbers
   */
  opera10(e) {
    const stack = e.stacktrace;
    const lines = stack.split('\n');
    const ANON = '{anonymous}';
    const lineRE = /.*line (\d+), column (\d+) in ((<anonymous function\:?\s*(\S+))|([^\(]+)\([^\)]*\))(?: in )?(.*)\s*$/i;
    let i;
    let j;
    let len;

    for (i = 2, j = 0, len = lines.length; i < len - 2; i += 1) {
      if (lineRE.test(lines[i])) {
        const location = `${RegExp.$6}:${RegExp.$1}:${RegExp.$2}`;
        let fnName = RegExp.$3;
        fnName = fnName.replace(/<anonymous function\s?(\S+)?>/g, ANON);
        lines[j += 1] = `${fnName}@${location}`;
      }
    }

    lines.splice(j, lines.length - j);
    return lines;
  },

  // Opera 7.x-9.x only!
  opera(e) {
    const lines = e.message.split('\n');
    const ANON = '{anonymous}';
    const lineRE = /Line\s+(\d+).*script\s+(http\S+)(?:.*in\s+function\s+(\S+))?/i;
    let i;
    let j;
    let len;

    for (i = 4, j = 0, len = lines.length; i < len; i += 2) {
      // TODO: RegExp.exec() would probably be cleaner here
      if (lineRE.test(lines[i])) {
        lines[j += 1] = `${RegExp.$3
          ? `${RegExp.$3}()@${RegExp.$2}${RegExp.$1}`
          : `${ANON}()@${RegExp.$2}:${RegExp.$1}`
        } -- ${
          lines[i + 1].replace(/^\s+/, '')}`;
      }
    }

    lines.splice(j, lines.length - j);
    return lines;
  },

  // Safari, IE, and others
  other(curr) {
    const ANON = '{anonymous}';
    const fnRE = /function\s*([\w\-$]+)?\s*\(/i;
    const stack = [];
    let j = 0;
    let fn;
    let args;
    const maxStackSize = 10;
    while (curr && stack.length < maxStackSize) {
      fn = fnRE.test(curr.toString()) ? RegExp.$1 || ANON : ANON;
      args = Array.prototype.slice.call(curr.arguments);
      stack[j += 1] = `${fn}(${this.stringifyArguments(args)})`;
      curr = curr.caller;
    }
    return stack;
  },

  /**
   * Given arguments array as a String, subsituting type names for non-string types.
   *
   * @param {Arguments} object
   * @return {Array} of Strings with stringified arguments
   */
  stringifyArguments(args) {
    for (let i = 0; i < args.length; ++i) {
      const arg = args[i];
      if (arg === undefined) {
        args[i] = 'undefined';
      } else if (arg === null) {
        args[i] = 'null';
      } else if (arg.constructor) {
        if (arg.constructor === Array) {
          if (arg.length < 3) {
            args[i] = `[${this.stringifyArguments(arg)}]`;
          } else {
            args[i] = `[${
              this.stringifyArguments(Array.prototype.slice.call(arg, 0, 1))
            }...${
              this.stringifyArguments(Array.prototype.slice.call(arg, -1))
            }]`;
          }
        } else if (arg.constructor === Object) {
          args[i] = '#object';
        } else if (arg.constructor === Function) {
          args[i] = '#function';
        } else if (arg.constructor === String) {
          args[i] = `"${arg}"`;
        }
      }
    }
    return args.join(',');
  },

  sourceCache: {},

  /**
   * @return the text from a given URL.
   */
  ajax(url) {
    const req = this.createXMLHTTPObject();
    if (!req) {
      return;
    }
    req.open('GET', url, false);
    req.setRequestHeader('User-Agent', 'XMLHTTP/1.0');
    req.send('');
    return req.responseText;
  },

  /**
   * Try XHR methods in order and store XHR factory.
   *
   * @return <Function> XHR function or equivalent
   */
  createXMLHTTPObject() {
    let xmlhttp;
    const XMLHttpFactories = [
      () => new XMLHttpRequest(),
      () => new ActiveXObject('Msxml2.XMLHTTP'),
      () => new ActiveXObject('Msxml3.XMLHTTP'),
      () => new ActiveXObject('Microsoft.XMLHTTP'),
    ];
    for (let i = 0; i < XMLHttpFactories.length; i += 1) {
      try {
        xmlhttp = XMLHttpFactories[i]();
        // Use memoization to cache the factory
        this.createXMLHTTPObject = XMLHttpFactories[i];
        return xmlhttp;
      } catch (e) {}
    }
  },

  /**
   * Given a URL, check if it is in the same domain (so we can get the source
   * via Ajax).
   *
   * @param url <String> source url
   * @return False if we need a cross-domain request
   */
  isSameDomain(url) {
    return url.indexOf(location.hostname) !== -1;
  },

  /**
   * Get source code from given URL if in the same domain.
   *
   * @param url <String> JS source URL
   * @return <String> Source code
   */
  getSource(url) {
    if (!(url in this.sourceCache)) {
      this.sourceCache[url] = this.ajax(url).split('\n');
    }
    return this.sourceCache[url];
  },

  guessFunctions(stack) {
    for (let i = 0; i < stack.length; ++i) {
      const reStack = /\{anonymous\}\(.*\)@(\w+:\/\/([\-\w[^-]]+)+(:\d+)?[^:]+):(\d+):?(\d+)?/;
      const frame = stack[i];
      const m = reStack.exec(frame);
      if (m) {
        const file = m[1];
        const lineno = m[4]; // m[7] is character position in Chrome
        if (file && this.isSameDomain(file) && lineno) {
          const functionName = this.guessFunctionName(file, lineno);
          stack[i] = frame.replace('{anonymous}', functionName);
        }
      }
    }
    return stack;
  },

  guessFunctionName(url, lineNo) {
    try {
      return this.guessFunctionNameFromLines(lineNo, this.getSource(url));
    } catch (e) {
      return (
        `getSource failed with url: ${url}, exception: ${e.toString()}`
      );
    }
  },

  guessFunctionNameFromLines(lineNo, source) {
    const reFunctionArgNames = /function ([^(]*)\(([^)]*)\)/;
    const reGuessFunction = /['"]?([0-9A-Za-z_]+)['"]?\s*[:=]\s*(function|eval|new Function)/;
    // Walk backwards from the first line in the function until we find the line which
    // matches the pattern above, which is the function definition
    let line = '';
    const maxLines = 10;

    for (let i = 0; i < maxLines; ++i) {
      line = source[lineNo - i] + line;
      if (line !== undefined) {
        let m = reGuessFunction.exec(line);
        if (m && m[1]) {
          return m[1];
        }
        m = reFunctionArgNames.exec(line);
        if (m && m[1]) {
          return m[1];
        }
      }
    }
    return '(?)';
  },
};

/* global Image, Worker */
import $ from 'jquery';
import _ from 'underscore';
import log from '../lib/log';
import {
  isInt,
} from '../utils/util';

export default class Map {
  constructor(game) {
    this.game = game;
    this.renderer = this.game.renderer;
    this.supportsWorker = this.game.client.hasWorker();
    this.data = [];
    this.tilesets = [];
    this.grid = null;
    this.tilesetsLoaded = false;
    this.mapLoaded = false;

    this.loadMap();
    this.loadTilesets();
    this.ready();
  }

  ready() {
    const rC = () => {
      if (this.readyCallback) {
        this.readyCallback();
      }
    };

    if (this.mapLoaded && this.tilesetsLoaded) {
      rC();
    } else {
      setTimeout(() => {
        this.ready();
      }, 50);
    }
  }

  loadMap() {
    if (this.supportsWorker) {
      log.debug('Map - loadMap() - Parsing map with Web Workers...');

      const worker = new Worker('./js/map/mapworker.js');
      worker.postMessage(1);

      worker.onmessage = (event) => {
        const map = event.data;

        this.parseMap(map);
        this.grid = map.grid;
        this.mapLoaded = true;
      };
    } else {
      log.debug('Map - loadMap() - Parsing map with Ajax...');

      $.get(
        '/data/maps/world_client.json',
        (data) => {
          this.parseMap(data);
          this.loadCollisions();
          this.mapLoaded = true;
        },
        'json',
      );
    }
  }

  /**
   * The tile-sheet of scale one is never used because
   * of its wrong proportions. Interesting enough, this would mean
   * that neither the entities would be necessary.
   */
  async loadTilesets() {
    const scale = this.renderer.getScale();
    log.debug('Map - loadTilesets()', scale);
    const tileset2 = await this.loadTileset(`/img/2/tilesheet.png`);
    const tileset3 = await this.loadTileset(`/img/3/tilesheet.png`);
    this.tilesets.push(tileset2);
    this.tilesets.push(tileset3);
    this.renderer.setTileset(this.tilesets[scale - 2]);
    this.tilesetsLoaded = true;
  }

  async updateTileset() {
    const scale = this.renderer.getDrawingScale();

    if (scale > 2 && !this.tilesets[1]) {
      this.tilesets.push(await this.loadTileset('/img/3/tilesheet.png'));
    }

    this.renderer.setTileset(this.tilesets[scale - 2]);
  }

  async loadTileset(path) {
    return new Promise((resolve) => {
      const tileset = new Image();
      tileset.crossOrigin = 'Anonymous';
      tileset.src = path;
      tileset.loaded = true;
      tileset.scale = this.renderer.getDrawingScale();

      tileset.onload = () => {
        if (tileset.width % this.tileSize > 0) {
          throw Error(`The tile size is malformed in the tile set: ${path}`);
        }
        resolve(tileset);
      };
    });
  }

  parseMap(map) {
    this.width = map.width;
    this.height = map.height;
    this.tileSize = map.tilesize;
    this.data = map.data;
    this.blocking = map.blocking || [];
    this.collisions = map.collisions;
    this.high = map.high;
    this.animated = map.animated;
  }

  loadCollisions() {
    this.grid = [];

    for (let i = 0; i < this.height; i += 1) {
      this.grid[i] = [];
      for (let j = 0; j < this.width; j += 1) {
        this.grid[i][j] = 0;
      }
    }

    _.each(this.collisions, (index) => {
      const position = this.indexToGridPosition(index + 1);
      this.grid[position.y][position.x] = 1;
    });

    _.each(this.blocking, (index) => {
      const position = this.indexToGridPosition(index + 1);

      if (this.grid[position.y]) {
        this.grid[position.y][position.x] = 1;
      }
    });
  }

  indexToGridPosition(index) {
    index -= 1; // eslint-disable-line

    const x = this.getX(index + 1, this.width);
    const y = Math.floor(index / this.width);

    return {
      x,
      y,
    };
  }

  gridPositionToIndex(x, y) {
    return y * this.width + x + 1;
  }

  isColliding(x, y) {
    if (this.isOutOfBounds(x, y) || !this.grid) {
      return false;
    }
    return this.grid[y][x] === 1;
  }

  isHighTile(id) {
    return this.high.indexOf(id + 1) >= 0;
  }

  isAnimatedTile(id) {
    return id + 1 in this.animated;
  }

  isOutOfBounds(x, y) {
    return (
      isInt(x)
      && isInt(y)
      && (x < 0 || x >= this.width || y < 0 || y >= this.height)
    );
  }

  getX(index, width) {
    if (index === 0) {
      return 0;
    }

    return index % width === 0 ? width - 1 : (index % width) - 1;
  }

  getTileAnimationLength(id) {
    return this.animated[id + 1].l;
  }

  getTileAnimationDelay(id) {
    const properties = this.animated[id + 1];
    return properties.d ? properties.d : 150;
  }

  onReady(callback) {
    this.readyCallback = callback;
  }
}

/* global postMessage */
import _ from 'underscore';
import mapData from '../../../../data/maps/world_client';

function loadCollisionGrid() {
  const tileIndex = 0;

  mapData.grid = [];

  for (let i = 0; i < mapData.height; i++) {
    mapData.grid[i] = [];
    for (let j = 0; j < mapData.width; j++) mapData.grid[i][j] = 0;
  }

  _.each(mapData.collisions, (tileIndex) => {
    const position = indexToGridPosition(tileIndex + 1);
    mapData.grid[position.y][position.x] = 1;
  });

  _.each(mapData.blocking, (tileIndex) => {
    const position = indexToGridPosition(tileIndex + 1);

    if (mapData.grid[position.y]) mapData.grid[position.y][position.x] = 1;
  });
}

function getX(index, width) {
  if (index === 0) {
    return 0;
  }

  return index % width === 0 ? width - 1 : (index % width) - 1;
}

const onmessage = () => {
  loadCollisionGrid();
  postMessage(mapData);
};

function indexToGridPosition(index) {
  let x = 0;
  let y = 0;

  index -= 1; // eslint-disable-line

  x = getX(index + 1, mapData.width);
  y = Math.floor(index / mapData.width);

  return {
    x,
    y,
  };
}

import _ from 'underscore';
import Packets from './packets';
import log from '../lib/log';

/**
 * Do not clutter up the Socket class with callbacks,
 * have this class here until a better method arises in my head.
 *
 * This class should not have any complex functionality, its main
 * role is to provide organization for packets and increase readability
 *
 * Please respect the order of the Packets Enum and arrange functions
 * accordingly.
 */
export default class Messages {
  constructor(client) {
    log.debug('Messages - constructor()', client);

    this.client = client;
    this.messages = [];
    this.messages[Packets.Handshake] = this.receiveHandshake; // 0
    this.messages[Packets.Intro] = null; // 1
    this.messages[Packets.Welcome] = this.receiveWelcome; // 2
    this.messages[Packets.Spawn] = this.receiveSpawn; // 3
    this.messages[Packets.List] = this.receiveEntityList; // 4
    this.messages[Packets.Who] = null; // 5
    this.messages[Packets.Equipment] = this.receiveEquipment; // 6
    this.messages[Packets.Ready] = null; // 7
    this.messages[Packets.Sync] = this.receiveSync; // 8
    this.messages[Packets.Movement] = this.receiveMovement; // 9
    this.messages[Packets.Teleport] = this.receiveTeleport; // 10
    this.messages[Packets.Request] = null; // 11
    this.messages[Packets.Despawn] = this.receiveDespawn; // 12
    this.messages[Packets.Target] = null; // 13
    this.messages[Packets.Combat] = this.receiveCombat; // 14
    this.messages[Packets.Animation] = this.receiveAnimation; // 15
    this.messages[Packets.Projectile] = this.receiveProjectile; // 16
    this.messages[Packets.Population] = this.receivePopulation; // 17
    this.messages[Packets.Points] = this.receivePoints; // 18
    this.messages[Packets.Network] = this.receiveNetwork; // 19
    this.messages[Packets.Chat] = this.receiveChat; // 20
    this.messages[Packets.Command] = this.receiveCommand; // 21
    this.messages[Packets.Inventory] = this.receiveInventory; // 22
    this.messages[Packets.Bank] = this.receiveBank; // 23
    this.messages[Packets.Ability] = this.receiveAbility; // 24
    this.messages[Packets.Quest] = this.receiveQuest; // 25
    this.messages[Packets.Notification] = this.receiveNotification; // 26
    this.messages[Packets.Blink] = this.receiveBlink; // 27
    this.messages[Packets.Heal] = this.receiveHeal; // 28
    this.messages[Packets.Experience] = this.receiveExperience; // 29
    this.messages[Packets.Death] = this.receiveDeath; // 30
    this.messages[Packets.Audio] = this.receiveAudio; // 31
    this.messages[Packets.NPC] = this.receiveNPC; // 32
    this.messages[Packets.Respawn] = this.receiveRespawn; // 33
    this.messages[Packets.Trade] = null;
    this.messages[Packets.Enchant] = this.receiveEnchant; // 35
    this.messages[Packets.Guild] = this.receiveGuild; // 36
    this.messages[Packets.Pointer] = this.receivePointer; // 37
    this.messages[Packets.PVP] = this.receivePVP; // 38
    this.messages[Packets.Click] = null; // 39
    this.messages[Packets.Warp] = null; // 40
    this.messages[Packets.Shop] = this.receiveShop; // 41
  }

  /**
   * Handle a message from the server
   * @param  {String} data the packet number and data
   */
  handleData(data) {
    log.debug('Messages - handleData()', data);

    const packet = data.shift();

    if (this.messages[packet] && _.isFunction(this.messages[packet])) {
      this.messages[packet].call(this, data);
    }
  }

  /**
   * Handle bulk data messages, calls handleData() on each one in the array
   * @param  {Array[String]} data Handle a list of messages
   */
  handleBulkData(data) {
    log.debug('Messages - handleBulkData()', data);

    _.each(data, (message) => {
      this.handleData(message);
    });
  }

  /**
   * Handle a UTF8 message, not a server packet message
   * Displays the error message on the client
   * @param  {String} message the message
   */
  handleUTF8(message) {
    log.debug('Messages - handleUTF8()', message);

    this.client.toggleLogin(false);

    switch (message) {
      case 'updated':
        this.client.sendError(null, 'The client has been updated!');
        break;

      case 'full':
        this.client.sendError(null, 'The servers are currently full!');
        break;

      case 'error':
        this.client.sendError(null, 'The server has responded with an error!');
        break;

      case 'development':
        this.client.sendError(
          null,
          'The game is currently in development mode.',
        );
        break;

      case 'disallowed':
        this.client.sendError(
          null,
          'The server is currently not accepting connections!',
        );
        break;

      case 'maintenance':
        this.client.sendError(
          null,
          'WTF?! Adventure is currently under construction.',
        );
        break;

      case 'userexists':
        this.client.sendError(
          null,
          'The username you have chosen already exists.',
        );
        break;

      case 'emailexists':
        this.client.sendError(
          null,
          'The email you have chosen is not available.',
        );
        break;

      case 'loggedin':
        this.client.sendError(null, 'The player is already logged in!');
        break;

      case 'invalidlogin':
        this.client.sendError(
          null,
          'You have entered the wrong username or password.',
        );
        break;

      case 'toofast':
        this.client.sendError(
          null,
          'You are trying to log in too fast from the same connection.',
        );
        break;

      case 'malform':
        this.client.game.handleDisconnection(true);
        this.client.sendError(null, 'Client has experienced a malfunction.');
        break;

      case 'timeout':
        this.client.sendError(
          null,
          'You have been disconnected for being inactive for too long.',
        );
        break;

      case 'validatingLogin':
        this.client.sendError(null, 'Validating login...');
        break;
      default:
        this.client.sendError(null, `An unknown error has occurred: ${message}`);
        break;
    }
  }

  /**
   * Recieve the server handshake
   * @param  {String} data Packet and data message
   */
  receiveHandshake(data) {
    log.debug('Messages - receiveHandshake()', data);

    if (this.handshakeCallback) {
      this.handshakeCallback(data.shift());
    }
  }

  /**
   * Recieve the server welcome
   * @param  {String} data Packet and data message
   */
  receiveWelcome(data) {
    log.debug('Messages - receiveWelcome()', data);

    const playerData = data.shift();

    if (this.welcomeCallback) {
      this.welcomeCallback(playerData);
    }
  }

  /**
   * Recieve server spawn
   * @param  {String} data Packet and data message
   */
  receiveSpawn(data) {
    log.debug('Messages - receiveSpawn()', data);

    if (this.spawnCallback) {
      this.spawnCallback(data);
    }
  }

  /**
   * Recieve equipment change
   * @param  {String} data Packet, equipment type, and equipment info message
   */
