import io from 'socket.io-client';
import log from '../lib/log';
import Messages from './messages';

/**
 * Connect to the server via websockets
 * @class
 */
export default class Socket {
  /**
   * Default constructor
   * @param {Game} instance of the game
   */
  constructor(game) {
    log.debug('Socket - constructor()', game);

    /**
    * Instance of the game
    * @type {Game}
    */
    this.game = game;

    /**
    * Pulls out the client configuration from the Game instance
    * @type {Object}
    */
    this.config = this.game.client.config;

    /**
    * IO websocket connection
    * @type {Object}
    */
    this.connection = null;

    /**
    * Whether or not this websocket is listening
    * @type {Boolean}
    */
    this.listening = false;

    /**
    * Whether or not this websocket has disconnected
    * @type {Boolean}
    */
    this.disconnected = false;

    /**
    * Array of messages sent from the nodeJS server to the client side game
    * @type {Messages[]}
    */
    this.messages = new Messages(this.game.client);
  }

  /**
   * Loads the connection, attempts to create the websocket
   */
  connect() {
    const protocol = this.config.ssl ? 'wss' : 'ws';
    const url = `${protocol}://${this.config.ip}:${this.config.port}`;

    log.debug('Socket - connect()', url);

    this.connection = null;

    this.connection = io(url, {
      forceNew: true,
      reconnection: false,
      upgrade: false,
      transports: ['websocket'],
    });

    // listens for server side errors
    this.connection.on('error', (error) => {
      log.debug('Socket - connect() - error', error);
    });

    // listens for websocket connection errors (ie db errors, server down, timeouts)
    this.connection.on('connect_error', (error) => {
      log.debug('Socket - connect() - server connection error', this.config.ip);
      log.error(error);

      this.listening = false;

      this.game.client.toggleLogin(false);
      this.game.client.sendError(null, 'Could not connect to the game server.');
    });

    // listens for socket connection attempts
    this.connection.on('connect', () => {
      log.debug('Socket - connect() - connecting to server', this.config.ip);
      this.listening = true;

      this.game.client.updateLoader('Preparing handshake...');
      this.connection.emit('client', {
        gVer: this.config.version,
        cType: 'HTML5',
      });
    });

    // listens for server side messages
    this.connection.on('message', (message) => {
      log.debug('Socket - connect() - message', message);
      this.receive(message);
    });

    // listens for a disconnect
    this.connection.on('disconnect', () => {
      log.debug('Socket - connect() - disconnecting');
      this.game.handleDisconnection();
    });
  }

  /**
   * Gets a message from the server
   * @param {Messages} message a single message object
   */
  receive(message) {
    log.debug('Socket - receive()', message);

    if (!this.listening) {
      return;
    }

    if (message.startsWith('[')) {
      const data = JSON.parse(message);

      if (data.length > 1) {
        this.messages.handleBulkData(data);
      } else {
        this.messages.handleData(JSON.parse(message).shift());
      }
    } else {
      this.messages.handleUTF8(message);
    }
  }

  /**
   * Sends a data packet from the client side to the server side
   * @param  {Packets} packet type of data to send to the server
   * @param  {Object} data    object which will be stringified as JSON before
   * it is sent to the server
   */
  send(packet, data) {
    log.debug('Socket - send()', packet, data);

    const json = JSON.stringify([packet, data]);

    if (this.connection && this.connection.connected) {
      this.connection.send(json);
    }
  }
}

import $ from 'jquery';
import Timer from '../../utils/timer';

/**
 * A type of bubble
 * @class
 */
export default class Blob {
  constructor(id, time, element, duration) {
    this.id = id;
    this.time = time;
    this.element = element;
    this.duration = duration || 5000;
    this.timer = new Timer(this.time, this.duration);
  }

  setClickable() {
    this.element.css('pointer-events', 'auto');
  }

  isOver(time) {
    return this.timer.isOver(time);
  }

  reset(time) {
    this.timer.time = time;
  }

  destroy() {
    $(this.element).remove();
  }
}

import Modules from '../utils/modules';

export default class Camera {
  constructor(renderer) {
    this.renderer = renderer;

    this.offset = 0.5;
    this.x = 0;
    this.y = 0;

    this.dX = 0;
    this.dY = 0;

    this.gridX = 0;
    this.gridY = 0;

    this.prevGridX = 0;
    this.prevGridY = 0;

    this.speed = 1;
    this.panning = false;
    this.centered = true;
    this.player = null;

    this.update();
  }

  update() {
    const factor = this.renderer.getUpscale();

    this.gridWidth = 15 * factor;
    this.gridHeight = 8 * factor;
  }

  setPosition(x, y) {
    this.x = x;
    this.y = y;

    this.prevGridX = this.gridX;
    this.prevGridY = this.gridY;

    this.gridX = Math.floor(x / 16);
    this.gridY = Math.floor(y / 16);
  }

  clip() {
    this.setGridPosition(Math.round(this.x / 16), Math.round(this.y / 16));
  }

  center() {
    if (this.centered) {
      return;
    }

    this.centered = true;
    this.centreOn(this.player);

    this.renderer.verifyCentration();
  }

  decenter() {
    if (!this.centered) {
      return;
    }

    this.clip();
    this.centered = false;

    this.renderer.verifyCentration();
  }

  setGridPosition(x, y) {
    this.prevGridX = this.gridX;
    this.prevGridY = this.gridY;

    this.gridX = x;
    this.gridY = y;

    this.x = this.gridX * 16;
    this.y = this.gridY * 16;
  }

  setPlayer(player) {
    this.player = player;

    this.centreOn(this.player);
  }

  handlePanning(direction) {
    if (!this.panning) {
      return;
    }

    switch (direction) {
      default:
        break;
      case Modules.Keys.Up:
        this.setPosition(this.x, this.y - 1);
        break;

      case Modules.Keys.Down:
        this.setPosition(this.x, this.y + 1);
        break;

      case Modules.Keys.Left:
        this.setPosition(this.x - 1, this.y);
        break;

      case Modules.Keys.Right:
        this.setPosition(this.x + 1, this.y);
        break;
    }
  }

  centreOn(entity) {
    if (!entity) {
      return;
    }

    const width = Math.floor(this.gridWidth / 2);
    const height = Math.floor(this.gridHeight / 2);

    this.x = entity.x - width * this.renderer.tileSize;
    this.y = entity.y - height * this.renderer.tileSize;

    this.gridX = Math.round(entity.x / 16) - width;
    this.gridY = Math.round(entity.y / 16) - height;
  }

  zone(direction) {
    switch (direction) {
      default:
        break;
      case Modules.Orientation.Up:
        this.setGridPosition(this.gridX, this.gridY - this.gridHeight + 2);
        break;

      case Modules.Orientation.Down:
        this.setGridPosition(this.gridX, this.gridY + this.gridHeight - 2);
        break;

      case Modules.Orientation.Right:
        this.setGridPosition(this.gridX + this.gridWidth - 2, this.gridY);
        break;

      case Modules.Orientation.Left:
        this.setGridPosition(this.gridX - this.gridWidth + 2, this.gridY);
        break;
    }
  }

  forEachVisiblePosition(callback, offset) {
    if (!offset) {
      offset = 1; // eslint-disable-line
    }

    for (let y = this.gridY - offset, maxY = y + this.gridHeight + offset * 2; y < maxY; y += 1) {
      for (let x = this.gridX - offset, maxX = x + this.gridWidth + offset * 2; x < maxX; x += 1) {
        callback(x, y);
      }
    }
  }
}

import _ from 'underscore';
import log from '../lib/log';

export default class Grids {
  constructor(map) {
    this.map = map;

    this.renderingGrid = [];
    this.pathingGrid = [];
    this.entityGrid = [];
    this.itemGrid = [];

    this.loadGrids();
  }

  loadGrids() {
    for (let i = 0; i < this.map.height; i += 1) {
      this.renderingGrid[i] = [];
      this.pathingGrid[i] = [];
      this.entityGrid[i] = [];
      this.itemGrid[i] = [];

      for (let j = 0; j < this.map.width; j += 1) {
        this.renderingGrid[i][j] = {};
        this.pathingGrid[i][j] = this.map.grid[i][j];
        this.entityGrid[i][j] = {};
        this.itemGrid[i][j] = {};
      }
    }

    log.debug('Finished loading preliminary grids.');
  }

  checkPathingGrid(player, xRadius, yRadius) {
    // mobile 1 = 15 * 8
    // desktop 2 = 30 x 16

    for (let y = player.gridY - yRadius; y < player.gridY + yRadius; y += 1) {
      for (let x = player.gridX - xRadius; x < player.gridX + xRadius; x += 1) {
        if (
          !this.map.isColliding(x, y)
          && _.size(this.entityGrid[y][x] === 0)
        ) this.removeFromPathingGrid(x, y);
      }
    }
  }

  resetPathingGrid() {
    this.pathingGrid = [];

    for (let i = 0; i < this.map.height; i += 1) {
      this.pathingGrid[i] = [];

      for (let j = 0; j < this.map.width; j += 1) this.pathingGrid[i][j] = this.map.grid[i][j];
    }
  }

  addToRenderingGrid(entity, x, y) {
    if (!this.map.isOutOfBounds(x, y)) this.renderingGrid[y][x][entity.id] = entity;
  }

  addToPathingGrid(x, y) {
    this.pathingGrid[y][x] = 1;
  }

  addToEntityGrid(entity, x, y) {
    if (entity && this.entityGrid[y][x]) this.entityGrid[y][x][entity.id] = entity;
  }

  addToItemGrid(item, x, y) {
    if (item && this.itemGrid[y][x]) this.itemGrid[y][x][item.id] = item;
  }

  removeFromRenderingGrid(entity, x, y) {
    if (
      entity
      && this.renderingGrid[y][x]
      && entity.id in this.renderingGrid[y][x]
    ) delete this.renderingGrid[y][x][entity.id];
  }

  removeFromPathingGrid(x, y) {
    this.pathingGrid[y][x] = 0;
  }

  removeFromEntityGrid(entity, x, y) {
    if (entity && this.entityGrid[y][x] && entity.id in this.entityGrid[y][x]) {
      delete this.entityGrid[y][x][entity.id];
    }
  }

  removeFromItemGrid(item, x, y) {
    if (item && this.itemGrid[y][x][item.id]) delete this.itemGrid[y][x][item.id];
  }

  removeEntity(entity) {
    if (entity) {
      this.removeFromEntityGrid(entity, entity.gridX, entity.gridY);
      this.removeFromPathingGrid(entity.gridX, entity.gridY);
      this.removeFromRenderingGrid(entity, entity.gridX, entity.gridY);

      if (entity.nextGridX > -1 && entity.nextGridY > -1) {
        this.removeFromEntityGrid(entity, entity.nextGridX, entity.nextGridY);
        this.removeFromPathingGrid(entity.nextGridX, entity.nextGridY);
      }
    }
  }
}

export default class Splat {
  constructor(id, type, text, x, y, statique) {
    this.id = id;
    this.type = type;
    this.text = text;
    this.x = x;
    this.y = y;

    this.statique = statique;

    this.opacity = 1.0;
    this.lastTime = 0;
    this.speed = 100;

    this.duration = 1000;
  }

  setColours(fill, stroke) {
    this.fill = fill;
    this.stroke = stroke;
  }

  setDuration(duration) {
    this.duration = duration;
  }

  tick() {
    if (!this.statique) {
      this.y -= 1;
    }

    this.opacity -= 70 / this.duration;

    if (this.opacity < 0) {
      this.destroy();
    }
  }

  update(time) {
    if (time - this.lastTime > this.speed) {
      this.lastTime = time;
      this.tick();
    }
  }

  destroy() {
    if (this.destroyCallback) {
      this.destroyCallback(this.id);
    }
  }

  onDestroy(callback) {
    this.destroyCallback = callback;
  }
}

export default class Pointer {
  constructor(id, element, type) {
    this.id = id;
    this.element = element;
    this.type = type;

    this.blinkInterval = null;
    this.visible = true;

    this.x = -1;
    this.y = -1;

    this.loadPointer();
  }

  loadPointer() {
    this.blinkInterval = setInterval(() => {
      if (this.visible) {
        this.hide();
      } else {
        this.show();
      }

      this.visible = !this.visible;
    }, 600);
  }

  destroy() {
    clearInterval(this.blinkInterval);
    this.element.remove();
  }

  setPosition(x, y) {
    this.x = x;
    this.y = y;
  }

  show() {
    this.element.css('display', 'block');
  }

  hide() {
    this.element.css('display', 'none');
  }
}

/* global document */
import _ from 'underscore';
import $ from 'jquery';
import log from '../lib/log';
import Camera from './camera';
import Tile from './tile';
import Character from '../entity/character/character';
import Item from '../entity/objects/item';
import Detect from '../utils/detect';
import { isIntersecting } from '../utils/util';

const getX = (index, width) => {
  if (index === 0) {
    return 0;
  }

  return index % width === 0
    ? width - 1
    : (index % width) - 1;
};

export default class Renderer {
  constructor(backgroundCanvas, entitiesCanvas, foregroundCanvas, textCanvas, cursorCanvas, game) {
    this.backgroundCanvas = backgroundCanvas;
    this.entitiesCanvas = entitiesCanvas;
    this.foregroundCanvas = foregroundCanvas;
    this.textCanvas = textCanvas;
    this.cursorCanvas = cursorCanvas;

    this.context = entitiesCanvas.getContext('2d');
    this.backContext = backgroundCanvas.getContext('2d');
    this.foreContext = foregroundCanvas.getContext('2d');
    this.textContext = textCanvas.getContext('2d');
    this.cursorContext = cursorCanvas.getContext('2d');

    this.context.imageSmoothingEnabled = false;
    this.backContext.imageSmoothingEnabled = false;
    this.foreContext.imageSmoothingEnabled = false;
    this.textContext.imageSmoothingEnabled = true;
    this.cursorContext.imageSmoothingEnabled = false;

    this.contexts = [this.backContext, this.foreContext, this.context];
    this.canvases = [
      this.backgroundCanvas,
      this.entitiesCanvas,
      this.foregroundCanvas,
      this.textCanvas,
      this.cursorCanvas,
    ];

    this.game = game;
    this.camera = null;
    this.entities = null;
    this.input = null;

    this.checkDevice();

    this.scale = 1;
    this.tileSize = 16;
    this.fontSize = 10;

    this.screenWidth = 0;
    this.screenHeight = 0;

    this.time = new Date();

    this.fps = 0;
    this.frameCount = 0;
    this.renderedFrame = [0, 0];
    this.lastTarget = [0, 0];

    this.animatedTiles = [];

    this.resizeTimeout = null;
    this.autoCentre = false;

    this.drawTarget = false;
    this.selectedCellVisible = false;

    this.stopRendering = false;
    this.animateTiles = true;
    this.debugging = false;
    this.brightness = 100;
    this.drawNames = true;
    this.drawLevels = false;
    this.forceRendering = false;
    this.textCanvas = $('#textCanvas');

    this.loadRenderer();
  }

  stop() {
    log.debug('Renderer - stop()');

    this.camera = null;
    this.input = null;
    this.stopRendering = true;

    this.forEachContext((context) => {
      context.fillStyle = '#12100D'; // eslint-disable-line
      context.fillRect(0, 0, context.canvas.width, context.canvas.height);
    });
  }

  loadRenderer() {
    log.debug('Renderer - loadRenderer()');

    this.scale = this.getScale();
    this.drawingScale = this.getDrawingScale();

    this.forEachContext((context) => {
      context.imageSmoothingEnabled = false; // eslint-disable-line
      context.webkitImageSmoothingEnabled = false; // eslint-disable-line
      context.mozImageSmoothingEnabled = false; // eslint-disable-line
      context.msImageSmoothingEnabled = false; // eslint-disable-line
      context.oImageSmoothingEnabled = false; // eslint-disable-line
    });
  }

  loadSizes() {
    // log.debug('Renderer - loadSizes()');

    if (!this.camera) {
      return;
    }

    this.screenWidth = this.camera.gridWidth * this.tileSize;
    this.screenHeight = this.camera.gridHeight * this.tileSize;

    const width = $('#container').width() - this.camera.gridWidth;
    const height = $('#container').height() - this.camera.gridHeight;

    this.forEachCanvas((canvas) => {
      canvas.width = width; // eslint-disable-line
      canvas.height = height; // eslint-disable-line
    });
  }

  loadCamera() {
    // log.debug('Renderer - loadCamera()');

    const { storage } = this.game;
    this.camera = new Camera(this);

    this.loadSizes();

    if (
      storage.data.new
      && (this.firefox
        || parseFloat(Detect.androidVersion()) < 6.0
        || parseFloat(Detect.iOSVersion() < 9.0)
        || Detect.isIpad())
    ) {
      this.camera.centered = false;

      storage.data.settings.centerCamera = false;
      storage.save();
    }
  }

  resize() {
    // log.debug('Renderer - resize()');

    this.stopRendering = true;

    this.clearAll();
    this.checkDevice();

    if (!this.resizeTimeout) {
      this.resizeTimeout = setTimeout(() => {
        this.scale = this.getScale();
        this.drawingScale = this.getDrawingScale();

        if (this.camera) {
          this.camera.update();
        }

        this.updateAnimatedTiles();

        this.loadSizes();

        if (this.entities) {
          this.entities.update();
        }

        if (this.map) {
          this.map.updateTileset();
        }

        if (this.camera) {
          this.camera.centreOn(this.game.player);
        }

        if (this.game.interface) {
          this.game.interface.resize();
        }

        this.renderedFrame[0] = -1;
        this.stopRendering = false;
        this.resizeTimeout = null;
      }, 500);
    }
  }

  render() {
    // log.debug('Renderer - render()');

    if (this.stopRendering) {
      return;
    }

    this.clearScreen(this.context);
    this.clearText();
    this.saveAll();

    /**
     * Rendering related draws
     */
    this.draw();
    this.drawAnimatedTiles();

    // the annoying square under the cursor
    // this.drawTargetCell();

    this.drawSelectedCell();
    this.drawEntities();
    this.drawInfos();
    this.drawDebugging();
    this.restoreAll();
    this.drawCursor();
  }

  /**
   * Context Drawing
   */

  draw() {
    // log.debug('Renderer - draw()');

    if (this.hasRenderedFrame()) {
      // log.debug('has rendered rate', this.hasRenderedFrame());
      return;
    }

    this.clearDrawing();
    this.updateDrawingView();

    this.forEachVisibleTile((id, index) => {
      const isHighTile = this.map.isHighTile(id);
      const context = isHighTile ? this.foreContext : this.backContext;

      if (!this.map.isAnimatedTile(id) || !this.animateTiles) {
        this.drawTile(
          context,
          id,
          this.tileset,
          this.tileset.width / this.tileSize,
          this.map.width,
          index,
        );
      }
    });

    this.saveFrame();
  }

  drawAnimatedTiles() {
    // log.debug('Renderer - drawAnimatedTiles()');

    this.setCameraView(this.context);

    if (!this.animateTiles) {
      return;
    }

    this.forEachAnimatedTile((tile) => {
      this.drawTile(
        this.context,
        tile.id,
        this.tileset,
        this.tileset.width / this.tileSize,
        this.map.width,
        tile.index,
      );
      tile.loaded = true; // eslint-disable-line
    });
  }

  drawInfos() {
    // log.debug('Renderer - drawInfos()');

    if (this.game.info.getCount() === 0) {
      return;
    }

    this.game.info.forEachInfo((info) => {
      const factor = this.mobile ? 2 : 1;

      this.textContext.save();
      this.textContext.font = '24px sans serif';
      this.setCameraView(this.textContext);
      this.textContext.globalAlpha = info.opacity;
      this.drawText(
        `${info.text}`,
        Math.floor((info.x + 8) * factor),
        Math.floor(info.y * factor),
        true,
        info.fill,
        info.stroke,
      );
      this.textContext.restore();
    });
  }

  drawDebugging() {
    // log.debug('Renderer - drawDebugging()');

    if (!this.debugging) {
      return;
    }

    this.drawFPS();

    if (!this.mobile) {
      this.drawPosition();
      this.drawPathing();
    }
  }

  drawEntities() {
    // log.debug('Renderer - drawEntities()');

    this.forEachVisibleEntity((entity) => {
      if (entity.spriteLoaded) {
        // log.debug('drawEntities', entity);
        this.drawEntity(entity);
      }
    });
  }

  drawEntity(entity) {
    // log.debug('Renderer - drawEntity()', entity);

    const { sprite } = entity;
    const animation = entity.currentAnimation;
    const data = entity.renderingData;

    if (!sprite || !animation || !entity.isVisible()) {
      return;
    }

    const frame = animation.currentFrame;
    const x = frame.x * this.drawingScale;
    const y = frame.y * this.drawingScale;
    const dx = entity.x * this.drawingScale;
    const dy = entity.y * this.drawingScale;
    const flipX = dx + this.tileSize * this.drawingScale;
    const flipY = dy + data.height;

    this.context.save();

    if (data.scale !== this.scale || data.sprite !== sprite) {
      data.scale = this.scale;
      data.sprite = sprite;
      data.width = sprite.width * this.drawingScale;
      data.height = sprite.height * this.drawingScale;
      data.ox = sprite.offsetX * this.drawingScale;
      data.oy = sprite.offsetY * this.drawingScale;

      if (entity.angled) {
        data.angle = (entity.angle * Math.PI) / 180;
      }

      if (entity.hasShadow()) {
        data.shadowWidth = this.shadowSprite.width * this.drawingScale;
        data.shadowHeight = this.shadowSprite.height * this.drawingScale;
        data.shadowOffsetY = entity.shadowOffsetY * this.drawingScale;
      }
    }

    if (entity.fading) {
      this.context.globalAlpha = entity.fadingAlpha;
    }

    if (entity.spriteFlipX) {
      this.context.translate(flipX, dy);
      this.context.scale(-1, 1);
    } else if (entity.spriteFlipY) {
      this.context.translate(dx, flipY);
      this.context.scale(1, -1);
    } else this.context.translate(dx, dy);

    if (entity.angled) {
      this.context.rotate(data.angle);
    }

    if (entity.hasShadow()) {
      this.context.drawImage(
        this.shadowSprite.image,
        0,
        0,
        data.shadowWidth,
        data.shadowHeight,
        0,
        data.shadowOffsetY,
        data.shadowWidth,
        data.shadowHeight,
      );
    }

    this.drawEntityBack(entity);

    this.context.drawImage(
      sprite.image,
      x,
      y,
      data.width,
      data.height,
      data.ox,
      data.oy,
      data.width,
      data.height,
    );

    if (
      entity instanceof Character
      && !entity.dead
      && !entity.teleporting
      && entity.hasWeapon()
    ) {
      const weaponSprite = this.entities.getSprite(entity.weapon.getName());

      if (weaponSprite) {
        if (!weaponSprite.loaded) {
          weaponSprite.loadSprite();
        }

        const weaponAnimationData = weaponSprite.animationData[animation.name];

        const index = frame.index < weaponAnimationData.length
          ? frame.index
          : frame.index % weaponAnimationData.length;


        const weaponX = weaponSprite.width * index * this.drawingScale;
        const weaponY = weaponSprite.height * animation.row * this.drawingScale;
        const weaponWidth = weaponSprite.width * this.drawingScale;
        const weaponHeight = weaponSprite.height * this.drawingScale;

        this.context.drawImage(
          weaponSprite.image,
          weaponX,
          weaponY,
          weaponWidth,
          weaponHeight,
          weaponSprite.offsetX * this.drawingScale,
          weaponSprite.offsetY * this.drawingScale,
          weaponWidth,
          weaponHeight,
        );
      }
    }

    if (entity instanceof Item) {
      const {
        sparksAnimation,
      } = this.entities.sprites;
      const sparksFrame = sparksAnimation.currentFrame;

      if (data.scale !== this.scale) {
        data.sparksX = this.sparksSprite.width * sparksFrame.index * this.drawingScale;
        data.sparksY = this.sparksSprite.height * sparksAnimation.row * this.drawingScale;

        data.sparksWidth = this.sparksSprite.width * this.drawingScale;
        data.sparksHeight = this.sparksSprite.height * this.drawingScale;
      }

      this.context.drawImage(
        this.sparksSprite.image,
        data.sparksX,
        data.sparksY,
        data.sparksWidth,
        data.sparksHeight,
        0,
        0,
        data.sparksWidth,
        data.sparksHeight,
      );
    }

    this.drawEntityFore(entity);

    this.context.restore();

    this.drawHealth(entity);
    this.drawName(entity);
  }

  /**
   * Function used to draw special effects prior
   * to rendering the entity.
   */
  drawEntityBack(entity) {
    // const self = this;
    // @TODO
  }

  drawEntityFore(entity) {
    // log.debug('Renderer - drawEntityFore()');

    /**
     * Function used to draw special effects after
     * having rendererd the entity
     */

    if (
      entity.terror
      || entity.stunned
      || entity.critical
      || entity.explosion
    ) {
      const sprite = this.entities.getSprite(entity.getActiveEffect());

      if (!sprite.loaded) {
        sprite.loadSprite();
      }

      if (sprite) {
        const animation = entity.getEffectAnimation();
        const { index } = animation.currentFrame;
        const x = sprite.width * index * this.drawingScale;
        const y = sprite.height * animation.row * this.drawingScale;
        const width = sprite.width * this.drawingScale;
        const height = sprite.height * this.drawingScale;
        const offsetX = sprite.offsetX * this.drawingScale;
        const offsetY = sprite.offsetY * this.drawingScale;

        this.context.drawImage(
          sprite.image,
          x,
          y,
          width,
          height,
          offsetX,
          offsetY,
          width,
          height,
        );
      }
    }
  }

  drawHealth(entity) {
    // log.debug('Renderer - entity()');

    if (!entity.hitPoints || entity.hitPoints < 0 || !entity.healthBarVisible) {
      return;
    }

    const barLength = 16;
    const healthX = entity.x * this.drawingScale - barLength / 2 + 8;
    const healthY = (entity.y - 9) * this.drawingScale;

    const healthWidth = Math.round(
      (entity.hitPoints / entity.maxHitPoints)
      * barLength
      * this.drawingScale,
    );

    const healthHeight = 2 * this.drawingScale;

    this.context.save();
    this.context.strokeStyle = '#00000';
    this.context.lineWidth = 1;
    this.context.strokeRect(
      healthX,
      healthY,
      barLength * this.drawingScale,
      healthHeight,
    );
    this.context.fillStyle = '#FD0000';
    this.context.fillRect(healthX, healthY, healthWidth, healthHeight);
    this.context.restore();
  }

  drawName(entity) {
    // log.debug('Renderer - drawName()');

    if (entity.hidden || (!this.drawNames && !this.drawLevels)) {
      return;
    }

    let colour = entity.wanted ? 'red' : 'white';
    const factor = this.mobile ? 2 : 1;

    if (entity.rights > 1) {
      colour = '#ba1414';
    } else if (entity.rights > 0) {
      colour = '#a59a9a';
    }

    if (entity.id === this.game.player.id) {
      colour = '#fcda5c';
    }

    this.textContext.save();
    this.setCameraView(this.textContext);
    this.textContext.font = '14px sans serif';

    if (!entity.hasCounter) {
      if (this.drawNames && entity !== 'player') {
        this.drawText(
          entity.username,
          (entity.x + 8) * factor,
          (entity.y - (this.drawLevels ? 20 : 10)) * factor,
          true,
          colour,
        );
      }

      if (
        this.drawLevels
        && (entity.type === 'mob' || entity.type === 'player')
      ) {
        this.drawText(
          `Level ${entity.level}`,
          (entity.x + 8) * factor,
          (entity.y - 10) * factor,
          true,
          colour,
        );
      }

      if (entity.type === 'item' && entity.count > 1) {
        this.drawText(
          entity.count,
          (entity.x + 8) * factor,
          (entity.y - 10) * factor,
          true,
          colour,
        );
      }
    } else {
      if (this.game.time - entity.countdownTime > 1000) {
        entity.countdownTime = this.game.time; // eslint-disable-line
        entity.counter -= 1; // eslint-disable-line
      }

      if (entity.counter <= 0) {
        entity.hasCounter = false; // eslint-disable-line
      }

      this.drawText(
        entity.counter,
        (entity.x + 8) * factor,
        (entity.y - 10) * factor,
        true,
        colour,
      );
    }

    this.textContext.restore();
  }

  drawCursor() {
    // log.debug('Renderer - drawCursor()');

    if (this.tablet || this.mobile) {
      return;
    }

    const { cursor } = this.input;

    this.clearScreen(this.cursorContext);
    this.cursorContext.save();

    if (cursor && this.scale > 1) {
      if (!cursor.loaded) {
        cursor.loadSprite();
      }

      if (cursor.loaded) {
        this.cursorContext.drawImage(
          cursor.image,
          0,
          0,
          14 * this.drawingScale,
          14 * this.drawingScale,
          this.input.mouse.x,
          this.input.mouse.y,
          14 * this.drawingScale,
          14 * this.drawingScale,
        );
      }
    }

    this.cursorContext.restore();
  }

  drawFPS() {
    // log.debug('Renderer - drawFPS()');

    const currentTime = new Date();
    const timeDiff = currentTime - this.time;

    if (timeDiff >= 1000) {
      this.realFPS = this.frameCount;
      this.frameCount = 0;
      this.time = currentTime;
      this.fps = this.realFPS;
    }

    this.frameCount += 1;

    this.drawText(`FPS: ${this.realFPS}`, 10, 11, false, 'white');
  }

  drawPosition() {
    // log.debug('Renderer - drawPosition()');

    const { player } = this.game;

    this.drawText(
      `x: ${player.gridX} y: ${player.gridY}`,
      10,
      31,
      false,
      'white',
    );
  }

  drawPathing() {
    // log.debug('Renderer - drawPathing()');

    const { pathingGrid } = this.entities.grids;

    if (!pathingGrid) {
      return;
    }

    this.camera.forEachVisiblePosition((x, y) => {
      if (x < 0 || y < 0) {
        return;
      }

      if (pathingGrid[y][x] !== 0) {
        this.drawCellHighlight(x, y, 'rgba(50, 50, 255, 0.5)');
      }
    });
  }

  drawSelectedCell() {
    // log.debug('Renderer - drawSelectedCell()');

    if (!this.input.selectedCellVisible) {
      return;
    }

    const posX = this.input.selectedX;
    const posY = this.input.selectedY;

    // only draw the highlight cell if they are not adjacent
    // from character's current position
    if (!this.game.player.isPositionAdjacent(posX, posY)) {
      this.drawCellHighlight(posX, posY, this.input.mobileTargetColour);
    }
  }

  /**
   * Primitive drawing functions
   */

  drawTile(context, tileId, tileset, setWidth, gridWidth, cellId) {
    // log.debug('Renderer - draw()', context, tileId, tileset, setWidth, gridWidth, cellId);

    if (tileId === -1) {
      return;
    }

    this.drawScaledImage(
      context,
      tileset,
      getX(tileId + 1, setWidth / this.drawingScale) * this.tileSize,
      Math.floor(tileId / (setWidth / this.drawingScale)) * this.tileSize,
      this.tileSize,
      this.tileSize,
      getX(cellId + 1, gridWidth) * this.tileSize,
      Math.floor(cellId / gridWidth) * this.tileSize,
    );
  }

  clearTile(context, gridWidth, cellId) {
    // log.debug('Renderer - clearTile()', context, gridWidth, cellId);

    const x = getX(cellId + 1, gridWidth) * this.tileSize * this.drawingScale;
    const y = Math.floor(cellId / gridWidth) * this.tileSize * this.drawingScale;
    const w = this.tileSize * this.scale;

    context.clearRect(x, y, w, w);
  }

  drawText(text, x, y, centered, colour, strokeColour) {
    // log.debug('Renderer - drawText()', text, x, y, centered, colour, strokeColour);

    let strokeSize = 1;
    const context = this.textContext;

    if (this.scale > 2) {
      strokeSize = 3;
    }

    if (text && x && y) {
      context.save();

      if (centered) {
        context.textAlign = 'center';
      }

      context.strokeStyle = strokeColour || '#373737';
      context.lineWidth = strokeSize;
      context.strokeText(text, x * this.scale, y * this.scale);
      context.fillStyle = colour || 'white';
      context.fillText(text, x * this.scale, y * this.scale);

      context.restore();
    }
  }

  drawScaledImage(context, image, x, y, width, height, dx, dy) {
    // log.debug('Renderer - drawScaledImage()', context, image, x, y, width, height, dx, dy);

    if (!context) {
      return;
    }

    context.drawImage(
      image,
      x * this.drawingScale,
      y * this.drawingScale,
      width * this.drawingScale,
      height * this.drawingScale,
      dx * this.drawingScale,
      dy * this.drawingScale,
      width * this.drawingScale,
      height * this.drawingScale,
    );
  }

  updateAnimatedTiles() {
    // log.debug('Renderer - updateAnimatedTiles()');

    if (!this.animateTiles) {
      return;
    }

    const newTiles = [];

    this.forEachVisibleTile((id, index) => {
      /**
       * We don't want to reinitialize animated tiles that already exist
       * and are within the visible camera proportions. This way we can parse
       * it every time the tile moves slightly.
       */

      if (!this.map.isAnimatedTile(id)) {
        return;
      }

      /**
       * Push the pre-existing tiles.
       */

      const tileIndex = this.animatedTiles.indexOf(id);

      if (tileIndex > -1) {
        newTiles.push(this.animatedTiles[tileIndex]);
        return;
      }

      const tile = new Tile(
        id,
        index,
        this.map.getTileAnimationLength(id),
        this.map.getTileAnimationDelay(id),
      );

      const position = this.map.indexToGridPosition(tile.index);

      tile.setPosition(position);
      newTiles.push(tile);
    }, 2);

    this.animatedTiles = newTiles;
  }

  checkDirty(rectOne, source, x, y) {
    // log.debug('Renderer - checkDirty()', rectOne, source, x, y);

    this.entities.forEachEntityAround(x, y, 2, (entityTwo) => {
      if (source && source.id && entityTwo.id === source.id) return;

      if (!entityTwo.isDirty && isIntersecting(rectOne, this.getEntityBounds(entityTwo))) {
        entityTwo.loadDirty();
      }
    });

    if (source && !source.hasOwnProperty('index')) { // eslint-disable-line
      this.forEachAnimatedTile((tile) => {
        if (!tile.isDirty && isIntersecting(rectOne, this.getTileBounds(tile))) {
          tile.dirty = true; // eslint-disable-line
        }
      });
    }

    if (!this.drawTarget && this.input.selectedCellVisible) {
      const targetRect = this.getTargetBounds();

      if (isIntersecting(rectOne, targetRect)) {
        this.drawTarget = true;
        this.targetRect = targetRect;
      }
    }
  }

  drawCellRect(x, y, colour) {
    // log.debug('Renderer - drawCellRect()', x, y, colour);

    const multiplier = this.tileSize * this.drawingScale;

    this.context.save();
    this.context.lineWidth = 2 * this.drawingScale;
    this.context.translate(x + 2, y + 2);
    this.context.strokeStyle = colour;
    this.context.strokeRect(0, 0, multiplier - 4, multiplier - 4);
    this.context.restore();
  }

  drawCellHighlight(x, y, colour) {
    // log.debug('Renderer - drawCellHighlight()', x, y, colour);

    this.drawCellRect(
      x * this.drawingScale * this.tileSize,
      y * this.drawingScale * this.tileSize,
      colour,
    );
  }

  drawTargetCell() {
    // log.debug('Renderer - drawTargetCell()');

    if (
      this.mobile
      || this.tablet
      || !this.input.targetVisible
      || !this.input
      || !this.camera
    ) {
      return;
    }

    const location = this.input.getCoords();

    if (
      !(
        location.x === this.input.selectedX
        && location.y === this.input.selectedY
      )
    ) {
      this.drawCellHighlight(location.x, location.y, this.input.targetColour);
    }
  }

  /**
   * Primordial Rendering functions
   */

  forEachVisibleIndex(callback, offset) {
    // log.debug('Renderer - forEachVisibleIndex()', callback, offset);

    this.camera.forEachVisiblePosition((x, y) => {
      if (!this.map.isOutOfBounds(x, y)) callback(this.map.gridPositionToIndex(x, y) - 1);
    }, offset);
  }

  forEachVisibleTile(callback, offset) {
    // log.debug('Renderer - forEachVisibleTile()', callback, offset);

    if (!this.map || !this.map.mapLoaded) {
      return;
    }

    this.forEachVisibleIndex((index) => {
      if (_.isArray(this.map.data[index])) {
        _.each(this.map.data[index], (id) => {
          callback(id - 1, index);
        });
      } else if (!isNaN(this.map.data[index] - 1)) { // eslint-disable-line
        callback(this.map.data[index] - 1, index);
      }
    }, offset);
  }

  forEachAnimatedTile(callback) {
    // log.debug('Renderer - forEachAnimatedTile()', callback);

    _.each(this.animatedTiles, (tile) => {
      callback(tile);
    });
  }

  forEachVisibleEntity(callback) {
    // log.debug('Renderer - forEachVisibleEntity()', callback);

    if (!this.entities || !this.camera) {
      return;
    }

    const { grids } = this.entities;

    this.camera.forEachVisiblePosition((x, y) => {
      if (!this.map.isOutOfBounds(x, y) && grids.renderingGrid[y][x]) {
        _.each(grids.renderingGrid[y][x], (entity) => {
          callback(entity);
        });
      }
    });
  }

  isVisiblePosition(x, y) {
    // log.debug('Renderer - isVisiblePosition()', x, y);

    return (
      y >= this.camera.gridY
      && y < this.camera.gridY + this.camera.gridHeight
      && x >= this.camera.gridX
      && x < this.camera.gridX + this.camera.gridWidth
    );
  }

  getScale() {
    // log.debug('Renderer - getScale()');

    return this.game.getScaleFactor();
  }

  getDrawingScale() {
    // log.debug('Renderer - getDrawingScale()');

    let scale = this.getScale();

    if (this.mobile) {
      scale = 2;
    }

    return scale;
  }

  getUpscale() {
    // log.debug('Renderer - getUpscale()');

    let scale = this.getScale();

    if (scale > 2) {
      scale = 2;
    }

    return scale;
  }

  clearContext() {
    // log.debug('Renderer - clearContext()');

    this.context.clearRect(
      0,
      0,
      this.screenWidth * this.scale,
      this.screenHeight * this.scale,
    );
  }

  clearText() {
    // log.debug('Renderer - clearText()');

    this.textContext.clearRect(
      0,
      0,
      this.textCanvas.width * this.scale,
      this.textCanvas.height * this.scale,
    );
  }

  restore() {
    // log.debug('Renderer - restore()');

    this.forEachContext((context) => {
      context.restore();
    });
  }

  clearAll() {
    // log.debug('Renderer - clearAll()');

    this.forEachContext((context) => {
      context.clearRect(0, 0, context.canvas.width, context.canvas.height);
    });
  }

  clearDrawing() {
    // log.debug('Renderer - clearDrawing()');

    this.forEachDrawingContext((context) => {
      context.clearRect(0, 0, context.canvas.width, context.canvas.height);
    });
  }

  saveAll() {
    // log.debug('Renderer - saveAll()');

    this.forEachContext((context) => {
      context.save();
    });
  }

  restoreAll() {
    // log.debug('Renderer - restoreAll()');

    this.forEachContext((context) => {
      context.restore();
    });
  }

  focus() {
    // log.debug('Renderer - focus()');

    this.forEachContext((context) => {
      context.focus();
    });
  }

  /**
   * Rendering Functions
   */

  updateView() {
    // log.debug('Renderer - updateView()');

    this.forEachContext((context) => {
      this.setCameraView(context);
    });
  }

  updateDrawingView() {
    // log.debug('Renderer - updateDrawingView()');

    this.forEachDrawingContext((context) => {
      this.setCameraView(context);
    });
  }

  setCameraView(context) {
    // log.debug('Renderer - setCameraView()');

    if (!this.camera || this.stopRendering) {
      return;
    }

    context.translate(
      -this.camera.x * this.drawingScale,
      -this.camera.y * this.drawingScale,
    );
  }

  clearScreen(context) {
    // log.debug('Renderer - clearScreen()', context);

    context.clearRect(
      0,
      0,
      this.context.canvas.width,
      this.context.canvas.height,
    );
  }

  hasRenderedFrame() {
    // log.debug('Renderer - hasRenderedFrame()');

    if (this.forceRendering) {
      return false;
    }

    if (!this.camera || this.stopRendering || !this.input) {
      return true;
    }

    return (
      this.renderedFrame[0] === this.camera.x
      && this.renderedFrame[1] === this.camera.y
    );
  }

  saveFrame() {
    // log.debug('Renderer - saveFrame()');

    if (!this.hasRenderedFrame()) {
      this.renderedFrame[0] = this.camera.x;
      this.renderedFrame[1] = this.camera.y;
    }
  }

  adjustBrightness(level) {
    // log.debug('Renderer - adjustBrightness()', level);

    if (level < 0 || level > 100) {
      return;
    }

    this.textCanvas.css(
      'background',
      `rgba(0, 0, 0, ${0.5 - level / 200})`,
    );
  }

  loadStaticSprites() {
    log.debug('Renderer - loadStaticSprites()');

    this.shadowSprite = this.entities.getSprite('shadow16');

    // if (!this.shadowSprite.loaded) {
    //   this.shadowSprite.loadSprite();
    // }

    this.sparksSprite = this.entities.getSprite('sparks');

    // if (!this.sparksSprite.loaded) {
    //   this.sparksSprite.loadSprite();
    // }
  }

  /**
   * Miscellaneous functions
   */

  forEachContext(callback) {
    // log.debug('Renderer - forEachContext()', callback);

    _.each(this.contexts, (context) => {
      callback(context);
    });
  }

  forEachDrawingContext(callback) {
    // log.debug('Renderer - forEachDrawingContext()', callback);

    _.each(this.contexts, (context) => {
      if (context.canvas.id !== 'entities') {
        callback(context);
      }
    });
  }

  forEachCanvas(callback) {
    // log.debug('Renderer - forEachCanvas()', callback);

    _.each(this.canvases, (canvas) => {
      callback(canvas);
    });
  }

  checkDevice() {
    // log.debug('Renderer - checkDevice()');

    this.mobile = this.game.client.isMobile();
    this.tablet = this.game.client.isTablet();
    this.firefox = Detect.isFirefox();
  }

  verifyCentration() {
    // log.debug('Renderer - verifyCentration()');

    this.forceRendering = (this.mobile || this.tablet) && this.camera.centered;
  }

  isPortableDevice() {
    // log.debug('Renderer - isPortableDevice()');

    return this.mobile || this.tablet;
  }

  /**
   * Setters
   */

  setTileset(tileset) {
    log.debug('Renderer - setTileset()', tileset);

    this.tileset = tileset;
  }

  setMap(map) {
    // log.debug('Renderer - setMap()', map);

    this.map = map;
  }

  setEntities(entities) {
    // log.debug('Renderer - entities()', entities);

    this.entities = entities;
  }

  setInput(input) {
    // log.debug('Renderer - setInput()', input);

    this.input = input;
  }

  /**
   * Getters
   */

  getTileBounds(tile) {
    // log.debug('Renderer - getTileBounds()', tile);

    const bounds = {};
    const cellId = tile.index;

    bounds.x = (getX(cellId + 1, this.map.width) * this.tileSize
        - this.camera.x)
      * this.drawingScale;
    bounds.y = (Math.floor(cellId / this.map.width) * this.tileSize - this.camera.y)
      * this.drawingScale;
    bounds.width = this.tileSize * this.drawingScale;
    bounds.height = this.tileSize * this.drawingScale;
    bounds.left = bounds.x;
    bounds.right = bounds.x + bounds.width;
    bounds.top = bounds.y;
    bounds.bottom = bounds.y + bounds.height;

    return bounds;
  }

  getEntityBounds(entity) {
    // log.debug('Renderer - getEntityBounds()', entity);

    const bounds = {};
    const { sprite } = entity;

    // TODO - Ensure that the sprite over there has the correct bounds

    if (!sprite) {
      log.error(`Sprite malformation for: ${entity.name}`);
    } else {
      bounds.x = (entity.x + sprite.offsetX - this.camera.x) * this.drawingScale;
      bounds.y = (entity.y + sprite.offsetY - this.camera.y) * this.drawingScale;
      bounds.width = sprite.width * this.drawingScale;
      bounds.height = sprite.height * this.drawingScale;
      bounds.left = bounds.x;
      bounds.right = bounds.x + bounds.width;
      bounds.top = bounds.y;
      bounds.bottom = bounds.y + bounds.height;
    }

    return bounds;
  }

  getTargetBounds(x, y) {
    // log.debug('Renderer - getTargetBounds()', x, y);

    const bounds = {};
    const tx = x || this.input.selectedX;
    const ty = y || this.input.selectedY;

    bounds.x = (tx * this.tileSize - this.camera.x) * this.drawingScale;
    bounds.y = (ty * this.tileSize - this.camera.y) * this.drawingScale;
    bounds.width = this.tileSize * this.drawingScale;
    bounds.height = this.tileSize * this.drawingScale;
    bounds.left = bounds.x;
    bounds.right = bounds.x + bounds.width;
    bounds.top = bounds.y;
    bounds.bottom = bounds.y + bounds.height;

    return bounds;
  }

  getTileset() {
    // log.debug('Renderer - getTileset()');

    return this.tileset;
  }
}

export default class Tile {
  constructor(id, index, length, speed) {
    this.initialId = id;
    this.id = id;
    this.index = index;
    this.length = length;
    this.speed = speed;
    this.lastTime = 0;
    this.loaded = false;
  }

  setPosition(position) {
    this.x = position.x;
    this.y = position.y;
  }

  tick() {
    this.id = this.id - this.initialId < this.length - 1
      ? this.id + 1
      : this.initialId;
  }

  animate(time) {
    if (time - this.lastTime > this.speed) {
      this.tick();
      this.lastTime = time;
      return true;
    }

    return false;
  }

  getPosition() {
    if (this.x && this.y) {
      return [this.x, this.y];
    }

    return [-1, -1];
  }
}

import Modules from '../utils/modules';
import Character from '../entity/character/character';

export default class Updater {
  constructor(game) {
    this.game = game;
    this.camera = game.getCamera();
    this.renderer = game.renderer;
    this.input = game.input;
    this.sprites = null;
  }

  update() {
    this.timeDifferential = (new Date() - this.lastUpdate) / 1000;

    this.animateTiles();
    this.updateEntities();
    this.input.updateCursor();
    this.updateKeyboard();
    this.updateAnimations();
    this.verifyScale();
    this.updateInfos();
    this.updateBubbles();

    this.lastUpdate = new Date();
  }

  animateTiles() {
    const {
      time,
    } = this.game;

    this.renderer.forEachAnimatedTile((tile) => {
      if (tile.animate(time)) {
        tile.isDirty = true; // eslint-disable-line
        tile.dirtyRect = this.renderer.getTileBounds(tile); // eslint-disable-line
      }
    });
  }

  updateEntities() {
    this.game.entities.forEachEntity((entity) => {
      if (entity.spriteLoaded) {
        this.updateFading(entity);

        const animation = entity.currentAnimation;

        if (animation) animation.update(this.game.time);

        if (entity instanceof Character) {
          if (entity.critical && entity.criticalAnimation) {
            entity.criticalAnimation.update(this.game.time);
          }

          if (entity.terror && entity.terrorAnimation) {
            entity.terrorAnimation.update(this.game.time);
          }

          if (entity.stunned && entity.stunAnimation) {
            entity.stunAnimation.update(this.game.time);
          }

          if (entity.explosion && entity.explosionAnimation) {
            entity.explosionAnimation.update(this.game.time);
          }

          if (entity.movement && entity.movement.inProgress) {
            entity.movement.step(this.game.time);
          }

          if (entity.hasPath() && !entity.movement.inProgress) {
            const tick = Math.round(266 / entity.movementSpeed);

            switch (entity.orientation) {
              case Modules.Orientation.Left:
                entity.movement.start(
                  this.game.time,
                  (x) => {
                    entity.x = x; // eslint-disable-line
                    entity.moved();
                  },
                  () => {
                    entity.x = entity.movement.endValue; // eslint-disable-line
                    entity.moved();
                    entity.nextStep();
                  },
                  entity.x - tick,
                  entity.x - 16,
                  entity.movementSpeed,
                );

                break;

              case Modules.Orientation.Right:
                entity.movement.start(
                  this.game.time,
                  (x) => {
                    entity.x = x; // eslint-disable-line
                    entity.moved();
                  },
                  () => {
                    entity.x = entity.movement.endValue; // eslint-disable-line
                    entity.moved();
                    entity.nextStep();
                  },
                  entity.x + tick,
                  entity.x + 16,
                  entity.movementSpeed,
                );

                break;

              case Modules.Orientation.Up:
                entity.movement.start(
                  this.game.time,
                  (y) => {
                    entity.y = y; // eslint-disable-line
                    entity.moved();
                  },
                  () => {
                    entity.y = entity.movement.endValue; // eslint-disable-line
                    entity.moved();
                    entity.nextStep();
                  },
                  entity.y - tick,
                  entity.y - 16,
                  entity.movementSpeed,
                );

                break;

              case Modules.Orientation.Down:
                entity.movement.start(
                  this.game.time,
                  (y) => {
                    entity.y = y; // eslint-disable-line
                    entity.moved();
                  },
                  () => {
                    entity.y = entity.movement.endValue; // eslint-disable-line
                    entity.moved();
                    entity.nextStep();
                  },
                  entity.y + tick,
                  entity.y + 16,
                  entity.movementSpeed,
                );

                break;
              default:
                break;
            }
          }
        } else if (entity.type === 'projectile') {
          const mDistance = entity.speed * this.timeDifferential;
          const dx = entity.destX - entity.x;
          const dy = entity.destY - entity.y;
          const tDistance = Math.sqrt(dx * dx + dy * dy);
          let amount = mDistance / tDistance;

          if (amount > 1) {
            amount = 1;
          }

          entity.x += dx * amount; // eslint-disable-line
          entity.y += dy * amount; // eslint-disable-line

          if (tDistance < 5) entity.impact();
        }
      }
    });
  }

  updateFading(entity) {
    if (!entity || !entity.fading) {
      return;
    }

    const duration = 1000;
    const {
      time,
    } = this.game;
    const dt = time - entity.fadingTime;

    if (dt > duration) {
      entity.isFading = false; // eslint-disable-line
      entity.fadingAlpha = 1; // eslint-disable-line
    } else {
      entity.fadingAlpha = dt / duration; // eslint-disable-line
    }
  }

  updateKeyboard() {
    const {
      player,
    } = this.game;

    const position = {
      x: player.gridX,
      y: player.gridY,
    };

    if (player.frozen) {
      return;
    }

    if (player.moveUp) {
      position.y -= 1;
    } else if (player.moveDown) {
      position.y += 1;
    } else if (player.moveRight) {
      position.x += 1;
    } else if (player.moveLeft) {
      position.x -= 1;
    }

    if (player.hasKeyboardMovement()) {
      this.input.keyMove(position);
    }
  }

  updateAnimations() {
    const target = this.input.targetAnimation;

    // stops animation from updating on targetAnimation
    if (target && this.input.selectedCellVisible && !this.renderer.mobile) {
      target.update(this.game.time);
    }

    if (!this.sprites) {
      return;
    }

    const sparks = this.sprites.sparksAnimation;

    if (sparks) {
      sparks.update(this.game.time);
    }
  }

  verifyScale() {
    const scale = this.renderer.getDrawingScale();

    if (this.renderer.tileset && this.renderer.tileset.scale !== scale) {
      this.game.map.updateTileset();
    }
  }

  updateInfos() {
    if (this.game.info) {
      this.game.info.update(this.game.time);
    }
  }

  updateBubbles() {
    if (this.game.bubble) {
      this.game.bubble.update(this.game.time);
    }

    if (this.game.pointer) {
      this.game.pointer.update();
    }
  }

  setSprites(sprites) {
    this.sprites = sprites;
  }
}

const Detect = {
  isWindows: jest.fn().mockReturnValue(true),
  isOpera: jest.fn().mockReturnValue(true),
  isFirefoxAndroid: jest.fn().mockReturnValue(true),
};

export default Detect;

/* global navigator, window */
const userAgentContains = string => navigator.userAgent.indexOf(string) !== -1;

const Detect = {
  isIpad: () => /ipad/i.test(navigator.userAgent.toLowerCase()),
  isAndroid: () => /Android/i.test(navigator.userAgent),
  isWindows: () => userAgentContains('Windows'),
  isChromeOnWindows: () => userAgentContains('Chrome') && userAgentContains('Windows'),
  isCanaryOnWindows: () => userAgentContains('Chrome/52') && userAgentContains('Windows'),
  isEdgeOnWindows: () => userAgentContains('Edge') && userAgentContains('Windows'),
  isFirefox: () => userAgentContains('Firefox'),
  isSafari: () => userAgentContains('Safari') && !userAgentContains('Chrome'),
  isOpera: () => userAgentContains('Opera'),
  isFirefoxAndroid: () => userAgentContains('Android') && userAgentContains('Firefox'),
  isTablet: (screenWidth) => {
    const userAgent = navigator.userAgent.toLowerCase();
    const isAppleTablet = /ipad/i.test(userAgent);
    const isAndroidTablet = /android/i.test(userAgent);
    return (isAppleTablet || isAndroidTablet) && screenWidth >= 640;
  },

  iOSVersion: () => {
    if (typeof window !== "undefined") {
      if (window.MSStream) {
        // There is some iOS in Windows Phone...
        // https://msdn.microsoft.com/en-us/library/hh869301(v=vs.85).aspx
        return '';
      }
    }

    const match = navigator.appVersion.match(/OS (\d+)_(\d+)_?(\d+)?/);

    let version;

    if (match !== undefined && match !== null) {
      version = [
        parseInt(match[1], 10),
        parseInt(match[2], 10),
        parseInt(match[3] || 0, 10),
      ];
      return parseFloat(version.join('.'));
    }

    return '';
  },

  androidVersion: () => {
    const userAgent = navigator.userAgent.split('Android');
    let version;

    if (userAgent.length > 1) {
      version = userAgent[1].split(';')[0]; // eslint-disable-line
    }

    return version;
  },

  isAppleDevice: () => {
    const devices = [
      'iPad Simulator',
      'iPhone Simulator',
      'iPod Simulator',
      'iPad',
      'iPhone',
      'iPod',
    ];

    if (navigator.platform) {
      while (devices.length) {
        navigator.platform = devices.pop();
        if (navigator.platform) {
          return true;
        }
      }
    }

    return false;
  },
};

export default Detect;

export const Modules = {
  Orientation: {
    Up: 0,
    Down: 1,
    Left: 2,
    Right: 3,
    toString: (orientation) => {
      switch (orientation) {
        case 2:
          return 'left';
        case 3:
          return 'right';
        case 0:
          return 'up';
        case 1:
          return 'down';
        default:
          return null;
      }
    },
  },

  Types: {
    Player: 0,
  },

  InputType: {
    Key: 0,
    LeftClick: 1,
  },

  Actions: {
    Idle: 0,
    Attack: 1,
    Walk: 2,
  },

  Hits: {
    Damage: 0,
    Poison: 1,
    Heal: 2,
    Mana: 3,
    Experience: 4,
    LevelUp: 5,
    Critical: 6,
    Stun: 7,
    Explosive: 8,
