export default class OgreLord extends Combat {
  constructor(character) {
    super(character);
    this.character = character;

    this.dialogues = [
      'Get outta my swamp',
      'No, not the onion.',
      'My minions give me strength! You stand no chance!',
    ];

    this.minions = [];

    this.lastSpawn = 0;

    this.loaded = false;

    character.projectile = Modules.Projectiles.Boulder; // eslint-disable-line
    character.projectileName = 'projectile-boulder'; // eslint-disable-line

    character.onDeath(() => {
      this.reset();
    });
  }

  load() {
    this.talkingInterval = setInterval(() => {
      if (this.character.hasTarget()) {
        this.forceTalk(this.getMessage());
      }
    }, 9000);

    this.updateInterval = setInterval(() => {
      this.character.armourLevel = 50 + this.minions.length * 15;
    }, 2000);

    this.loaded = true;
  }

  hit(character, target, hitInfo) {
    if (this.isAttacked()) {
      this.beginMinionAttack();
    }

    if (!character.isNonDiagonal(target)) {
      const distance = character.getDistance(target);

      if (distance < 7) {
        hitInfo.isRanged = true; // eslint-disable-line
        character.attackRange = 7; // eslint-disable-line
      }
    }

    if (this.canSpawn()) {
      this.spawnMinions();
    }

    super.hit(character, target, hitInfo);
  }

  forceTalk(message) {
    if (!this.world) {
      return;
    }

    this.world.pushToAdjacentGroups(
      this.character.target.group,
      new Messages.NPC(Packets.NPCOpcode.Talk, {
        id: this.character.instance,
        text: message,
        nonNPC: true,
      }),
    );
  }

  getMessage() {
    return this.dialogues[Utils.randomInt(0, this.dialogues.length - 1)];
  }

  spawnMinions() {
    const
      xs = [414, 430, 415, 420, 429];


    const ys = [172, 173, 183, 185, 180];

    this.lastSpawn = new Date().getTime();

    this.forceTalk('Now you shall see my true power!');

    for (let i = 0; i < xs.length; i += 1) this.minions.push(this.world.spawnMob(12, xs[i], ys[i]));

    _.each(this.minions, (minion) => {
      minion.onDeath(() => {
        if (this.isLast()) {
          this.lastSpawn = new Date().getTime();
        }

        this.minions.splice(this.minions.indexOf(minion), 1);
      });

      if (this.isAttacked()) {
        this.beginMinionAttack();
      }
    });

    if (!this.loaded) {
      this.load();
    }
  }

  beginMinionAttack() {
    if (!this.hasMinions()) {
      return;
    }

    _.each(this.minions, (minion) => {
      const randomTarget = this.getRandomTarget();

      if (!minion.hasTarget() && randomTarget) {
        minion.combat.begin(randomTarget);
      }
    });
  }

  reset() {
    this.lastSpawn = 0;

    const listCopy = this.minions.slice();

    for (let i = 0; i < listCopy.length; i += 1) {
      this.world.kill(listCopy[i]);
    }

    clearInterval(this.talkingInterval);
    clearInterval(this.updateInterval);

    this.talkingInterval = null;
    this.updateInterval = null;

    this.loaded = false;
  }

  getRandomTarget() {
    if (this.isAttacked()) {
      const keys = Object.keys(this.attackers);
      const randomAttacker = this.attackers[keys[Utils.randomInt(0, keys.length)]];

      if (randomAttacker) {
        return randomAttacker;
      }
    }

    if (this.character.hasTarget()) {
      return this.character.target;
    }

    return null;
  }

  hasMinions() {
    return this.minions.length > 0;
  }

  isLast() {
    return this.minions.length === 1;
  }

  canSpawn() {
    return (
      new Date().getTime() - this.lastSpawn > 50000
      && !this.hasMinions()
      && this.isAttacked()
    );
  }
}

import Combat from '../../js/game/entity/character/combat/combat';
import Utils from '../../js/util/utils';
import Messages from '../../js/network/messages';

export default class PirateCaptain extends Combat {
  constructor(character) {
    super(character);
    this.character = Object.assign(character, {
      spawnDistance: 20,
    });

    this.teleportLocations = [];

    this.lastTeleportIndex = 0;
    this.lastTeleport = 0;

    this.location = {
      x: this.character.x,
      y: this.character.y,
    };

    this.load();
  }

  load() {
    const
      south = {
        x: 251,
        y: 574,
      };
    const west = {
      x: 243,
      y: 569,
    };
    const east = {
      x: 258,
      y: 568,
    };
    const north = {
      x: 251,
      y: 563,
    };

    this.teleportLocations.push(north, south, west, east);
  }

  hit(character, target, hitInfo) {
    if (this.canTeleport()) {
      this.teleport();
    } else {
      super.hit(character, target, hitInfo);
    }
  }

  teleport() {
    const position = this.getRandomPosition();

    if (!position) {
      return;
    }

    this.stop();

    this.lastTeleport = new Date().getTime();
    this.lastTeleportIndex = position.index;

    this.character.setPosition(position.x, position.y);

    if (this.world) {
      this.world.pushToGroup(
        this.character.group,
        new Messages.Teleport(
          this.character.instance,
          this.character.x,
          this.character.y,
          true,
        ),
      );
    }

    this.forEachAttacker((attacker) => {
      attacker.removeTarget();
    });

    if (this.character.hasTarget()) {
      this.begin(this.character.target);
    }
  }

  getRandomPosition() {
    const random = Utils.randomInt(0, this.teleportLocations.length - 1);
    const position = this.teleportLocations[random];

    if (!position || random === this.lastTeleportIndex) {
      return null;
    }

    return {
      x: position.x,
      y: position.y,
      index: random,
    };
  }

  canTeleport() {
    // Just randomize the teleportation for shits and giggles.
    return (
      new Date().getTime() - this.lastTeleport > 10000
      && Utils.randomInt(0, 4) === 2
    );
  }

  getHealthPercentage() {
    // Floor it to avoid random floats
    return Math.floor(
      (this.character.hitPoints / this.character.maxHitPoints) * 100,
    );
  }
}

import _ from 'underscore';
