import Messages from '../../../../../network/messages.js';
import Packets from '../../../../../network/packets.js';
import Utils from '../../../../../util/utils.js';

export default class Quest {
  constructor(player, data, description) {
    this.player = player;
    this.data = data;
    this.id = data.id;
    this.name = data.name;
    this.description = description || data.description;
    this.stage = 0;
  }

  finish() {
    if (this.hasItemReward()) {
      const item = this.getItemReward();

      if (item) {
        if (this.hasInventorySpace(item.id, item.count)) {
          this.player.inventory.add(item.id, item.count);
        } else {
          this.player.notify('You do not have enough space in your inventory.');
          this.player.notify('Please make room prior to finishing the quest.');

          return;
        }
      }
    }

    this.setStage(9999);

    this.player.send(
      new Messages.Quest(Packets.QuestOpcode.Finish, {
        id: this.id,
        isQuest: true,
      }),
    );
  }

  isFinished() {
    return this.stage >= 9999;
  }

  setStage(stage) {
    this.stage = stage;
    this.update();
  }

  hasMob() {
    return false;
  }

  triggerTalk(npc) {
    if (this.npcTalkCallback) this.npcTalkCallback(npc);
  }

  onNPCTalk(callback) {
    this.npcTalkCallback = callback;
  }

  hasNPC(id) {
    return this.data.npcs.indexOf(id) > -1;
  }

  update() {
    this.player.save();
  }

  updatePointers() {
    if (!this.data.pointers) return;

    const pointer = this.data.pointers[this.stage];

    if (!pointer) return;

    const opcode = pointer[0];


    const x = pointer[1];


    const y = pointer[2];

    this.player.send(
      new Messages.Pointer(opcode, {
        id: Utils.generateRandomId(),
        x,
        y,
      }),
    );
  }

  forceTalk(npc, message) {
    if (!npc) {
      return;
    }

    npc.talkIndex = 0; // eslint-disable-line

    this.player.send(
      new Messages.NPC(Packets.NPCOpcode.Talk, {
        id: npc.instance,
        text: message,
      }),
    );
  }

  /**
   * Ensures that an NPC does not go off the conversation
   * index and is resetted in order to start a new chat
   */
  resetTalkIndex(npc) {
    if (!npc) {
      return;
    }

    npc.talkIndex = 0; // eslint-disable-line

    this.player.send(
      new Messages.NPC(Packets.NPCOpcode.Talk, {
        id: npc.instance,
        text: null,
      }),
    );
  }

  clearPointers() {
    this.player.send(new Messages.Pointer(Packets.PointerOpcode.Remove, {}));
  }

  getConversation(id) {
    const
      conversation = this.data.conversations[id];

    if (!conversation || !conversation[this.stage]) return [''];

    return conversation[this.stage];
  }

  hasItemReward() {
    return !!this.data.itemReward;
  }

  getTask() {
    return this.data.task[this.stage];
  }

  getId() {
    return this.id;
  }

  getName() {
    return this.name;
  }

  getDescription() {
    return this.description;
  }

  getStage() {
    return this.stage;
  }

  getItem() {
    return this.data.itemReq ? this.data.itemReq[this.stage] : null;
  }

  getItemReward() {
    return this.hasItemReward() ? this.data.itemReward : null;
  }

  hasInventorySpace(id, count) {
    return this.player.inventory.canHold(id, count);
  }

  getInfo() {
    return {
      id: this.getId(),
      name: this.getName(),
      description: this.getDescription(),
      stage: this.getStage(),
      finished: this.isFinished(),
    };
  }
}

import Modules from '../../../../util/modules.js';

/**
 * We maintain a trade instance for every player,
 * and we trigger it whenever the trading is
 * started and/or requested.
 */
export default class Trade {
  constructor(player) {
    this.player = player;
    this.oPlayer = null;
    this.requestee = null;
    this.state = null;
    this.subState = null;
    this.playerItems = [];
    this.oPlayerItems = [];
  }

  start() {
    this.oPlayer = this.requestee;
    this.state = Modules.Trade.Started;
  }

  stop() {
    this.oPlayer = null;
    this.state = null;
    this.subState = null;
    this.requestee = null;
    this.playerItems = [];
    this.oPlayerItems = [];
  }

  finalize() {
    if (!this.player.inventory.containsSpaces(this.oPlayerItems.length)) {
      return;
    }

    for (const i in this.oPlayerItems) {
      const item = this.oPlayerItems[i];

      if (item && item.id !== -1) {
        this.oPlayer.inventory.remove(item.id, item.count, item.index);
        this.player.inventory.add(item);
      }
    }
  }

  select(slot) {
    const item = this.player.inventory.slots[slot];

    if (!item || item.id === -1 || this.playerItems.indexOf(item) < 0) {
      return;
    }

    this.playerItems.push(item);
  }

  request(oPlayer) {
    this.requestee = oPlayer;

    if (oPlayer.trade.getRequestee() === this.player.instance) {
      this.start();
    }
  }

  accept() {
    this.subState = Modules.Trade.Accepted;

    if (this.oPlayer.trade.subState === Modules.Trade.Accepted) {
      this.finalize();
      this.oPlayer.trade.finalize();
    }
  }

  getRequestee() {
    if (!this.requestee) {
      return null;
    }

    return this.requestee.instance;
  }

  decline() {
    this.stop();
  }

  isStarted() {
    return this.state !== null;
  }
}

import Modules from '../../../../util/modules.js';
import Utils from '../../../../util/utils.js';

export default class Warp {
  constructor(player) {
    this.player = player;
    this.lastWarp = 0;
    this.warpTimeout = 300000;
  }

  warp(id) {
    if (!this.canWarp()) {
      this.player.notify(
        `You must wait another ${this.getDuration()} to warp.`,
      );
      return;
    }

    const data = Modules.Warps[id];

    if (!data) {
      return;
    }

    const name = data[0];
    const x = data[3] ? data[1] + Utils.randomInt(0, 1) : data[1];
    const y = data[3] ? data[2] + Utils.randomInt(0, 1) : data[2];
    const levelRequirement = data[4];

    if (this.player.level < levelRequirement) {
      this.player.notify(
        `You must be at least level ${levelRequirement} to warp here!`,
      );
      return;
    }

    this.player.teleport(x, y, false, true);
    this.player.notify(`You have been warped to ${name}`);
    this.lastWarp = new Date().getTime();
  }

  setLastWarp(lastWarp) {
    if (isNaN(lastWarp)) {
      this.lastWarp = 0;
      this.player.save();
    } else this.lastWarp = lastWarp;
  }

  canWarp() {
    return this.getDifference() > this.warpTimeout || this.player.rights > 1;
  }

  getDuration() {
    const difference = this.warpTimeout - this.getDifference();

    if (!difference) {
      return '5 minutes';
    }

    return difference > 60000
      ? `${Math.ceil(difference / 60000)} minutes`
      : `${Math.floor(difference / 1000)} seconds`;
  }

  getDifference() {
    return new Date().getTime() - this.lastWarp;
  }
}

