  },
  getData: (name) => {
    if (name in ItemsDictionary.data) {
      return ItemsDictionary.data[name];
    }

    return 'null';
  },
  hasPlugin: (id) => {
    if (id in ItemsDictionary.data && ItemsDictionary.data[id].plugin in ItemsDictionary.plugins) {
      return true;
    }

    return false;
