const ShopsDictionary = {
  data: [],
  properties: {},
  plugins: {},
  getProperty: key => ShopsDictionary.properties[key],
  setProperty: (key, value) => {
    ShopsDictionary.properties[key] = value;
  },
  getData: key => ShopsDictionary.data[key],
  setData: (key, value) => {
    ShopsDictionary.data[key] = value; // by key
    ShopsDictionary.data[value.id] = value; // by id
  },
  idToString: (id) => {
    let string = null;
    Object.entries(ShopsDictionary.data).forEach(([key, shop]) => {
      if (key && shop.id === id) {
        string = key;
      }
    });

    return string;
  },
  idToName: (id) => {
    let value = null;
    Object.entries(ShopsDictionary.data).forEach(([key, shop]) => {
      if (key && shop.id === id) {
        value = shop.name;
      }
    });

    return value;
  },
  stringToId: (name) => {
    if (name in ShopsDictionary.data) {
      return ShopsDictionary.data[name].id;
    }

    log.error(`${name} not found in the ShopsDictionary.`);
    return 'null';
  },
  exists: id => id in ShopsDictionary.data,
  setPlugins: (directory) => {
    ShopsDictionary.plugins = PluginLoader(directory);
  },
  isShopNPC: npcId => ShopsDictionary.data[npcId],
  getItems: id => ShopsDictionary.data[id].items,
  getItemCount: id => ShopsDictionary.getItems(id).length,

  /**
   * Reason for the shopId variable is because some shops
   * may have different prices for the same item. A way to
   * spice up the game.
   */
  getCost: (shopId, itemId, count) => {
    const shop = ShopsDictionary.data[shopId];
    const index = shop.items.indexOf(itemId);

    if (!index) {
      return -1;
    }

    return shop.prices[index] * count;
  },

  getCount: (id) => {
    const {
      count,
    } = ShopsDictionary.data[id];
    const counts = [];

    if (_.isArray(count)) {
      return count;
    }

    for (let i = 0; i < ShopsDictionary.getItemCount(id); i += 1) {
      counts.push(count);
    }

    return counts;
  },
};

export default ShopsDictionary;

import Packets from '../network/packets.js';
import { fileURLToPath } from 'url';
import { dirname } from 'path';

const randomInt = (min, max) => min + Math.floor(Math.random() * (max - min + 1));
const random = range => Math.floor(Math.random() * range);

export default {
  dirName: val => {
    const __filename = fileURLToPath(val);
    return dirname(__filename);
  },
  fileName: val => fileURLToPath(val),
  isInt: n => n % 1 === 0,
  random,
  randomRange: (min, max) => min + Math.random() * (max - min),
  randomInt,
  getDistance: (startX, startY, toX, toY) => {
    const x = Math.abs(startX - toX);
    const y = Math.abs(startY - toY);
    return x > y ? x : y;
  },
  positionOffset: radius => ({
    x: randomInt(0, radius),
    y: randomInt(0, radius),
  }),
  /**
   * There is seriously no way two clients can end up with the same ID
   */
  generateClientId: () => (
    randomInt(0, 1000000)
    + randomInt(0, 40000)
    + randomInt(0, 9000)
  ),
  generateInstance: (randomizer, id, modulo, posY) => (
    `${randomizer}${randomInt(0, id)}${randomizer}${randomInt(0, modulo)}${posY || 0}`
  ),
  generateRandomId: () => `${1}${random(0, 200)}${random(0, 20)}${2}`,
  validPacket: (packet) => {
    const keys = Object.keys(Packets);


    const filtered = [];

    for (let i = 0; i < keys.length; i += 1) {
      if (!keys[i].endsWith('Opcode')) {
        filtered.push(keys[i]);
      }
    }

    return packet > -1 && packet < Packets[filtered[filtered.length - 1]] + 1;
  },
};

